INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (1, '5·18 기념공원', '5·18 기념공원은 1980년 광주 5·18 민주화운동을 기념하기 위해 조성한 공원이다. 5·18 민주화운동이 전개될 당시 군사시설이었던 상무대가 이전하자 정부가 공원 부지를 제공하였다. 이에 따라 1998년 광주광역시는 5·18 민주화운동의 명예회복과 값진 교훈을 올바르게 계승 발전시키기 위하여 208,000㎡ 규모로 5·18 기념공원을 조성하였다.  5·18 기념공원에는 자료실과 공연 및 행사시설을 갖춘 5·18 기념문화센터, 시민군조각상, 추모공간 등으로 구성된 5·18 현황조각 및 추모승화공간, 5·18 정신을 상징적으로 나타낸 원형 분수공간을 갖춘 대동광장, 5·18 민주화운동학생기념탑이 있다. 5·18 기념공원이 조성된 여의산 자락에는 광주학생교육문화회관이 있고, 산 둘레로 산책로가 조성되어 있다. 정상에는 전망대 역할을 하는 누각인 오월대가 있다.
현재 5·18 기념공원은 광주시민들에게 5·18 민주화운동의 추모와 기념 공간일 뿐만 아니라 도심 속의 쾌적한 휴식 공간으로도 많은 사랑을 받고 있다. 전체면적의 약 45%인 92,099㎡가 녹지로 조성되어 전통 연못과 다양한 수목들이 자리하고 있어 아이들의 자연학습장으로도 널리 활용되고 있기도 하다.',
        35.1557506526, 126.8585681905,
        'http://tong.visitkorea.or.kr/cms/resource/27/3350527_image2_1.jpg', '광주광역시 서구 내방로 152', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (2, 'BIFF 광장', '남포동 극장가는 8.15 광복 후 극장이 한두 군데 생겼고, 1960년대에 이르러 20여 개소의 극장이 BIFF광장 주변으로 밀집하여 생겨났다. 1996년 9월 13일 작지만 권위 있는 영화제를 만들고자 제1회 부산국제영화제(Busan International Film Festival)가 개최되었고, 영화제를 개최하면서 극장가를 새롭게 단장하고 일대를 BIFF 광장으로 명명하게 되었다. BIFF광장에서는 부산국제영화제 전야제 행사가 개최되었고, 유명 영화인들의 핸드프린팅 행사 등 BIFF 행사의 주 무대로 활용되었으나, 해운대에 영화의 전당이 만들어지면서 개막식 등 주요 행사는 그곳으로 옮겨 갔으나, 영화제 초창기의 흔적은 많이 남아있고 영화 상영은 계속 이어오고 있다. 이곳처럼 개봉 극장이 한 곳에 밀집된 곳은 아시아는 물론 유럽 어느 나라에서도 찾아볼 수 없는 곳으로 무한한 잠재력을 지니고 있는 곳이며, 근래 들어 단순히 영화만 보는 곳이 아닌 쇼핑과 관광을 함께 즐길 수 있는 복합문화공간이 되면서 젊은 층이 많이 찾고 있어 이 지역 상권이 활기를 되찾는 데 큰 역할을 하고 있다.',
        35.0988508949, 129.0292126150,
        'http://tong.visitkorea.or.kr/cms/resource/83/2364283_image2_1.jpg', '부산광역시 중구 남포길 4 (남포동3가)', 6, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (3, 'DMZ 생생누리', 'DMZ 생생누리는 DMZ를 보다 가깝고 생생하게 체험하고 즐길 수 있는 곳이라는 뜻을 담고 있다. DMZ와 접경지역의 역사, 상징성, 생태환경 등의 다양한 가치와 매력을 실감미디어콘텐츠로 구현하여 방문객들에게 재미있고 의미 있는 경험을 제공하는 곳이다.',
        37.8905011326, 126.7404030300,
        '', '경기도 파주시 임진각로 148-53 종합관광안내소', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (4, 'DMZ 생태평화공원', 'DMZ 생태평화공원은 환경부, 국방부(육군 3사단)와 철원군이 공동 협약을 맺어 전쟁·평화·생태가 공존하는 DMZ의 상징적 메시지를 전파하고 조성된 곳이다. 휴전 후 지난 60년간 민간인에게 전혀 개방되지 않았던 원시 생태계가 방문객들이 직접 체험할 수 있도록 탐방코스로 개발되어 국내뿐 아니라 외국인들에게도 커다란 호응을 얻고 있다. DMZ 생태평화공원은 탐방코스 2개 노선을 따라 조성되었으며, 제 1코스 십자탑 탐방로와 제 2코스 용양보 코스로 구성되어있다. 제1코스인 십자탑 탐방로는 육군 제3보병사단에서 북한에 사랑과 평화가 전달되기를 기원하며 성재산 위에 설치한 십자탑을 전망시설로 활용하여 남북한의 철책과 진지를 직접 볼 수 있는 곳으로 DMZ 내부의 자연환경과 한반도의 냉전 현실을 피부로 느낄 수 있는 곳이다. 제 2코스인 용양보 코스는 6·25전쟁 격전지의 한가운데 위치한 곳으로 현재에는 암정교와 금강산 전철의 도로원표에서 전쟁의 흔적을 느낄 수 있으며, 용양보는 DMZ 통제구역 내에 위치하여 국내에서도 찾아보기 어려운 아름다운 호수형 습지의 자연환경을 관찰할 수 있는 곳이다. 철원군 DMZ(비무장지대)는 천연기념물인 두루미와 재두루미, 독수리 등 다양한 겨울 철새들이 월동하는 지역이다. 그리고 수천 마리의 기러기 떼들이 하루에도 몇 번씩 휴전선을 넘나들며 자유롭게 남녘과 북녘을 오가는 모습을 볼 수 있다. 또한 화강 상류 DMZ 지역은 수달과 묵납자루 등 다양한 희귀 생물 종이 서식하고 경관 미가 탁월한 곳으로, DMZ 산록에는 참나무와 싸리나무가 자생하고, 자연습지에는 침수식물, 정수식물, 수변 식물 등이 군락을 이루고 있다.',
        38.2812378818, 127.4513042085,
        '', '강원특별자치도 철원군 김화읍 생창길 481-1', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (5, 'Fe01(에프이01)', 'Fe01은 울산시 울주군에 위치한 세계 최대 규모의 업사이클 복합문화 예술공간이다. 폐 자동차, 오토바이 등의 철(Fe) 부속품을 재활용한 정크아트 작품을 통해 새로운 시각과 무한한 상상의 세계의 계기를 마련하고 있다. <로봇시리즈>, <기원의 확장>, <상상동물원>, <쥬라기파크>, <영화시리즈> 5가지 테마의 다양한 정크아트 야외 조형 전시물 1,200여 점을 선보이고 있다. 테마별 스토리와 작가의 정교한 표현 기법을 보여주는 본 전시는 오픈런으로 진행하고 있으며, 업사이클 문화 형성을 선도하고자 전시에 연계하여 정크아트를 직접 제작해 보는 체험형 교육 프로그램과 당일 생산하는 도넛을 디자인해 볼 수 있는 프로그램을 운영 중이다.',
        35.3574548653, 129.3074923807,
        '', '울산광역시 울주군 용연길 160', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (6, '[강화 나들길 제5코스] 고비고개길', '강화나들길은 해안을 따라 1.3km마다 자리한 돈대와 강화산성, 고려왕릉을 돌며 강화도를 찬찬히 들여다보는 도보 여행길이다. 탐방로를 천천히 걸으며 강화도의 역사, 생태, 생활, 문화를 다양하게 접할 수 있는 길로 구성되며 총 20개 코스가 있다. 강화는 섬 전체가 박물관이라 해도 과언이 아닐 만큼 유적지가 많은 곳이다.
[강화 나들길 제5코스] 고비고개길은 강화버스터미널에서 외포여객터미널까지 20.2㎞를 잇는 구간으로 6시간 40분 정도 소요된다. 옛사람들이 강화장터를 가기 위해 이용했던 길을 복원한 제5코스는 높지 않은 산길을 수 시간 동안 거닐며 삼림욕을 즐길 수 있다. 강화를 동서로 연결하던 옛길로 고천리에서 나무꾼과 장사꾼들이 등짐을 지고 고비고개를 넘어 강화장터로 가던 길과 2개의 저수지를 도는 풍경, 내가시장에서 만나는 소박한 시골장 풍경 등을 볼 수 있으며 덕산산림욕장의 상쾌한 숲길과 외포어시장의 볼거리는 도보여행의 진수를 느끼게 해준다.',
        37.7458245442, 126.4822613345,
        'http://tong.visitkorea.or.kr/cms/resource/21/1895221_image2_1.jpg', '인천광역시 강화군 강화읍 청하동길 24', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (7, '[백년가게]금강식당', '1979년에 개업한 맛과 감동을 느낄 수 있는 식당, 미래를 설계하여 긍지와 자부심을 느낄 수 있는 식당, 자식에게 대물림할 수 있는 떳떳한 식당, 손님과 지역사회와 함께할 수 있는 식당이다. (1대 윤기분 1979.9.19 ~ 1997.3.24 / 2대 이남규 1997.3.24 ~ 현재)',
        37.0427647107, 128.4840307001,
        'http://tong.visitkorea.or.kr/cms/resource/91/2630891_image2_1.jpg', '충청북도 단양군 백자1길 11 금강식당', 33, '043-423-7350', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (8, '[백년가게]동래할매파전', '[중소벤처기업부&nbsp;2018년도&nbsp;''백년가게''로&nbsp;선정]&nbsp;일반 파전과 달리 두툼하고 풍성한 해물맛을 자랑하는 동래파전. 장을 활용하여 반죽하고 솥뚜껑 모양의 무쇠그릇에 기름을 넉넉히 붓고 전통방식을 따라 조리하며, 해물과 잘 어울리도록 초고추장을 제공하는 ''동래파전''의 문화를 이어가고 있다. 정성들여 준비하는 밑반찬과 유기그릇, 고풍스러운 느낌의 인테리어가 ''동래부사가 임금님께 진상''했다는 스토리와 썩 잘어울린다. 동래구 주민 뿐 아니라, 전국에서도 찾는 맛집으로 최근 유명 맛집프로그램에 출현하면서 그 유명새가 더욱 높아졌다.',
        35.2046206142, 129.0838605224,
        'http://tong.visitkorea.or.kr/cms/resource/05/2565305_image2_1.jpg', '부산광역시 동래구 명륜로94번길 43-10', 6, '051-552-0792', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (9, '[북악하늘길 1산책로] 북악팔각정~말바위쉼터', '‘북악하늘길 1산책로’는 북악팔각정부터 말바위 쉼터까지의 코스로 북악하늘길 3개의 코스 중 첫 번째 코스이다. 1968년 1.21 사태(북한의 청와대 습격 시도사건) 때 김신조 등 북한 공작원 31명이 청와대를 습격할 때 이용한 뒤 폐쇄된 길로 일명 북악산 김신조 루트로 불리며 41년간 군사통제구역이었다. 그동안 [걸을 수 없는 길]로 봉인됐던 이 길에 걷기 편한 산책로가 만들어지고 북악산 일대가 2010년 2월 27일, 41년 만에 개방되었다.
이곳은 40여 년간 사람들의 발길이 닿지 않아 생태적 가치가 높고 자연경관이 우수해 일명[서울 속의 비무장지대]이라고 불릴 정도로 천혜의 자연이 그대로 살아 숨 쉬고 있다. 북악하늘길 산책로는 ‘하늘길’이라는 이름에 걸맞게 산허리를 시원하게 감고 돌고 있어 서울 시내와 북한산, 북악산, 인왕산을 바라보며 걸을 수 있다. 수도권 지하철 4호선 한성대역 6번 출구로 나와서 마을버스를 타고 성북구민회관 앞에서 내려서 안내판을 따라 시작하면 된다.',
        37.6020279352, 126.9805670485,
        'http://tong.visitkorea.or.kr/cms/resource/14/1868114_image2_1.jpg', '서울특별시 종로구 북악산로 267 (평창동)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (10, '[제주올레 3코스] 온평-표선 올레 (A)', '중산간 길의 고즈넉함을 만끽할 수 있는 올레. 양옆에 늘어선 오래된 제주 돌담과 제주에 자생하는 울창한 수목이 운치를 더한다. 나지막하면서 독특한 전망, 통오름과 독자봉이 제주의 오름이 지닌 고유의 멋을 느끼게 해준다. 동백나무길, 감귤밭길 등 삼달리 중산간길이 이어지고 김영갑갤러리 두모악이 나온다. 병마에 시달린 마지막까지도 아름다운 제주의 사진을 찍어냈던 고 김영갑 사진작가의 작품을 만날 수 있다.',
        33.4052580126, 126.9039284074,
        '', '제주특별자치도 서귀포시 성산읍 온평리 1001-6 (성산읍)', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (11, '가고파 꼬부랑길 벽화마을', '가고파 꼬부랑길벽화마을은 2013년 12월 추산동과 성호동 일대 30여 가구를 잇는 골목에 조성됐다. 여행은 벽화마을 안내도가 설치된 성호서10길과 성호서2길이 만나는 지점에서 시작한다. 미국 러시모어산의 큰바위얼굴에 조각가 문신과 천상병 시인 등 창원시와 인연이 있는 예술가의 얼굴을 그려 넣은 벽화가 있어 쉽게 찾을 수 있다. 450m 남짓한 골목과 계단에 다양한 그림이 가득하다. 무지개 색깔로 칠한 짧은 돌계단 끝에는 마을 주민이 1910년부터 사용하던 백년우물이 있다. 마산항이 바라보이는 백년우물 옆 물지게 벽화는 가고파 꼬부랑길벽화마을 최고의 포토존이다. 이 외에도 몬드리안의 작품을 연상케 하는 벽화와 등용문을 의미하는 물고기 벽화, 담벼락을 카페 입구처럼 꾸민 벽화 등 좁은 골목 곳곳에 예쁜 그림이 가득해 걷는 내내 마음이 즐겁다. 가고파 꼬부랑길벽화마을을 돌아볼 때는 마을 주민이 생활하는 공간이라는 사실을 명심하고 사생활을 침해하는 행동은 삼가야 한다.',
        35.2077664004, 128.5696552845,
        'http://tong.visitkorea.or.kr/cms/resource/55/2033255_image2_1.jpg', '경상남도 창원시 마산합포구 성호서7길 15-8', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (12, '가실성당', '가실성당은 1895년에 조선 교구의 11번째 본당으로 초대본당신부는 파리외방선교회의 하경조 신부다. 본당의 넓은 관할 지역 안에는 공소 31개가 있으며, 현재의 성당 및 사제관은 1924년에 완성되었다. 성당의 주보성인 성당은 인민군 병원으로 그대로 남아있으며, 전쟁 관계로 월남한 성 베네딕도 수도회의 신부들은 1952년부터 가실성당을 위한 사목을 맡고있다. 가실성당에 속하고 있더거 본당이 된 곳은 모두 4개이며, 김천 황금동, 용평, 퇴강, 왜관이 있다. 1995년 100주년 사업으로 현재 성모당이 형성되었고 성당의 거룩한 분위기를 높이는 색유리화는 2002년에 설치되었고 2003년에 성당 및 구사제관은 경상북도 문화재로 지정되었다.',
        35.9368172454, 128.4049220662,
        'http://tong.visitkorea.or.kr/cms/resource/86/2687486_image2_1.jpg', '경상북도 칠곡군 왜관읍 가실1길 1', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (13, '가창 찐빵 골목', '가창 찐빵 골목은 대구광역시 달성군 가창면에 위치하고 있다. 대구방향에서 가창, 청도 방향 신천대로로 진입하다 가창댐으로 들어가는 길목 대로변과 가창면 행정복지 센터로 들어가는 가창로 주변에 모여 있다. 대구 수성구 파동에서 가창 냉천(스파벨리), 가창댐, 헐티재, 청도 방면으로 오가는 시민과 관광객들에게 맛있는 찐빵으로 알려졌으며, 2000년에 가창면사무소 맞은편 찐빵집이 문을 열면서 시작된 골목의 역사가 이후 20여 년이 지난 지금 9곳의 찐빵집이 골목을 이루고 있다.
대구 지역 방송에 처음 소개되면서 널리 알려지기 시작해 여러 방송에 출연하며 전국적으로 유명한 대구 명물거리가 되었고, 가창 찐빵은 대구 달성의 대표 브랜드로 자리 잡았다. 골목 어느 상점이여도 찐빵 맛집으로는 손색이 없어 가게마다 나름의 특색과 장점을 내세워 단골을 확보, 가게 앞에 구매 고객들의 행렬을 만들고 있다.
겨울이면 만두와 찐빵을 찌는 하얀 김이 거리를 덮어 이곳이 가창 모락모락 찐빵 골목임을 증명한다.',
        35.8027142041, 128.6222232980,
        'http://tong.visitkorea.or.kr/cms/resource/70/1876170_image2_1.jpg', '대구광역시 달성군 가창면 가창로 1099 (가창면)', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (14, '간월암(서산)', '간월암은 부석면 간월도리에 위치한 작은 암자로 조선 초 무학대사가 창건하였으며 송만공 대사가 중건하였다고 전해진다. 다른 암자와는 달리 간조 시, 육지와 연결되고 만조 시, 섬이 되는 신비로운 암자로 만조 시 물 위에 떠 있는 암자처럼 느껴진다.
주위 자연경관과 옛 선조들의 숨결을 함께 느낄 수 있는 고찰이 어우러져 관광객의 발길이 줄을 잇고 있다. 또한 간월도에서 생산되는 굴의 풍년을 기원하는 굴부르기군왕제가 매년 정월 보름날 만조 시에 간월도리 어리굴젓 기념탑 앞에 서 있는데, 이 행사는 부정한 행동을 하지 않은 청결한 아낙네들이 소복(흰옷)을 입고  마을 입구에서 춤을 추며 출발하여 굴탑 앞에 도착하면 제물을 차려 놓고 굴 풍년 기원제를 지내며 채취한 굴은 관광객에게 시식도 시켜준다.',
        36.6038362307, 126.4114209947,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=479ec82e-56fd-414f-a868-503c7f787e77', '충청남도 서산시 부석면 간월도1길 119-29', 34, '041-664-6624', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (15, '간절곶 등대', '새로운 천년의 첫해가 한반도에서 가장 먼저 떠오르는 곳, 울주군 서생면 대송리의 간절곶은 동해안에서 맨 먼저 떠오르는 해를 맞이할 수 있다. 간절곶 등대는 1920년 3월 26일 처음 불빛을 밝힌 이래 100여 년을 한결같이 울산항 뱃길을 인도해오고 있으며 ‘2000년 1월 1일 오전 7시 31분 26초’ 새천년의 해가 가장 먼저 떠오른 간절곶에 있는 등대로 유명해졌다. 영일만의 호미곶보다도 1분 빠르게, 강릉시의 정동진보다도 5분이나 더 빨리 해돋이가 시작되는데 그 광경이 장관이다. 울산 지역의 진하해수욕장, 서생포 왜성과 함께 새로운 명소로 주목을 받고 있다. 울산남부순환도로 입구에서 20분이면 닿는 지역이지만 아주 멀리 떠나온 듯 시원스러운 풍경이 이국적이다. 바다를 바라보고 우뚝 선 하얀색 등대 안으로는 나선형의 계단이 나있으며, 등대 창으로 바라보는 바다는 또 다른 새로운 느낌이다. 아카시아가 필 때 울산에서 부산해운대 쪽으로 해안선을 따라 달릴 때 바다내음보다 아카시아 향기가 더 그윽하다. 하얀 꽃을 주렁주렁 매단 아카시아 줄기가 축축 늘어져 있는 산길을 끼고 달리다가 어느새 바다가 펼쳐지기도 하는 이 길은 언제라도 드라이브 코스로 제격이다.',
        35.3589668021, 129.3605738299,
        'http://tong.visitkorea.or.kr/cms/resource/94/2648694_image2_1.jpg', '울산광역시 울주군 서생면 간절곶1길 39-2', 7, '052-239-6313', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (16, '감악산(파주)', '감악산은 파주시, 양주시, 연천군 사이에 있는 높이 675m의 산이다. 예부터 바위 사이로 검은빛과 푸른빛이 동시에 쏟아져 나온다고 하여 감악, 즉 감색 바위산이라 불렀다. 삼국시대부터 명산으로 알려져 왔으며 지역 주민들에게는 감박산으로 더 잘 알려져 있고 [고려사]나 [동국여지승람]에는 감악으로 표기되어 있다. 또한 조선시대 도성을 중심으로 북악, 송악, 관악, 심악 등과 함께 경기 오악의 하나로 수도권에서 가까워 사계절 인기가 있다.
정상에 오르면 강 건너편으로 휴전선 일대의 산과 들이 눈앞에 펼쳐지며 맑은 날에는 개성의 송악산과 북한산이 눈에 들어온다. 산 정상에는 이제는 흔적도 없이 마모되어 글씨를 찾아볼 수 없는 감악산비가 석대위에 우뚝 서 있다. 파주시 향토 유적인 이 비는 글자가 없다고 하여 몰자비, 또는 빗돌대왕비, 설인귀사적비 등 여러 개의 비 이름과 함께 전설들이 구전되어 오고 있다. 폭포, 계곡, 암벽 등을 고루 갖추고 있으며  임꺽정이 관군의 추격을 피하기 위해 숨어 지냈다는 장군봉 아래 임꺽정 굴이 있으며 설마리 계곡에는 6.25 전쟁과 관련한 영국군전적비와 대한의열단 전적비가 있다.
산림청선정 100대 명산 중의 하나이기도 하며 최근(2016년) 감악산 출렁다리로 방문객들의 즐거움을 더해주고 있다.',
        37.9121440779, 126.9520635715,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=eb21604e-6966-49ec-83f4-96b359298e2d', '경기도 파주시 적성면 감골길 48', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (17, '갑곶돈대', '돈대는 해안가나 접경지역에 돌이나 흙으로 쌓은 소규모 관측·방어시설로, 조선시대 강화도 해안에 돌로 쌓은 돈대 53개가 설치됐다. 갑곶돈대는 1679년 5월에 완성된 48 돈대 가운데 하나이다. 강화 돈대는 황해도·강원도·함경도 승군 8,900명과 어영청 소속 어영군 4,262명이 80일 정도 걸려서 쌓았다. 갑곶돈대는 망해돈대·제승돈대·염주돈대와 함께 제물진의 관할 하에 있었으며, 포좌가 있는 본래의 갑곶돈대는 옛 강화대교 입구 북쪽 언덕에 있었다. 지금 사적으로 지정된 갑곶돈대는 제물진과 강화 외성의 일부이다. 갑곶돈대에서는 강화전쟁박물관, 강화 갑곶리 탱자나무, 홍이포, 이섭정, 비석군 등을 관람할 수 있다.
(출처: 강화군시설관리공단 홈페이지)',
        37.7335377029, 126.5171140395,
        'http://tong.visitkorea.or.kr/cms/resource/71/3364171_image2_1.jpg', '인천광역시 강화군 강화읍 해안동로1366번길 18', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (18, '강동 해수온천', '강동 해수온천은 정자 앞바다 자연 바닷물을 그대로 침전시켜 100℃ 이상 끓여 공급하기 때문에 염도가 높고 살균효과가 뛰어나다. 내부는 요오드탕, 해수 냉탕, 해수 열탕, 온탕으로 구성되어 있다. 해수탕에 들어가기 전 반드시 먼저 샤워를 하고 청맥반석, 한방안개(사우나) 탕이나 핀란드식 건식사우나에서 충분히 몸을 따뜻하게 한 다음 해수탕에 들어가야 한다. 해수탕에서는 약 5-15분 정도 몸을 완전히 담그고 2회 정도 반복하면 체내의 노폐물이 완전히 제거된다. 탕 안에서 보는 오션뷰도 좋고 해수온천물도 좋기로 소문이 나 몸이 아픈 사람들이 많이 찾는 곳으로 유명하다.',
        35.6138565417, 129.4505699154,
        'http://tong.visitkorea.or.kr/cms/resource/26/3077926_image2_1.bmp', '울산광역시 북구 동해안로 1418 (구유동)', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (19, '강동 화암 주상절리', '화암 주상절리는 북구 화암동 화암마을 화암(꽃바위) 해변 일대에 분포하는 유적으로 약 2,000만 년 전 신생대 제3기 중신세에 분출한 현무암 용암이 냉각하면서 열 수축 작용으로 생성된 냉각절리이다. 강동 화암 주상절리의 가장 큰 특징은 주상체 횡단면이 꽃무늬 모양인 점과 수평, 수직, 경사진 형태 등 다양한 형태와 방향으로 발달해 있다는 점이다. 동해안 주상절리 가운데 용암 주상절리로는 가장 오래되어 학술적 가치가 높으며, 양한 각도로 형성되어 있어 경관이 아름답다. 주상절리 곳곳에 계단이 설치되어 있어 가까이에서 관찰하고 만져볼 수도 있다. 이곳의 주상절리는 다량의 목재 더미 모양을 하고 있는데, 길이는 수십 미터에 이르며, 하나의 주상체 횡단면 긴 쪽 대각선의 길이는 50㎝ 정도이다.',
        35.6341985442, 129.4424755991,
        '', '울산광역시 북구 산하동 952-1번지 일원 (산하동)', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (20, '강동몽돌해변', '북구 산하동에 있는 해변으로 몽돌들이 모여있다. 주전에서 해안도로를 따라 북쪽으로 약 20분 정도 차를 달리면 만나게 되며 강동몽돌해변에서 북쪽으로 강동 화암 주상절리가 있고 신명해변으로 이어진다. 강돌몽돌해변은 동해안에서는 보기 드문 몽돌 해변으로 파도에 밀려 몽돌들이 부딪히는 소리가 제법 요란하다.
몽돌 해변의 좋은 점은 걷기 편하고 물놀이 후에 발바닥이 모래 범벅이 되는 번거로움이 없는 것이다. 해변 환경과 화장실, 음수대, 개수대 등이 깨끗하게 관리되고 있어 캠핑지로도 활용되며 해변 주변으로 아파트 단지와 카페 등 상가가 위치해 있어 편의시설도 완벽하다.',
        35.6278381472, 129.4423980082,
        'http://tong.visitkorea.or.kr/cms/resource/22/3048822_image2_1.jpg', '울산광역시 북구 산하동', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (21, '강원특별자치도립화목원', '강원특별자치도립화목원은 강원특별자치도에서 운영하는 공립수목원으로 도심 속 산림 휴양과 자연학습 공간을 제공할 목적으로 1999년에 개원했다. 식물유전자원 수집·증식·보존을 위한 연구 등 현지 외 보전기능 강화와 대국민 자연체험학습장 및 휴식공간 제공으로 자연에 대한 소중한 가치체험의 기회를 제공하고 있다. 총 1,827종류 85천여본의 식물을 보유하고 있으며, 이 중 산솜다리, 모데미풀, 노랑만병초, 홍월귤 등 희귀식물이 129종류, 모데미풀, 금강봄맞이, 금강초롱꽃, 개느 삼 등의 특산식물이 49종류, 두메닥나무, 제비동자꽃, 등대시호, 금꿩의다리 등 기후변화취약식물보존원이 136종류가 있다.
계절별로 다채로운 꽃이 피고 지면서 색다른 풍경과 재미를 선사한다. 봄에는 벚나무길과 철쭉원에서 꽃놀이를 즐기고, 여름에는 분수광장과 메타세쿼이아 숲에서 시원한 한때를 보낸다. 가을에는 국화전시회를 감상하고 겨울에도 따뜻한 유리온실에서 푸릇한 기운을 느낄 수 있다.
강원특별자치도립화목원 내 산림박물관도 운영한다. 산림박물관은 강원특별자치도의 동식물, 산촌생활 등 강원특별자치도 산림을 주제로 한 상설전시관을 비롯해 기획전시관, 3D영상관 등을 갖추었다. 연못, 인공폭포, 정자가 어우러지는 수생식물원과 어린이들을 위한 자연 놀이터인 어린이정원도 화목원의 놓칠 수 없는 포인트다.',
        37.9233347533, 127.7254731639,
        'http://tong.visitkorea.or.kr/cms/resource/60/3331860_image2_1.jpg', '강원특별자치도 춘천시 화목원길 24', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (22, '강진 백운동별서정원', '조선중기 처사 이담로(聃老, 1627~1701)가 들어와 계곡 옆 바위에 ‘백운동(白雲洞)’이라 새기고 조영(造營)한 원림으로, 자연과 인공이 적절히 배합된 배치와 짜임새 있는 구성을 이루며 우리 전통 원림의 원형이 그대로 보존된 별서이다. 백운동이란 ‘월출산에서 흘러 내린 물이 다시 안개가 되어 구름으로 올라가는 마을’이라는 뜻으로 약사암과 백운암이 있었던 곳으로 전해진다. 월출산 능선의 기암괴석의 바위들이 보이는 이곳에 전형적인 호남전통원림의 형태를 띄고 있으며, 마침 이곳길도 해남부터 시작하는 삼남길이 지나는 길이기도 하다. [출처:강진군 문화관광 홈페이지]',
        34.7420918349, 126.6998840655,
        '', '전라남도 강진군 성전면 월하안운길 100-63', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (23, '강화 고인돌 유적 [유네스코 세계유산]', '고인돌은 청동기시대의 대표적인 무덤으로, 주로 경제력이 있거나 정치권력을 가진 지배층의 무덤으로 알려져 있다. 강화 부근리 고인돌은 길이 710cm, 높이 260cm, 넓이 550cm의 커다란 돌을 사용했으며, 형태는 북방식 고인돌로서 고대사 연구에 좋은 자료가 되고 있다. 2000년 11월 29일 호주 케인즈 제24차 유네스코 세계문화유산위원회에서 고창, 화순의 고인돌과 함께 세계문화유산으로 등재됐다. 강화도 고인돌 유적은 고인돌의 제작 기겁과 선사시대의 다양한 무덤 형태 등에 대해 알 수 있는 공간으로 아이와 함께 역사 여행을 즐기기 좋은 곳이다.',
        37.7734344221, 126.4374730037,
        'http://tong.visitkorea.or.kr/cms/resource/97/3309797_image2_1.jpg', '인천광역시 강화군 하점면 부근리', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (24, '강화 덕진진', '강화 덕진진은 고려시대 강화해협을 지키던 외성의 요충지이다. 병자호란 뒤 강화도를 방비하기 위해 내성, 외성, 돈대, 진보 등의 12진보를 만들었는데 그중의 하나이다. 현종 7년(1666) 국방력 강화를 위해 해군주둔지(수영)에 속해 있던 덕진진을 덕포로 옮겼으며, 숙종 5년(1679)에 용두돈대와 덕진돈대를 거느리고 덕진포대와 남장포대를 관할함으로써 강화해협에서 가장 강력한 포대로 알려져 있었고, 강화 12진보 가운데 가장 중요한 곳을 지키고 있었다.
1866년 병인양요 때는 양헌수의 군대가 덕진진을 거쳐 정족산성으로 들어가 프랑스 군대를 격파하였으며, 1871년 신미양요 때는 미국 함대와 가장 치열한 포격전을 벌인 곳이다. 그러나 초지진에 상륙한 미국군대에 의하여 점령당하였다. 이때 건물에 몸을 숨겨서 적과 싸울 수 있도록 쌓았던 낮은 담은 모두 파괴되었다. 1976년 성곽과 돈대를 고치고 남장포대도 고쳐 쌓았으며, 앞면 3칸·옆면 2칸의 문의 누각도 다시 세웠고, 당시의 대포를 복원하여 설치하였다.
지금 덕진진에는 문루인 공조루(拱潮樓), 남장포대, 덕진돈대, 대원군이 세운 해문방수비(海門防守碑)가 있다. 해문방수비에는 ‘바다의 문을 막고 지켜서, 다른 나라의 배가 지나가지 못하도록 하라.’는 의미의 ‘海門防守他國船愼勿過’가 새겨져 있다.',
        37.6492060173, 126.5256656119,
        '', '인천광역시 강화군 불은면 덕진로 31-9', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (25, '강화 서도 중앙교회', '강화도는 19세기 말 조선이 문호를 개방할 때 지리적 여건으로 가장 먼저 영향을 받은 탓에 서양인들의 선교 활동도 활발하게 이루어진 곳이다.  당시에 세워진 교회 중 지금까지 강화도에 남아있는 것만 3곳에 이른다. 1900년에 세운 성공회 강화 성당, 1906년에 세워진 강화 온수리 성공회 교회 그리고 서도 중앙 교회가 현존하고 있는 당시의 교회들이다.
그중 서도 중앙 교회는 1923년 2월 교인들의 건축 헌금으로 지어지기 시작해 같은 해 7월에 완공된 한옥 예배당이다. 1978년 주문 교회에서 서도 중앙 교회로 명칭이 변경되었다. 현재 대한 기독교 감리회 소속이다. 건물 내부는 중세 전기의 서양 교회 양식을 하고 있지만 매우 단순하여 예배실로 쓰이는 좁은 신랑과 측랑, 중앙의 강단으로만 구성돼 있다. 전면에는 강단 위에 설교대를 두었으며, 바닥은 마루를 깔았다. 이 건축물은 우리 전통 목조건물의 가구 형식을 바탕으로 서양 교회를 지었다는데 큰 의미를 지니고 있다. 옛 모습을 잘 보존하고 있으면서 지금도 종교시설로 사용되고 있다.',
        37.6436850663, 126.2442776542,
        '', '인천광역시 강화군 서도면 주문도길 256-1', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (26, '강화 용두레마을', '강화 용두레 마을은 들녘 가득히 울려 퍼지는 할아버지들의 구수한 용두레질 노래 가락에 맞춰 물을 푸는 아름다운 전통을 간직한 농어촌체험·휴양마을이다. 용두레질 체험과 천연염색, 전통음식 만들기 체험 등 우리 농촌의 소중함을 배울 수 있는 곳이다. 예부터 맑은 물이 흘러 큰 인물이 난다는 전설이 내려오는 마을이다. 마을 남쪽과 동쪽은 등산을 할 수 있는 봉화산과 국수산이 엄마 품처럼 다가오고 서쪽으로 석모도와 서해 바다가 펼쳐져 있어 아름다운 서해 낙조를 관망할 수 있다. 또한 넓은 농경지 끝자락의 바닷물이 빠지면 나타나는 갯벌에서 조개잡이와 망둥어 낚시 등을 즐길 수 있으며 겨울철에는 청둥오리와 기러기 등 철새도 많이 찾아와 또 하나의 멋진 볼거리를 제공하는 곳이다.
(출처 : 인천투어)',
        37.7184755935, 126.3604121578,
        'http://tong.visitkorea.or.kr/cms/resource/79/3388779_image2_1.JPG', '인천광역시 강화군 내가면 황청포구로385번길 8', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (27, '강화 장정리 석조여래입상', '강화군 봉천산 아래에 있는 고려시대의 석조불상이다. 두꺼운 화강암의 판석에 새겼는데, 현재는 전각을 지어서 그 안에 있다. 전체적으로 장대하고 비례가 좋은 편이나 큰 얼굴, 좁은 어깨, 큰 손 등이 다소 균형감을 깨고 있다. 높이 2.8m의 마애불로 머리 위에 큼직한 육계가 솟아 있고 얼굴은 둥근 편으로 입가부터 양쪽 볼과 눈매에 이르기까지 미소를 가득 머금고 있다. 양감이 있는 얼굴, 짧은 목, 움츠린 어깨, 형식적인 옷 주름 등에서 고려시대 불상의 특징을 잘 나타내고 있는 작품이라고 할 수 있어 제작 시기를 11세기경으로 추정하고 있다. 후손이 조상의 은공을 기리기 위하여 봉은사라는 사찰을 짓고 이 석조여래입상을 새겨 매년 제사를 올려 은혜에 보답하고자 한다는 전설이 전해져온다.',
        37.7809416705, 126.4256211035,
        '', '인천광역시 강화군 하점면 장정리 산 122', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (28, '강화 초지진', '강화 초지진은 강화의 해안 경계 부대인 12진보 중 하나이다. 해상으로부터 침입하는 적을 막기 위하여 구축한 요새로, 조선왕조실록이나 승정원일기에서 초지진 설치에 대한 기사에 따르면 조선 효종 6년(1655)에 설치된 것으로 보인다.
이곳은 1871년(고종 8) 신미양요 때 미군과 충돌했던 격전지이다. 미국 해병이 초지진에 침략해 왔을 때 전력의 열세로 패하여 점령당하였다. 이때 군기고, 화약창고 등의 군사시설물이 모두 파괴되었다. 이후 1875년(고종 12) 일본 운요호 사건 때 상륙을 시도하는 일본군과 치열한 전투를 벌인 곳이기도 하다. 일본이 조선을 힘으로 개항시키기 위해서 파견했던 운양호의 침공은 고종 13년(1876)의 강압적인 강화도 수호조약으로 이어져 일본침략의 문호가 개방되었다. 그 뒤 허물어져 돈대의 터와 성의 기초만 남아 있었으나 1973년 초지돈을 복원하였다. 정비된 초지돈대 안에는 대포가 전시돼 있다. 돈대 옆 소나무에는 신미양요 혹은 운요호 사건 때 포탄 맞은 흔적이 그대로 남아 있다.',
        37.6326163996, 126.5322543599,
        '', '인천광역시 강화군 길상면 해안동로 58', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (29, '강화 화문석 문화관', '2005년 개관한 강화화문석문화관은 화문석과 관련한 다양한 자료를 전시하고 있다. 화문석은 여러 색으로 물들인 왕골을 가지고 꽃무늬 등을 놓아 짠 돗자리로 강화도의 특산물이다. 화문석문화관은 1층에는 체험장과 작업장, 2층에는 전시실로 전시실은 짚과 인간, 완초 재배와 가공, 화문석 제작, 화문석 역사와 문화라는 주제로 구성되어 있다. 고려의 수도였던 강화도 시대부터 조선에 이르기까지 왕실과 상류층을 위한 화문석의 제작 역사를 살펴볼 수 있다. 화문석의 재료가 되는 왕골에 대한 다양한 정보와 화문석 직조 방법 등이 모형으로 전시돼 볼거리를 제공한다. 다채로운 크기와 문양의 화문석을 감상할 수 있으며 현지 공예인들과 함께 왕골 공예품을 만드는 체험 프로그램도 있다.',
        37.7957598993, 126.4547735697,
        '', '인천광역시 강화군 송해면 장정양오길 413', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (30, '강화도제적봉 평화전망대', '강화평화전망대는 강화도 최북단 양사면 제적봉에 있는 전망대이다. 남한에서 가장 가까운 거리에서 북한 주민의 생활상을 육안으로 볼 수 있어 이북 실향민과 관광객이 많이 찾는 강화도의 명소이다. 민족 동질성 회복과 평화적 통일의 기반구축을 위한 문화관광 공간으로 활용하기 위해 민통선북방지역에 지하 1층 지상 4층 규모로 건립되어 2008년 9월 5일 개관하였다.
강화 평화전망대에서 북한 황해북도 개풍군 대성면 삼달리까지의 거리는 2.3km로 해안가를 건너 예성강이 흐르고, 좌측으로는 황해도 연안군 및 백천군으로 넓게 펼쳐진 연백평야가 있으며, 우측으로는 개풍군으로 북한주민의 생활모습과 선전용 위장마을, 개성송수신 탑, 송악산 등을 조망할 수 있다. 군사분계선이 인접해 있어 신분증 지참은 필수다.',
        37.8263253898, 126.4329663820,
        'http://tong.visitkorea.or.kr/cms/resource/03/3306503_image2_1.jpg', '인천광역시 강화군 양사면 전망대로 797 (양사면)', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (31, '강화역사박물관', '강화역사박물관은 강화의 문화유산을 보존·연구하여 전시할 목적으로 세워진 공립박물관으로 갑곶돈대 옆에 있었던 것을 2010년 지금의 자리로 이전했다. 상설전시실, 기획전시실을 비롯하여 영상실 등 다양한 부대 시설을 갖추고 있다. 상설전시실에는 강화의 선사유적지와 고려왕릉에서 출토된 유물과 향교, 전통 사찰 소장품 등의 문화재가 전시되어 있고, 기획전시실에서는 해마다 다채로운 주제의 특별전이 열린다. 강화 역사의 이해를 돕기 위해 영상실과 교육프로그램을 함께 운영하고 있어 복합문화공간으로서 역할을 다하고 있다.
강화역사박물관은 고인돌공원 옆에 자리 잡고 있어 세계문화유산으로 지정된 강화고인돌과 길이 710cm, 너비 550cm 되는 거석인 청동기시대의 대표적 유물인 강화지석묘를 볼 수 있다.',
        37.7737399035, 126.4352815292,
        'http://tong.visitkorea.or.kr/cms/resource/21/3383821_image2_1.JPG', '인천광역시 강화군 하점면 강화대로 994-19', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (32, '개항장 거리', '1883년 인천 개항장 세월의 흔적이 쌓인 개항기 역사와 문화가 만나는 장소이다. 이곳은 옛 일본영사관인 중구청과 일본 제1은행 인천지점, 일본 제18은행 인천지점, 일본 제58은행 인천지점 등 근대 건축을 그대로 볼 수 있는 장소이다. 구 일본우선주식회사나 인천의 아트플랫폼, 제물포구락부 등 역사의 흔적들을 직접 눈으로 볼 수 있고 귀로 들을 수 있는 곳이다. 이곳에서는 인천문화 관광해설사가 있어 예약하면 해설을 들으며 도보 관광을 즐길 수 있다.',
        37.4729365453, 126.6210062460,
        '', '인천광역시 중구 제물량로232번안길 27 (중앙동2가)', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (33, '거제케이블카', '거제의 자연 산과 노을, 숲과 바다, 경계 없는 자연 속으로 끝없이 펼쳐지는 천혜의 절경을 감상할 수 있는 거제 케이블카. 거제 파노라마 케이블카는 학동고개에서 노자산 정상을 연결하는 1.56km 구간의 케이블카이다. 상부전망대에서는 노자산과 다도해 전경을 경계 없이 360도로 접할 수 있으며, 총 45대의 캐빈 중 10대의 크리스탈 캐빈은 바닥이 유리로 되어있어 탑승하면 노자산 숲길을 걷는 듯한 느낌을 받을 수 있다.',
        34.7843399541, 128.6330110537,
        'http://tong.visitkorea.or.kr/cms/resource/91/2815791_image2_1.JPG', '경상남도 거제시 거제중앙로 288', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (34, '건청궁', '건청궁은 경복궁 내 가장 깊숙한 곳에 자리한 궁궐로 1,873년(고종 10)에 지어졌다. 이곳은 명성황후가 일본인들에게 무참히 시해된 곳이다. 청일전쟁이 끝난 후 명성황후가 일본 세력을 배척하자 일본공사 미우라가 주동이 되어 고종 32년 (1895) 10월 8일(음력 8월 20일) 일본인들이 경복궁에 침입하여 많은 상궁과 명성황후를 시해하였다. 이 사건을 을미사변이라 한다. (출처 : 경복궁관리소 홈페이지 안내글 인용)',
        37.5774168614, 126.9779874266,
        'http://tong.visitkorea.or.kr/cms/resource/10/2527110_image2_1.jpg', '서울특별시 종로구 사직로 161 (세종로)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (35, '경기도미술관', '경기도미술관은 국내외 작가의 전시, 미술품 수집을 비롯해 연구, 교육, 홍보사업 등 프로그램을 운영하면서 다양한 문화 서비스를 제공한다. 특히 전문성과 예술성, 지역성과 세계성이 조화를 이루는 다양한 기획 전시를 통해 미술 문화의 명소로 자리 잡고 있다. 공원 내 생태 호수를 배경으로 한 미술관 건물은 전면을 유리로 시공해 맑고 쾌적한 느낌을 주는 한편, 다양한 기획전이 이루어지는 높은 층고의 전시실을 중심으로 현대미술관의 역할을 다하고 있다.
경기도 미술관에서는 누구나 쉽게 일상에서 문화예술을 접할 수 있도록 매월 마지막 주에 [문화가 있는 날]을 운영하는데 경기도 미술관의 큐레이터들이 직접 전시, 작품, 아카이브, 건축 등 매월 새롭고 다양한 주제를 가지고 이야기를 들려준다. 이를 통해 더욱 편안하고 즐겁게 예술을 즐길 수 있으며, 미술전문가가 아닌 일반인과 아이들까지 누구나 예술 작품을 감상하고 즐길 수 있는 미술관으로 호평받고 있다.',
        37.3250570533, 126.8099598046,
        'http://tong.visitkorea.or.kr/cms/resource/36/3020536_image2_1.jpg', '경기도 안산시 단원구 동산로 268', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (36, '경기미래교육 양평캠퍼스', '2008년 4월 개원한 경기영어마을 양평캠프는 2018년 경기미래교육 양평캠퍼스로 명칭 및 기능이 전환되었다. 미국의 버지니아 주를 닮은 55개 동의 건축물로 구성되어 있어 이국적인 분위기의 마을이다. 양평의 아름다운 자연환경과 어우러진 이곳은 자연스럽게 영어문화를 체험하고, 4차 산업혁명 시대의 미래교육을 선도하기 위한 다양한 체험시설이 마련되어 있다. 강의동, 야외음악당, 콘도형 기숙사, 인조 잔디축구장, 천문 관측시설 등 다양한 체험공간 및 시설들이 있다. KBS 드라마 ‘꽃보다 남자’, SBS 드라마 ‘가면’의 촬영지로 알려지면서 관광지로 주목받고 있다. 캠퍼스를 걷다가 벽화와 드라마 속 촬영지를 발견하는 재미가 쏠쏠하다.',
        37.4995839707, 127.5756214372,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=b2f7e44b-cc1b-4928-b555-fffc53551b2f', '경기도 양평군 용문면 연수로 209', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (37, '경기미래교육 파주캠퍼스', '모든 시설과 교육프로그램이 수강생의 적극적인 참여를 유도하고 있어 생활 속에서 자연스러운 영어 체험학습이 가능하다. 영어권 국가의 마을과 유사한 환경에서 세계의 다양한 문화를 체험하면서 세계시민의식을 배우고, 영어가 목적이 아닌 의사소통을 위한 수단이라는 것을 느끼게 하며, 친절하고 자세한 원어민 선생님의 지도로 영어에 대한 흥미와 외국인과의 대화에 대한 자신감을 심어 주어 수강생 스스로가 퇴소 후 즐겁게 학습에 임할 수 있도록 한다. 특히, 파주캠프는 모든 시설을 영어권 국가 마을의 모습으로 인테리어하여 이국적이고 새로운 환경 속에서 문화적 체험을 통해 자연스럽게 영어를 습득하도록 만들고 있다. 시청, 호텔, 슈퍼마켓, 경찰서, 공연장 등의 공공시설과, 카페, 레스토랑, 전문숍 등의 상업시설, 야외공연장, 체육관 등의 놀이 및 특화시설 등 영어마을 내의 모든 시설은 실제 주거공간이자 체험공간이다.이처럼 파주캠프에서는 교육(Education),체험(Experience),놀이Entertainment)의 3E가 결합된 시설과 교육프로그램을 체험할 수 있다. 또한 2016년부터 미래교육을 시범운영한 이래, 현재 창의교육, 문화예술교육, 평화교육, 메이커 교육을 운영하고 있으며 미래직업체험관, 메이커스페이스를 통한 미래교육 전문공간을 조성하였고, 2022년 창의과학키움체험센터를 조성하여 증강현실, 로봇코딩 등 체험형 미래교육을 제공하고 있다. 2022년 신규 개방형시민대학 사업을 통해 지역협력을 통한 성인대생 프로그램을 운영하고, 파주캠퍼스의 지역적 특성과 인프라를 활용한 평화교육시민대학 사업을 추진하고 있다.',
        37.7845044938, 126.7026428319,
        '', '경기도 파주시 탄현면 얼음실로 40', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (38, '경복궁', '경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 ‘북궐’이라 불리기도 했다. 경복궁은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여 칸에 이르는 건물들을 다시 세웠다. 그러나 또다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.',
        37.5788222356, 126.9769930325,
        'http://tong.visitkorea.or.kr/cms/resource/33/2678633_image2_1.jpg', '서울특별시 종로구 사직로 161', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (39, '경북 상주 함창·이안·공검면 [슬로시티]', '상주시는 2011년 6월 국제슬로시티연맹 폴란드 총회에서 슬로시티로 공식 인증되었다. 넓은 평야와 적정한 강우량, 여름철 높은 기온, 많은 일조량 등 농작물 재배에 좋은 조건을 가지고 있으며, 상주시 합천읍, 공검면, 이안면을 상주 슬로시티 핵심지역으로 지정하였다.
상주는 신라 서라벌과 가야 고령 가야국의 옛 고도이며, 농업과 음식문화가 발달한 고장으로 전 가구 수의 42%가 농가이다. 또한, 대표적인 자전거 도시로 상주 자전거 박물관이 있는 곳이다. 낙동강 자락에 있는 제1경 경천대와 상주 자전거 박물관, 삼한시대 3대 저수지의 하나인 공검지, 함창의 명주 고을 등의 명소가 있으며, 상주 곶감, 상주 한과와 명실상감한우 등의 특산품이 있다. 쌀, 누에고치, 곶감이 유명하여 삼백의 고장으로 알려져 있으며, 건강 관련 제품이 많아 한방 바이오산업으로 주목을 받고 있다.
상주슬로시티 방문센터가 있는 함창명주박물관에서 누에고치짓기, 고치실로 명주 짜기 등 비단제작 과정과 관련도구를 볼 수 있고, 박물관에는 1년 뒤에 배달되는 느린 우체통이 있다. 인근 명주테마파크와 경상북도 잠사곤충사업장의 나비생태원, 누에곤충체험학습관이 있으며, 이안면 흑암리에는 전통기법으로 5대째 항아리를 제작하는 상주옹기마을이, 지산리 일대는 실제 귀농이나 귀촌체험을 할 수 있다.',
        36.5701909740, 128.1794354346,
        'http://tong.visitkorea.or.kr/cms/resource/12/3042612_image2_1.jpg', '경상북도 상주시 함창읍 함창중앙로 135', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (40, '경주 감은사지', '감은사는 동해안에 있는 통일신라시대의 사찰로 신라 문무왕이 삼국을 통일한 후 부처의 힘을 빌어 왜구의 침입을 막고자 세운 절이다.
절이 다 지어지기 전 문무왕이 죽자, 그 뜻을 이어받아 아들 신문왕이 682년 완성했다. 문무왕은 죽기 전 내가 죽으면 바다의 용이 되어 나라를 지키고자 하니 화장하여 동해에 장사를 지낼 것을 유언했다. 문무왕을 장사한 곳이 대왕암이며, 신문왕이 이곳 사찰을 완성하고 부왕의 은혜에 감사한다는 의미로 이름을 감은사라 지었다고 한다.
지금은 3층 석탑 2기와 금당 및 강당 등 건물터만 남아있다. 감은사는 황룡사, 사천왕사와 함께 나라를 보호하는 호국사찰로 알려져 있으며, 언제 절이 무너졌는지는 밝혀지지 않고 있다. 현재의 모습은 1979년부터 2년에 걸쳐 전면 발굴조사를 실시해 얻은 자료를 통해 창건 당시의 건물 기초대로 노출 정비한 것이다. 아울러 금당의 지하에는 동해 바닷물이 유입되는 통로를 만들어 바다용이 된 문무대왕의 이 통로를 통해 들어와 휴식을 취할 수 있도록 상징적인 공간을 만들었다는 흔적도 있다.
감은사터의 상징인 우뚝 솟은 두 3층 석탑은 통일신라 초기의 탑으로, 수십 개의 부분을 조립해 세웠다. 이 탑들은 우리나라 석탑 가운데 가장 큰 것이다.',
        35.7482196956, 129.4768554542,
        '', '경상북도 경주시 양북면 용당리 17', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (41, '경주 문무대왕릉', '신라 제30대 문무왕(재위 661∼681)의 의 수중 왕릉으로 신라인들의 창의적인 생각을 엿볼 수 있는 곳이다. 동해 봉길리 앞바다에서 200m 떨어진 곳에 있으며, 해수면 위로 동서 35m, 남북 36m, 높이 5.3m 정도 노출되어 있다. 문무왕은 아버지인 태종 무열왕의 업적을 이어받아 고구려를 멸망시키고, 당의 침략을 막아 삼국통일을 이루었다. 또한 병부, 창부 등 중앙관청을 창설하였고, 지방통치를 위한 5 소경제도와 9 서당 10 정의 군사제도의 기틀을 마련하는 등 국가 체제 완성의 기초를 제공하였다. 삼국통일을 이룬 문무왕이 자신이 용이 되어 침입해 들어오는 왜구를 막겠다며, 자신의 시신을 불교식으로 화장하고 유골을 동해에 묻어 달라고 한 유언을 따라서 장사한 것이다. 이 바위를 대왕암 또는 대왕바위라 하며, 바닷속 왕릉은 세계적으로 유례가 없다. 대왕암은 자연 바위를 이용하여 만든 것으로 바위섬에 동서와 남북으로 십자 모양의 인공수로를 만든 다음, 가운데 작은 못처럼 파서 항을 이루고 있다. 이곳에 3.7m, 폭 2.06m, 두께 0.9m의 넓적한 거북모양의 돌을 물속 2m 깊이에 놓아 그 밑에 유골을 모신 것으로 추측되고 있다. 그의 아들 신문왕은 동해 근처에 감은사를 세워 법당아래 동해를 향한 배수로를 만들어 용이 된 문무왕이 왕래할 수 있도록 설계하였다.',
        35.7407289853, 129.4827662639,
        '', '경상북도 경주시 양북면 동해안로 1366-9', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (42, '계룡돈대', '돈대는 적의 움직임을 살피거나 공격에 대비하기 위해서 영토 내 접경지역이나 해안지역의 감시가 쉬운 곳에 쌓은 소규모 관측·방어시설이다. 대개 높은 평지에 쌓아두는데 밖은 성곽으로 높게 하고, 안은 낮게 하여 포를 설치해 두는 시설물이다. 망월평야 남서방향의 언덕 위에 자리하고 있는 계룡돈대는 바깥벽 측면에 기록이 남아 있으며, 망월돈대와 함께 관아에서 관리하던 돈대이다. 남아 있는 기록에 의해 조선 숙종 5년(1679)에 설치된 것임을 알 수 있는데, 이처럼 돈대의 재작년대에 대한 기록이 남아 있는 것은 보기 드문 예이다.
1679년(숙종 5)에 완성된 48돈대에 이후 5개 돈대가 추가로 지어졌다. 48돈대는 황해도, 강원도, 함경도 승군 8,900명과 어영청 소속 어영군 4,262명이 80일 정도 걸려서 쌓았다. 계룡돈대는 1679년(숙종 5)에 쌓은 48돈대 가운데 하나로 ‘광대돈대’로도 불린다. 둘레가 108m인 방형구조로 석벽 높이는 290㎝~670㎝이다.
동쪽 출입문 옆으로  ‘강희 18년 4월 일 경상도 군위어영(康熙十八年四月日 慶尙道 軍威御營)’라고 명문(銘文)이 있는데, ‘강희 18년(1679) 4월 어느 날 경상도 군위현에서 온 어영청 소속의 군대’라는 의미이다. 이것은 돈대 쌓은 시기를 알 수 있는 귀중한 명문이다. 계룡돈대는 진보에 소속되지 않고 진무영에서 파견된 천총(千摠) 세 사람이 돌아가며 담당하는 영문 소속 돈대였다.',
        37.7235276660, 126.3538237336,
        'http://tong.visitkorea.or.kr/cms/resource/63/3309563_image2_1.jpg', '인천광역시 강화군 내가면 황청리', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (43, '계족산 황톳길', '계족산 황톳길은 2006년 임도 총 14.5km에 질 좋은 황토 2만여 톤을 투입하여 조성한 맨발 트래킹의 명소이다. 경사가 완만해 맨발로 걷기 무리 없을 뿐 아니라, 여름에는 발끝부터 황토의 시원한 기운이 올라와 무더위를 식히기 좋다. 신발을 신고 걸을 수 있는 둘레길도 있으며, 산책로의 시작지점에는 황톳길 이야기와 미술 작품을 살펴볼 수 있는 ‘숲 속 광장’이 있다. 놀이터나 정자 등 편의시설, 세족장이 있어 발을 담그고 잠시 머물기 좋다. 황톳길을 걷다가 푯말을 따라 20분간 더 오르면 계족산성으로 향한다. 해발 420m에 위치한 계족산성을 외부의 침입을 방어하는 삼국시대의 성벽으로 현재는 계족산의 대표 전망대로 자리매김했다. 곳곳에 벤치가 있어 산책 도중 쉬기 좋고, 자리에 앉으면 대청호, 벚꽃 나무 군락 등 대전 시내가 한눈에 담긴다. 또한 매년 5월 ‘계족산맨발축제’를 개최하며 4월~10월까지 다채로운 주말 프로그램을 진행한다.',
        36.4032551669, 127.4492219591,
        'http://tong.visitkorea.or.kr/cms/resource/08/2661808_image2_1.jpg', '대전광역시 대덕구 장동 산85', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (44, '고려궁지', '고려 고종 19년에 몽골군의 침입에 대항하기 위하여 왕도가 강화로 옮겨졌다. 이때 옮겨진 도읍터가 고려궁지로 원종 11년 환도할 때까지 39년간 사용되었다. 고려궁지는 규모는 작으나 송도 궁궐과 비슷하게 만들어졌고, 궁궐 뒷산 이름도 송악이라 하여 왕도의 제도를 잊지 않으려 하였다고 한다.병자호란 당시 강화성이 청군에게 함락된 사실이 있으며, 그 후 고려 궁터에는 조선 궁전건물(장령전, 행궁, 만령전, 봉선전, 외규장각, 척천정, 세심재 등) 및 유수부 건물들이 있었으나 1866년 병인양요 시 프랑스군에 의해 건물 등이 소실되고 지금은 유수부의 동헌과 이방청 건물만이 남아있다. 이 곳은 1977년 보수 정화되어 우리 민족의 자주정신과 국난극복의 역사적 교훈을 안겨주는 국민 교육의 장으로 활용되고 있다.',
        37.7508851202, 126.4854359021,
        'http://tong.visitkorea.or.kr/cms/resource/49/3350949_image2_1.jpg', '인천광역시 강화군 강화읍 북문길 42', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (45, '고려삼계탕', '고려삼계탕은 1960년 서소문동에 개점한 이래 40년 넘게 삼계탕 한 가지만을 고집해 온 삼계탕 전문점이다. 2대째 가업을 이어오고 있으며 삼계탕의 대명사로 미국, 일본, 동남아, 대만 등지의 관광객들에게도 알려진 곳이다.
고려삼계탕만의 맛의 비결은 엄선된 재료에 있다. 금산에서 재배한 4년산 금산 산삼과 닭 사육 전문농장에서 키운 토종닭 옹추로 맛을 낸 삼계탕이 대표 메뉴이다. 옹추는 기존의 삼계탕용 닭보다 4~5일쯤 더 키워서 약간 크고 살이 충분히 올라 맛이 한결 부드럽고 담백하다. 지방이 적고 육질이 담백해 오래 삶아도 고기가 흐트러지지 않는다.
본관 외관은 수원성에서 모티브를 얻어 전통과 현대가 만난 젠 스타일로 축조한 독특한 건축 형태이다. 1층에서 4층까지 현대적인 감각과 전통적인 아름다움을 조화시켜 세련되고 깔끔한 공간을 연출한다. 6층에는 전통 한옥의 멋스러움을 그대로 살린 전통 온돌방 공간도 마련되어 있다.',
        37.5629906688, 126.9728938549,
        '', '서울특별시 중구 서소문로11길 1 (서소문동)', 1, '02-752-9376', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (46, '고복자연공원', '공원 지정 면적은 1.95㎢로 오봉산의 자연림, 동굴(용굴), 사찰(신흥사), 야외조각 전시장 등이 있으며, 수변길(데크) 3.65km가 조성되어 있다. 주변 마을에는 포도, 복숭아, 배를 재배하는 과수 단지가 있다. 공원 변에 광장과 이화여대 미술대 강태성 교수가 조성한 야외조각 공원이 있어 방문객들에게 또 하나의 볼거리를 제공해 주고 있다. 특히, 야외수영장이 개장하는 여름방학 시기에 많은 사람들이 찾아온다. 중간지점에 있는 ‘민락정’에서 내려다보는 경관이 일품이며, 벚꽃 필 무렵의 고복 자연공원은 아는 사람만 아는 숨겨진 명소이다. 고복 자연공원 주변에는 갈비, 한방오리와 메기매운탕을 전문으로 하는 음식점 등이 산재해 있어 미식가들의 입맛을 돋워 주며, 수변 전망이 가능한 카페들이 있어 휴식공간을 제공해 준다.',
        36.6000120047, 127.2274836912,
        '', '세종특별자치시 연서면 도신고복로 586', 8, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (47, '고사포해수욕장', '부안군 변산면 운산리에 있는 해수욕장으로, 약 2km에 이르는 백사장과 방풍을 위해 심어 놓은 약 300m의 넓고 긴 소나무 숲이 장관을 이룬다. 일대의 해수욕장 중에서 가장 큰 규모를 자랑한다. 서해의 다른 해수욕장보다 물이 맑고 깨끗하며 모래도 곱고 부드럽기로 정평이 나 있다. 물의 수온도 적당해서 여름철이면 해수욕을 즐기러 오는 가족 단위 관광객이 많이 찾는 곳이다. 소나무숲은 해수욕을 즐긴 후 그늘에서 휴식을 취하거나 야영장으로 적당하다. 또 서해의 해수욕장답게 일몰이 아름답기로 유명하다. 매월 음력 보름이나 그믐쯤에는 해수욕장에서 이곳까지 사람들이 현대판 모세의 기적이라고 부르는 약 2km의 바닷길이 열린다. 이때에는 섬까지 걸어갈 수 있으며, 조개나 낙지, 해삼 등을 잡는 즐거움도 누릴 수 있다. 해수욕장 앞에는 새우 모양을 닮았다 하여 하섬으로 불리는 작은 섬이 있는데 원불교 재단의 소유로 일반인들은 사전에 허가를 받아야 섬에 들어갈 수 있지만 바닷길이 열리면 섬까지 갯벗체험은 가능하다.',
        35.6619179225, 126.5082532575,
        'http://tong.visitkorea.or.kr/cms/resource/67/636167_image2_1.jpg', '전북특별자치도 부안군 변산면 노루목길 8-8 (변산면)', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (48, '고성 어명기 고택', '강원특별자치도 고성군에 위치한 어명기 고택은 현 주인 어명기의 2대조 어용수가 1860년대에 사들인 집이다. 약 400년 전에 이 터에 처음 자리 잡은 집으로 화재로 불탄 것을 영조 26년(1750) 경에 어태준이 옛 모습대로 복원하였다고 한다. 경사진 언덕바지에 넓게 집터를 잡고 ㄱ자형의 몸채가 위치하고 왼쪽으로는 방앗간, 오른쪽으로는 행랑채, 뒤쪽으로 헛간채를 두었다.
몸채는 안채와 사랑채를 따로 짓지 않고 한 건물에 모두 시설하였다. 앞면 4칸, 옆면 3칸 규모로 세줄겹집이다. 건물 왼쪽에 넓은 부엌과 외양간이 있고 중앙에 안방과 건넌방, 대청이 자리 잡고 있으며, 오른쪽으로 사랑방 3칸이 세로로 있다. 외양간을 부엌 앞으로 돌출시킨 것은 보통 고성군에서 많이 볼 수 있는 평면형태이다. 부엌과 외양간 사이 간격에는 지붕을 설치하고 뒤쪽으로 뒤주를 붙박이 하여 곡식을 저장할 수 있도록 하였다.
사랑채 중 가장 앞쪽에 위치한 사랑방은 주인의 일상거처이며, 뒤쪽 2칸의 사랑방은 손님을 맞이하는 곳이다. 이 집에서 주목되는 점은 ''더그매''이다. 지붕과 천장사이에 나타난 공간을 더그매라고 하는데, 이 집에는 안방, 웃방, 가운데방 모두에서 더그매가 보인다. 필요할 때 집안물건을 넣어두는 보관장소로 사용되기도 하며, 지붕으로부터 내려오는 한기를 막기도 한다.

※ 위 국가유산은 일반적으로 관람이 가능하나, 소유주 등의 개인적 사정으로 관람이 제한될 수도 있습니다.',
        38.3147764738, 128.5177529605,
        'http://tong.visitkorea.or.kr/cms/resource/56/3378456_image2_1.JPG', '강원특별자치도 고성군 죽왕면 봉수대길 131-7', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (49, '고성 왕곡마을', '왕곡마을은 해안에서 내륙 쪽으로 약 1.5km 지점에 있으며 석호인 송지호(둘레 4km, 넓이 20만 평)와 해발 200m 내외의 봉우리 형태인 야산 다섯 개에 둘러 쌓여 외부와 차단된 ''골'' 형태의 분지를 이룬다. 마을의 동쪽은 골무산(骨蕪山), 남동쪽은 송지호, 남쪽은 호근산(湖近山)과 제공산(濟孔山), 서쪽은 진방산(唇防山), 북쪽은 오음산(五音山)으로 막혀 있고 마을 북쪽에 위치한 오음산에서 남서방향으로 마을을 관통하며 흐르는 왕곡천 좌우에 종심이 깊은 촌락으로 형성되어 있다.

마을 중앙의 개울을 따라 이어져 있는 마을 안 길을 중심으로 가옥들이 자연스럽게 자리 잡고 있으며 가옥과 가옥 사이에 비교적 넓은 텃밭이 있어서 따로 담이 없고 텃밭을 경계로 가옥들이 분리되어 있는 것이 특징이다. 왕곡마을의 가옥구조는 안방, 도장방, 사랑방, 마루, 부엌이 한 건물 내에 수용되어 있으며 부엌에 가축우리가 붙어 있는 겹집구조이다. 마을 안길 과 바로 연결되는 앞마당은 가족의 공동작업 공간 역할을 하면서 타인에게 개방적이었던 반면에 비교적 높은 담으로 둘러싸인 뒷마당은 여인들의 공간으로 비개방적이다. 뒷마당은 보이지 않고 지붕만 보여 여인들의 활동공간을 배려한 구조이다.',
        38.3405981100, 128.4995815837,
        '', '강원특별자치도 고성군 왕곡마을길 41', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (50, '고성 학동마을 옛 담장', '고성군 하일면에 있는 학동마을은, 1670년경 전주최씨 가문에서 학이 마을에 내려와 알을 품고 있는 모습을 꿈속에서 보고, 날이 밝아 그곳을 찾아가 보니 과연 산수가 수려하고 학이 알을 품고 있는 형국의 지형을 보고, 명당이라 여겨 입촌한 후 학동이라 명명하면서 형성된 유서 깊은 마을로 전해진다. 마을 뒤편은 수태산 줄기가 있으며, 마을 앞에는 좌이산이 솟아 있는 소위 좌청룡 우백호의 지세이며 마을 옆으로는 학림천이 흐르고 있어 전통 마을의 배산임수형 입지를 잘 보여주고 있다.
마을의 가옥은 상당 부분 새마을운동 당시 슬레이트 기와로 개량되었으나 문화재자료 ‘육영재’, ‘최씨고가’ 등 일부 전통가옥이 잘 보존되어 있어 전통 마을로서의 면모를 갖추고 있다. 학동마을의 담장은 수태산 줄기에서 채취한 납작돌(판석 두께 2~5㎝)과 황토를 결합하여 바른 후 쌓은 것으로 다른 마을에서는 볼 수 없는 고유한 특징이 있으며 건물의 기단, 후원의 돈대 등에도 담장과 동일한 방식으로 석축을 쌓아 조화를 이루고 있다. 남도의 정취가 물씬 풍기는 마을 주변 대숲과 잘 어우러져 수백 년을 거슬러 고성으로 끌어들이는 듯한 마을 안길의 긴 돌담길은 아름다운 경관을 연출하고 있으며, 황톳빛 돌담길을 따라 걷노라면 아련한 고향의 정취를 느낄 수 있는 곳이다.',
        34.9444364434, 128.1950033417,
        'http://tong.visitkorea.or.kr/cms/resource/91/3349191_image2_1.jpg', '경상남도 고성군 하일면 학동돌담길 11-5', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (51, '고소동 천사벽화마을', '고소동 벽화마을은 여수에서 가장 오래된 자연부락으로, 언덕에 자리해 바다와 돌산대교, 거북선대교가 한눈에 들어온다. 임진왜란 당시에는 충무공 이순신 장군이 작전을 세우고 명령을 내리던 역사적인 곳이기도 하다. 2012 여수엑스포를 계기로 여수시와 고소동 주민들이 힘을 합쳐 담벼락에 벽화를 채우고 지리적 이점을 활용해 관광명소화 했다.  진남관부터 고소동 언덕을 지나 여수해양공원에 이르는 길이가 1,004m라서 천사벽화골목이라고도 불린다. 골목은 7개 구간으로 나누어 여수엑스포 주제인 ‘해양’에 관련된 이야기를 비롯해 여수지역 역사와 문화, 풍경 그리고 이순신 장군과 수군들에 관한 그림 등을 벽화에 담았다. 전망 쉼터가 있으니 잠시 앉아 숨을 고르기 좋다. 마을 안에 자리한 충무공 대첩비각도 놓칠 수 없는 볼거리이다.  이곳에는 이순신 장군의 전적을 기린 좌수영대첩비(통제이공 수군대첩비·보물)와 장군의 덕을 추모하는 타루비(보물)가 있다. 벽화골목이 끝나는 곳에는 여수해양공원이 있다. 여수 시민이 주말 나들이 장소로 즐겨 찾는 이곳은 밤에 특히 아름답다.',
        34.7383571176, 127.7405900393,
        'http://tong.visitkorea.or.kr/cms/resource/77/2601677_image2_1.jpg', '전라남도 여수시 종화동', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (52, '고창 고인돌 유적 [유네스코 세계유산]', '고인돌은 청동기시대 대표적인 무덤양식으로, 우리나라에 3만여 기 이상이 분포되어 있는 것으로 알려져 있다. 그중에서도 전남과 전북을 포함한 한반도 서남해안 지역에 밀집 분포하고 있다. 전북지역은 2003년 조사 기준으로 424개 군집에 2,632기가 분포한 것으로 알려져 있다. 특히 고창군은 2023년 조사기준으로 전북 고인돌의 65% 이상인 1,748기의 고인돌이 존재하는 것으로 파악되었다. 고창 고인돌유적은 죽림리와 상갑리, 도산리 일대에 무리 지어 분포하고, 2000년 12월 유네스코 세계문화유산에 등재되었다. 단일 구역으로서는 우리나라에서 가장 큰 군집을 이루고 있을 뿐만 아니라 탁자식, 바둑판식, 개석식 등 다양한 형식의 고인돌이 한 지역에 분포한다. 또한 고인돌 축조과정을 알 수 있는 채석장의 존재 등 동북아시아 고인돌 변천사를 규명하는 데 있어 중요한 자료가 되고 있다. 세계유산위원회에서는 등재 기준 제3항(독특하거나 아주 오래된 것)을 적용, 세계유산적 가치를 인정했다. 고창은 고인돌 박물관을 비롯하여 제1코스 ~ 제5코스 1.8km (고창읍 죽림리, 아산면 상갑리 일대), 제6코스 1.7km (고창읍 도산리 일대)를 탐방코스로 만들어 3천 년 전 다양한 고인돌의 모습 그대로 전승하여 오고 있다.',
        35.4488111857, 126.6498571269,
        'http://tong.visitkorea.or.kr/cms/resource/22/3018322_image2_1.jpg', '전북특별자치도 완주군 상관면 죽림리 665-9', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (53, '고창 운곡람사르습지', '2011년 람사르습지로 지정된 운곡습지는 국가생태관광지로 멸종 위기의 야생동물을 비롯한 희귀 동식물을 관찰할 수 있는 최고의 장소로 다양한 생명체들이 내뿜는 강인한 생명력을 느낄 수 있다. 운곡람사르습지 주변의 물은 다양한 물줄기로 운곡습지로 모이며, 대상지 주변 일대의 수계는 크게 고창천에서 인천강으로 흘러들어 북쪽 곰소만으로 흘러 들어가고 있다. 안덕제, 운곡제 등 크고 작은 웅덩이(물웅덩이)가 위치하고 있으며, 이러한 둠벙은 주변 생태연못 등으로 흘러 습지생태 자원들의 서식처로서 훌륭한 역할을 하고 있다.
운곡저수지 일대에는 국화과, 벼과, 사초과, 마디풀과, 십자화과, 장미과, 콩과가 많이 분포한다. 오베이골에는 버드나무군락과 은사시나무군락, 운곡마을에는 저수지와 연접된 지점에 수생식물 집중 분포, 안덕제골에서 저수지와 연접된 곳은 연꽃과 같은 부엽식물, 그리고 계곡 안쪽에는 다양한 습지식물이 분포되어 있다.
동물의 경우는 멸종위기 야생동물 1급인 수달과 황새, 멸종위기 야생동물 2급인 삵, 구렁이, 팔색조, 새호리기, 단비, 그리고 문화재청 천연기념물인 붉은배새매, 황조롱이 등이 서식한다. 이 외에도 어류 533개체, 양서·파충류 12종, 조류 611개체, 포유류 11종, 곤충 297종, 나비 22종이 서식한다.',
        35.4693367042, 126.6540256843,
        'http://tong.visitkorea.or.kr/cms/resource/63/3342463_image2_1.jpg', '전북특별자치도 고창군 아산면 운곡서원길 362', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (54, '고판화박물관', '고판화박물관은 강원도 원주시 신림면 황둔리 치악산 자락에 위치한 국내 유일의 판화 박물관이다. 2004년 여름에 개관한 고판화박물관은 한국, 중국, 일본, 티베트, 몽골, 인도, 네팔 등 판화가 발전되었던 동양 각국의 고판화 자료들을 수집, 보관, 전시하며 연구, 교육하는 곳으로 6,000여 점의 유물을 소장하고 있다.
고판화박물관에서는 다양한 체험 프로그램도 운영 중이다. 숲속 판화학교는 1일 체험코스로 유로 프로그램이다. 가능한 체험으로는 목판 제작, 전통책 만들기 그리고 전통문양 인출체험을 통해 목판화 티셔츠 만들기 등이 있다. 체험 신청은 홈페이지와 전화, 이메일을 통해 신청해야 한다.',
        37.2571333614, 128.1286979760,
        'http://tong.visitkorea.or.kr/cms/resource/36/3375236_image2_1.JPG', '강원특별자치도 원주시 신림면 물안길 62', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (55, '고하도이충무공유적', '고하도는 목포에서 서남쪽으로 2km 정도 떨어진 작은 섬이다. 섬의 명칭은 동국여지승람에서 볼 수 있고, 난중일기에는 보화도라고 하였으며, 고하도 이충무공기념비에는 고화도로 표기되어 있다.
명량대첩 후 고군산도까지 올라갔다가 다시 내려온 이순신 장군은 이듬해 고금도로 옮기기까지 여기에 머물렀다. 이곳은 서남해안의 바닷길과 영산강의 내륙 수로가 연결되는 지점으로 지리적으로 중요한 곳이었으며, 섬의 서북쪽이 병풍처럼 높이 솟아 있어 겨울에 북서풍을 막아주는 구조였기 때문에 배를 감추기에도 적합한 곳이다. 장군은 이곳에 돌로 성을 쌓아 적의 배를 구별하고, 군자금을 모으기 위해 오가는 배들에 통행첩을 받아 가도록 하여 열흘 만에 일만 석의 군량미를 비축하였다. 또한 이곳에서 무기와 배를 만들며 전열을 가다듬었다고 한다.
고하도 내의 전망대와 국립호남권생물자원관, 목화체험전시장과 목화정원 등을 방문할 수 있으며 해상 데크와 둘레 숲길이 조성되어 있어 산책하기도 좋다.',
        34.7665225894, 126.3727234446,
        '', '전라남도 목포시 고하도길 175 (달동)', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (56, '곰소항', '곰소항은 부안군 진서면 진서리 일대에 위치하고 있다. 군산항 다음으로 두 번째 큰 항구이다. 줄포항이 토사로 메워져 폐항이 되자 1938년 진서리 앞바다의 곰섬을 중심으로 동쪽의 범섬과 연동, 서쪽의 까치섬과 작도리를 잇는 제방을 쌓아 만든 항만이며 서해어업의 전진기지항이다. 곰소라는 말은 곰처럼 생긴 두 개의 섬이라는 말과 그 섬 앞바다에 깊은 소가 있어 생긴 이름이며 “곰소 둔벙 속같이 깊다.”라는 속담이 있다. 위도와의 여객선이 운항된다. 곰소항은 하루에 130여 척의 어선들이 드나들 정도로 활성화된 어항이다. 곰소항 주변으로 대규모 곰소염전이 있고, 대한민국 최대의 젓갈시장인 곰소 젓갈시장과 수산시장, 건어물시장이 있어 많은 관광객이 찾는다.

염전이란 말 그대로 ‘소금을 만들어 내는 밭’이라고 할 수 있다. 소금은 염전에서 바람과 햇볕에 바닷물을 말려서 만들어 내는데 이것을 천일제염이라고 하며  공장에서도 소금을 만들어 내는데 이것은 화학소금이라고 부른다.
소금은 어떻게 만들어졌을까? 염전은 크게 저수지 → 증발지 → 결정지로 나누어진다. 저수지에서는 밀물 때 바닷물을 저수지에 저장해 놓았다가 긴 수로를 따라 바닷물을 증발지로 보낸다. 이때 바닷물은 5%의 소금기를 담고 있다. 증발지에서 또 7∼8일을 증발시킨다. 결정지(일명 소금밭, 항아리 조각 타일을 붙인 밭)로 바닷물을 보낸다.  소금밭 1개소에 기존의 소금 30㎏정도와 증발수를 혼합하면서 거품이 많이 나도록 하면, 거품이 떠다니면서 증발되는 염기가 거품에 부착되어 무게를 이기지 못하고 가라앉는다. 이때 바닷물은 약 25%의 소금기가 들어있는 짠물이다. 이 결정지에서 바람과 햇볕에 2∼3일 말리면 소금이 만들어진다. 그러면 고무래로 소금을 긁어모아 소금창고로 간다. 이렇게 만들어진 천일제염으로 만든 특산물인 곰소젓갈은 전국적으로 유명하여 곰소를 찾는 사람이면 누구나 곰소젓갈에 대한 구매력을 느낀다.',
        35.5861059588, 126.6054465080,
        'http://tong.visitkorea.or.kr/cms/resource/93/3376493_image2_1.JPG', '전북특별자치도 부안군 진서면 곰소리', 37, NULL, NOW(), NOW());
