INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (384, '용안생태습지', '금강 용안지구 생태하천은 4대 강 사업의 일환으로 용안면 난포리 일원의 금강변에 조성된 670,000㎡ 규모의 대규모 생태습지이다. 나무데크가 깔끔하게 조성되어 있어 도보여행 및 자전거 여행을 즐길 수 있으며, 청개구리광장, 풍뎅이광장, 잠자리광장, 나비광장 조류전망대, 야외학습장, 식물관찰원, 관찰데크, 갈대체험원 등이 마련되어 있다. 금강의 생태습지는 여름에는 연꽃, 가을에는 코스모스와 억새, 겨울에는 철새들의 쉼터가 되고 있으며, 용안생태습지 옆으로는 55만 평 규모로 전국 최대 규모의 시범 억새단지가 있어 가을이면 금강억새축제가 열리고 있다. 푸름 속에서 편안한 휴식을 맛볼 수 있는 진정한 힐링 여행지이다.',
        36.1337934564, 126.9259093329,
        '', '전북특별자치도 익산시 용안면 난포리 313-13 (용안면)', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (385, '용암사(옥천)', '용암사는 옥천군 장용산 중턱에 자리 잡고 있으며 대한불교조계종 제5교구 본사인 법주사의 말사로 신라 때인 552년(진흥왕 13)에 의신이 세운 사찰이다. 용암사에 있는 동서 삼층석탑은 일반적 가람배치와 달리 사방이 한눈에 보이는 북쪽 낮은 봉우리에 자리 잡고 있는데, 탑이나 건물을 세워 산천의 쇠퇴한 기운을 북돋아 준다는 고려 시대의 산천 비보 사상에 따라 건립된 것으로 보인다.
용암사에는 보물인 옥천 용암사 쌍삼층석탑과 용암사 마애불상이, 용암사 대성전에는 목조 아미타여래 좌상이 남아있다. 쌍삼층석탑은 같은 모양의 석탑 2기가 자연 암반 위에 나란히 세워진 쌍탑으로 기단 위에 3층의 탑신이 거의 체감 없이 비슷한 비율로 높게 서 있는 특이한 형태이다. 이 석탑은 각 부분의 양식과 석재의 결구 수법에서 간략화된 조성 수법으로 보아 고려 시대에 건립된 것으로 추정된다.
조선 효종 2년(1651)에 조성된 것으로 밝혀진 목조 아미타여래 좌상이 봉안되어 있는 주불전을 비롯한 당우 모두가 옛 모습은 아니나 이곳에 전해지는 동서 삼층석탑과 마애불은 천여 년 변함없이 자리를 지키고 있다. 당우는 자연과 어우러지는 산사의 전통건축미를 맛볼 수 있는 아름다운 절집이다. 동서 삼층석탑 왼편에는 등산로가 있는데, 이 길은 장령산 정상까지 이어진다.',
        36.2592375721, 127.5643100451,
        'http://tong.visitkorea.or.kr/cms/resource/21/2622321_image2_1.jpg', '충청북도 옥천군 옥천읍 삼청2길 400', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (386, '용연서원', '용연서원은 숙종 17년(1691)에 이사상 등 남인계 유생들의 주도로 이덕형과 조경의 학문과 덕행을 추모하기 위해 창건되었다. 이듬해인 숙종 18년(1692)에는 용연이라는 사액(조선시대에 왕이 사당이나 서원들에 이름을 지어 그것을 새긴 편액을 내리던 일)을 받았는데, 포천의 명소인 연못의 이름에서 따온 것이다. 용연서원은 경기도 내에서 유일하게 남인세력의 근거지에 설립되었으며, 이덕형이 임진왜란 때 세운 공로가 인정되어 대원군의 서원철폐령에도 훼철되지 않고 존속하였다.
창건 당시 경내 건물로 사우·강당·동재·서재 등이 있었으나 6·25 전쟁 등으로 소실되어, 현재는 사우만 남아 있어 일반적인 서원의 형태 구조를 갖추지 못하고 있다. 사우에는 이덕형과 조경의 위패가 봉안되어 있고, 사우는 정면 3칸, 측면 2칸의 맞배지붕으로 된 목조 기와로서 1972년에 보수하였다. 매년 3월과 8월 중정일에 향사례를 행하여 이덕형과 조경의 학문과 덕을 추모하고 있다.
용연서원은 흥선 대원군의 서원 철폐 시에도 존치되었던 서원으로서 경기도 내 유일의 남인계 서원이었다. 선현의 향사와 향촌 교화가 서원 본래의 사명이지만, 최근까지도 용연서원에서는 인근의 신북 초등학교, 영중 초등학교 학생들에게 전통 예절 교육을 실시하고 장학금을 지급하는 등 지역 교육의 일익을 유감없이 발휘하고 있다.',
        37.9502391100, 127.2289149109,
        '', '경기도 포천시 신북면 신평로72번길 1-13', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (387, '용화사(청주)', '용화사는 청주 무심천변에 자리한 통일신라시대의 사찰이다. 선덕여왕 때 은점선사가 처음 지었으며, 조선 인조 6년에 화재로 소실된 것을 영조 18년(1752) 벽담선사가 다시 짓고 용화사라 하여 오늘에 이르렀다.
경내에는 보광전을 비롯하여 명부전, 용화전, 탐진당, 적묵당, 해월루 등 목조건물과 불사리 4사자 법륜탑, 효봉스님 사리탑이 있다. 불사리 4사자 법륜탑은 우리나라에서는 보기 드문 고대 아쇼카 양식의 원주 석탑으로, 진신사리 7과가 봉안되어 있다. 산내 암자로는 고려 태조 26년(943) 도솔선사가 세웠다는 도솔암, 조선 숙종 7년(1681) 청안선사가 세웠다는 관음암이 있다.
용화사에는 1902년 고종의 비인 순빈 엄씨가 명하여 발견했다는 칠존의 석불이 안치되어 있으며, 상당산성 안에 있던 보국사를 옮겨 이 절을 지었다는 얘기도 전해진다. 이곳은 일반 사찰과 달리 시내 가운데에 있어 누구나 편히 방문할 수 있으며, 템플스테이를 운영하고 있어 편안한 마음으로 절에서 묵으며 몸과 마음을 정화하는 시간을 가지기 좋다. 봄에는 사찰 주위의 무심천에서 벚꽃축제가 열린다.',
        36.6418617421, 127.4820180268,
        'http://tong.visitkorea.or.kr/cms/resource/90/3341490_image2_1.JPG', '충청북도 청주시 서원구 무심서로 565 (사직동)', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (388, '용흥궁', '조선 제25대 왕인 철종(재위 1849∼1863)이 왕위에 오르기 전에 살던 집으로 철종이 왕위에 오르자 강화유수 정기세가 건물을 새로 짓고 용흥궁이라 이름을 붙였다. 지붕 옆면이 여덟 팔(八) 자 모양인 팔작지붕집이고, 지붕을 받치면서 장식을 겸하는 공포가 기둥 위에만 있는 주심포 양식이다. 용흥궁은 창덕궁의 연경당, 낙선재와 같이 살림집의 유형을 따라 지어져 소박하고 순수한 느낌이 든다. 경내에는 철종이 살았던 옛집임을 표시하는 비석과 비각이 있다.',
        37.7479493616, 126.4859998213,
        'http://tong.visitkorea.or.kr/cms/resource/70/3351970_image2_1.jpg', '인천광역시 강화군 강화읍 동문안길21번길 16-1', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (389, '우농타조농장', '파주시 교하읍 동패리에 위치한 ''우농타조마을''은 아름답고 아담한 동물농장에서 동물과 농촌체험을 테마로 자연을 배우고 동시에 즐길 수 있는 자연 학습공간이다.타조 및 양, 오리, 토끼, 거위 등 동물친구들에게 먹이주기 및 관찰 학습을 할 수 있으며, 타조 알 껍질 조각에 직접 그림을 그려 넣어 나만의 목걸이를 만들고 아토피 진정, 피부 보습에 좋은 타조오일과 천연 재료를 쓴 수제 비누를 직접 제작해 볼 수 있다. 또, 일일 농부가 되어 고구마 수확 체험도 해볼 수 있다. ',
        37.7052361633, 126.7142856710,
        'http://tong.visitkorea.or.kr/cms/resource/11/2482111_image2_1.jpg', '경기도 파주시 교하로 595-41 (동패동)', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (390, '우드트레인', '공전 간이역에는 김광기선생이 운영하는 우드트레인이 있다. 측백나무, 편백나무 등 힐링나무을 이용하여 모형의 우드 체험을 할 수 있다. 간이역의 운치와 기차소리를 들으며 제작하는 목공체험은 참으로 색다른 맛이다. ',
        37.1008670844, 128.0777209991,
        '', '충청북도 제천시 봉양읍 의암로 698 (봉양읍)', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (391, '우일선 선교사 사택', '우일선 선교사 사택은 1908년 제중원(현 기독병원)의 원장이 되어 의사로 선교활동을 한 미국인 선교사 우일선(Robert M. Willson)에 의해 1920년대에 지어졌다고 전해지며, 광주에 현존하는 가장 오래된 서양식 주택이다. 현재 내부를 개조하여 대한 예수교 장로회 사무실로 사용하고 있으며, 한국 근대 건축의 흐름을 이해하는 데 도움이 되는 귀중한 자료이다. 이 건물의 평면은 정사각형으로 1층에는 거실, 가족실, 다용도실, 부엌, 욕실이 있고, 2층에는 침실을 두었으며 지하에는 창고, 보일러실이 있다. 동향인 점은 한국의 전통적인 방위관을 받아들인 것이라 해석된다. 크기는 정면 10.6m이며, 구조는 벽이 두께 55mm의 회색 벽돌로 네덜란드식으로 쌓아져 있다. 내부는 회반죽으로 마감했으며, 고막이 부분은 화강석을 쌓아 처리했다. 개구부는 모두 반원형의 아치로 만들었으며, 창문 외부는 열 개 창, 내부는 오르내리 창의 이중창으로 되어 있다. 1층과 2층을 구별하기 위하여 벽돌로 돌림띠를 두어 외벽에 변화를 주었다.',
        35.1383979557, 126.9118416921,
        'http://tong.visitkorea.or.kr/cms/resource/17/2493617_image2_1.jpg', '광주광역시 남구 제중로47번길 20', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (392, '우화정(내장산)', '우화정은 내장산 국립공원 입구에 있는 연못 안의 정자로 날개가 돋아 승천하였다 하는 전설이 있어 우화정이라 부르며 거울같이 맑은 호수에 붉게 물든 단풍이 비치는 경관은 한 폭의 수채화 같다. 호수 주변에는 당단풍, 수양버들, 두릅나무, 산벚, 개나리, 산수유 등이 둘러싸여 장관을 이루고 있다. 특히 복자기나무 한 그루는 진홍색으로 물들어 가을 정취에 한몫을 한다. 우화정의 맑은 연못에는 1 급수에서만 서식하는 것으로 알려진 버들치 떼가 유유히 노닐고 있다. 각기 다른 크기와 모양의 돌로 만든 징검다리가 예스러운 정취를 더해준다.

근처에는 내장사, 케이블카승강장, 단풍열차승강장, 내장산단풍터널이 있으며 내장산은 가을단풍으로 유명한 곳이다. 내장산 초입 금선교에서 케이블카 승강장 앞까지 순환버스를 연중 운행하고 있어 내장산 주차장에 주차를 하고 셔틀버스를 타고 가면 편하게 우화정까지 갈 수 있다.',
        35.4864779890, 126.9102191565,
        'http://tong.visitkorea.or.kr/cms/resource/78/3028378_image2_1.jpg', '전북특별자치도 정읍시 내장산로 936 (내장동)', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (393, '운일암 반일암 계곡', '진안읍에서 북쪽으로 정천을 거쳐 24km를 달리면 주천면에 이르고 운장산 쪽 주자천 상류를 2km쯤 더 올라가면 기암절벽 운일암·반일암 계곡의 장관이 시작된다. 운장산 동북쪽 명덕봉과 명도봉 사이의 약 5km에 이르는 주자천 계곡을 운일암·반일암이라고 한다. 예전에는 깎아지른 절벽에 길이 없어 오로지 하늘과 돌과 나무와 오가는 구름뿐이었다 한다. 그래서 운일암이라 했고, 또한 깊은 계곡이라 햇빛을 하루에 반나절 밖에 볼 수 없어 반일암이라 불렸다. 이곳에는 용소 바위, 족두리 바위, 천렵 바위, 대불 바위 등의 집채 만 한 기암괴석들이 겹겹이 자리 잡고 있으며, 산자락에서 솟구치는 맑고 시원한 냉천수가 그 사이사이를 휘감아 흐르다가 곳곳에 크고 작은 폭포와 소를 만들어 그야말로 자연 조화의 극치이며 절경을 이루고 있다.
운일암·반일암에는 이러한 기암절벽 천혜의 자연환경을 한눈에 볼 수 있는 구름다리가 있다. 운일암·반일암 제1주차장에서 데크 탐방로를 따라 명도봉 방향으로 가면 길이 220m의 구름다리를 만날 수 있으며, 왕복 소요 시간은 1시간 정도로 진안의 새로운 관광 명소로 떠오르고 있다.
(출처 : 진안군 문화관광)',
        35.9798608986, 127.3963436460,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=fc6593cc-8bf7-4a64-a446-3381b3278277', '전북특별자치도 진안군 주천면 동상주천로 1926', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (394, '운정식당', '충북 충주시에 위치한 ‘운정식당’은 올뱅이 해장국 전문점이다. 해장국 주문 시 무생채, 깍두기, 감자조림, 김치 등 정갈한 밑반찬이 준비된다. 올뱅이 해장국에는 많은 다슬기와 시래기가 들어가 구수하면서도 얼큰하며 개운한 맛을 느낄 수 있다. 이외에도 육개장, 된장찌개 등 다양한 식사메뉴가 준비되어 있어 취향껏 메뉴선택이 가능하다.',
        36.9728900450, 127.9221553243,
        '', '충청북도 충주시 중원대로 3432-1 (문화동)', 33, '043-847-2820', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (395, '운주사', '화순 8경 중 2경인 운주사는 도선국사가 창건했다는 설, 운주스님이 창건했다는 설, 마고할미가 세웠다는 설 등이 있는데, 통일신라말 도선국사가 풍수지리에 근거해 비보 사찰로 세웠다는 이야기가 가장 널리 전해지고 있다. 비보사찰이라 함은 [돕고 보호한다]는 의미로 풍수지리상 지세를 보완해 주는 방법으로 인공적으로 탑이나 불상들을 조성하여 세워진 사찰을 의미하며 부처의 힘을 빌려 호국과 중생들의 안녕을 도모한 도선 스님의 지혜가 담긴 사찰이다.
임진왜란으로 사찰이 훼손되어 폐사로 남아있었으나 18세기에 자우가 불상과 불탑을 수리하고 약사전을 중건하였다. 발굴조사 과정에서 금동불입상을 비롯하여 순청자, 상감청자, 분청사기의 파편, 기와편 등 출토된 유물로 볼 때 늦어도 11세기 초인 고려 초기에는 건립되었을 것으로 추정하고 있다.
동국여지지에 고려 혜명스님이 1,000여 명과 함께 천불천탑을 조성했다고 기록되어 있는데, 현재는 석불 100여 기와 석탑 21기가 계곡 여기저기 남아있다. 1940년대에는 석불 213기와 석탑 30기가 운주사 내에 있었다고 전해진다. 또한, 운주사 골짜기 서쪽 언덕 위에 땅에 누워있는 한 쌍의 부부와불이 있다. 절 주변은 문화재 보호구역으로 지정되어 있고 2017년 [화순 운주사 석탑군]이란 이름으로 유네스코 세계유산 잠정목록에 최종 등재되었다.',
        34.9255908663, 126.8804275401,
        '', '전라남도 화순군 도암면 천태로 91-44 (도암면)', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (396, '운주산성', '일명 고산 산성으로 백제시대의 유물이다. 전동면 미곡리, 청송리와 전의면 동교리, 신정리 경계 지점에 운주산(459.7m)이 솟아있다. 운주산성은 바로, 이 운주산을 이용한 산성이다. 운주산성은 성의 둘레 3,210m, 폭 2m, 높이 2~8m의 웅장한 백제 산성으로, 분지형의 산세와 수려한 풍치가 일품이다. 성벽은 자연 지형을 최대한 이용하면서 축성되었는데, 북벽과 동벽은 운주산 정상에서 서쪽과 남쪽으로 뻗어 내린 능선을 따라 이어졌으며 남벽은 산봉우리를 에워싸면서 축조되었고, 서벽은 서쪽으로 뻗어 내린 능선을 가로지르면서 축성되었다. 따라서 북쪽은 해발고도가 높고 서남쪽이 낮은 지형으로 이루어져 있다. 서문, 남문, 북문에서 문지(門址)가 확인되는데 붕괴되어 자세한 형상을 알 수 없다.

이 산성은 내성과 외성으로 이루어져 있어서 고대 산성 연구에 중요한 자료가 된다. 성안에는 성문과 건물터, 우물터 등이 남아 있는데, 정상부에는 기우제(祈雨祭)를 지낸 제단으로 보이는 원형 대지가 있다. 또 성안에서는 백제 토기편과 기와편이 출토되었고, 고려시대와 조선시대의 자기편과 기와편도 발견되었다. 운주산성은 서기 660년 백제가 멸망하고 풍왕과 복신, 도침장군을 선두로 일어났던 백제  부흥 운동군 최후의 구국 항쟁지로 알려져 있다. 운주산 등산로 입구에 사찰(고산사)이 있으며 백제가 멸망한 매년 음력 9월 8일을 기해 토요일 백제 멸망기의 의자왕과 부흥기의 풍왕 그리고 백제부흥운동을 하다, 죽은 혼령을 위로하기 위해 고산제를 지내고 있다.',
        36.6829832889, 127.2420634455,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=4a26dfb0-8a67-4609-9000-76fb6f98988b', '세종특별자치시 전동면 청송리 산90', 8, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (397, '운천사마애여래좌상', '운천사마애여래좌상은 고려 전기에 제작된 것으로 추정되는 운천사 경내에 있는 마애불이다. 크기는 높이 2.1m, 무릎 폭 2.1m이다. 전라남도와 광주광역시에서는 보기 드문 거대한 불상이다. 자연 암벽을 다듬어 불상을 양각하고, 그 위에 건물을 지어 전각의 형태를 갖추어 놓았다. 전체적인 형태와 몸체 부분은 입체감 있게 조성하였으나 광배와 대좌 부분은 선으로 그 형태를 조각하였다. 육계(부처의 정수리에 있는 뼈가 솟아 저절로 상투 모양이 된 것)는 뚜렷하게 솟아 있는데, 머리카락은 나발이었으나 많이 마모되어 소발처럼 보인다. 양미간에는 백호(부처의 두 눈썹 사이에 있는 희고 빛나는 가는 터럭으로 이 광명이 무량세계를 비춘다)가 자리한 구멍이 희미하게 보이고, 눈은 명상을 하고 있는 듯 반쯤 감은 형태이다. 양쪽 귀는 매우 커서 어깨 위까지 늘어져 있으며, 귓바퀴와 귓불이 뚜렷한 편이다. 목에는 삼도가 뚜렷하게 표현되어 있고, 오른쪽 어깨를 드러낸 우견편단의 가사를 입고 있다. 운천사마애여래좌상은 노천불로 알려져 왔지만, 고려시대 기와가 발굴됨에 따라 조성 당시 마애불을 보호하는 전각 형태의 건물이 있었을 것으로 추측된다. 전체적으로 불상을 크게 조성하다 보니 신체적 균형이 맞지 않고, 경직된 얼굴과 도식화된 옷자락 등을 보아 고려 전기의 작품으로 추정된다.',
        35.1418410394, 126.8542355879,
        'http://tong.visitkorea.or.kr/cms/resource/28/3367228_image2_1.jpg', '광주광역시 서구 금호운천길 85-15 (쌍촌동)', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (398, '울산 12경', '[울산 1경 : 태화강 국가정원 십리대숲]
우리나라 제2호 국가정원으로 태화강 양편에 형성된 4.3㎞의 국내 최대 규모의 십리대숲이 어우러져 아름다운 풍광을 연출한다.
[울산 2경 : 대왕암공원]
신라시대 문무 대왕비가 죽어서 남편처럼 동해의 호국룡이 되고자 이 바다에 잠겼다는 전설이 전해지는 곳으로 우리나라 동남단에서 동해 쪽으로 가장 뾰족하게 나온 부분의 끝지저이다.
[울산 3경 : 가지산 사계]
영남알프스 내의 가장 높은 산인 가지산(1,241m)은 봄에는 진달래와 철쭉군락지가, 여름에는 맑은 계곡물과 녹음, 가을이면 단풍이 곱게 물들고 겨울에는 설경이 아름답다.
[울산 4경 : 신불산 억새평원]
가지산과 간월산에 이어져 영축산에 이르는 주봉으로 영축산 사이 약 3km 구간에는 넓고 평탄한 능선이 이어지면서 억새밭이 펼쳐지는 가을 억새 평원이다.
[울산 5경 : 간절곶 일출]
동해안에서 가장 먼저 해가 뜨는 간절곶은 포항의 호미곶보다 1분 강릉 정동진 보다 5분 정도 해가 빨리 뜨는 일출 명소로 매년 1월1일 일출을 보기 위해 많은 관광객이 찾고 있다.
[울산 6경 : 반구대 암각화와 천전리각석]
바위에 새겨진 그림으로 반구대암각화는 선사시대 해양어로문화를 이해하는 데 중요한 유적이며, 천전리각석은 신라시대에 해당하는 세선화, 명문 등 각종 문양이 새겨져 있다.
[울산 7경 : 강동·주전 몽돌해변]
울산 동구 여름철 대표 해변 관광지로 울산 북구 강동동 일대 해변으로 몽돌로 이루어져 있다.
[울산 8경 : 울산대공원]
울산대공원은 도심공원으로는 전국 최대 규모(369만㎡)로 시민들의 명품 휴식 공간이자 도심 생태공원이다.
[울산 9경 : 울산대교 전망대에서 바라본 야경]
국내 최장 단경간 현수교인 울산대교와 울산의 3대 산업단지 및 7대 명산을 조망할 수 있는 울산의 랜드마크이다.
[울산 10경 : 장생포 고래문화마을]
고래의 모든 것일 한곳에서 살펴볼 수 있는 국내 유일의 고래특구이다. 박물관, 생태체험관, 장생포항의 옛 모습 등을 두루 살펴볼 수 있다.
[울산 11경 : 외고산 옹기마을]
선조들의 지혜와 슬기를 이어오고 있는 전국 최대의 옹기마을로 옹기의 제작 과정과 쓰임새를 체험하는 공간이다.
[울산 12경 : 대운산 내원암 계곡]
깊은 숲과 청량한 계곡, 반석들을 품고 있는 대운산은 예로부터 영남 제일의 명당으로 원효대사가 마지막으로 수행한 내원암이 있다.',
        35.5475915537, 129.2918939669,
        'http://tong.visitkorea.or.kr/cms/resource/11/3078011_image2_1.jpg', '울산광역시 남구 태화동', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (399, '울산대공원', '1960년 이후 중화학공업을 위주로 한 공업도시로서 울산의 이미지는 ‘성장’이라는 긍정적인 면이 있지만 공해도시 그리고 삶의 질이 열악한 도시라는 부정적인 측면이 더 부각되기 시작하였고, 이러한 상황 속에서 청정제 역할을 할 공원이 절실히 요구되었다. 이에 따라 1986년부터 울산광역시는 남구 공업탑 로터리 주변 신정동과 옥동 일대 364만여㎡의 부지를 매입ㆍ제공하였고, SK주식회사는 1996년부터 10년 동안 울산대공원 시설을 조성한 후 이를 울산광역시에 무상 기부하였다.

이렇게 설립된 울산 대공원은 시민 삶의 질 향상에 기여함은 물론, 도시의 균형적 성장의 시작점이 되고 있으며, 산업도시 울산이 산업과 자연이 조화를 이루는 도시로 전환하는 기폭제가 되었다. 울산대공원은 시민들이 이용하기 편리하며, 풍부한 산업환경과 울산의 문화를 그대로 느낄 수 있도록 설계되었다. 남녀노소 다양한 계층을 위한 체험의 장이자 문화와 휴식의 공간이며, 공원의 효율적인 관리를 통하여 생동감 넘치는 다양한 프로그램을 진행한다. 주민들에게 부담 없이, 지속적해서 방문할 수 있는 공간이 되고자 자연생태에 관한 교육 및 체험 학습도 제공하고 있다.',
        35.5308045918, 129.2940414740,
        'http://tong.visitkorea.or.kr/cms/resource/22/3072322_image2_1.jpg', '울산광역시 남구 대공원로 94', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (400, '울산대교 전망대', '울산대교 전망대는 높이 63M(해발 203M)로 화정산 정상에 위치해 있다. 전망대에 올라서면 2015년 5월 개통한 국내 최장이자 동양에서 3번째로 긴 단경 간 현수교인 울산대교와 울산의 3대 산업인 석유화학, 자동차, 조선산업 단지 및 울산 7대 명산을 조망할 수 있다. 주간에 바라보는 울산의 전경과, 야간에 바라보는 공단과 도심의 야경은 색다른 즐거움을 제공한다. 전망대에는 동구 관광기념품 기프트샵과 카페 등을 운영하고 있다.',
        35.5017849664, 129.4062491748,
        '', '울산광역시 동구 봉수로 155-1', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (401, '울주 대곡리 반구대 암각화', '언양읍 대곡리에는 경승지인 반구대가 있는데 연고산의 한 자락이 뻗어 내려와 이곳에 와서 우뚝 멎으면서 기암괴석으로 절정을 이루고 있으며, 마치 거북이 넙죽 엎드린 형상이므로 반구대(盤龜臺)라 한다. 두동면 천전계곡(川前溪谷)으로부터 흘러내리는 옥류가 이곳에 모여 호반을 형성하니 절승가경(絶勝佳景)으로 이름이 높다. 그래서 옛날부터 경향각처의 시인묵객들은 이곳을 찾아 시영(詩詠)으로써 경관을 즐겼다고 한다. 암각화란 선사시대의 사람들이 생활 주변에서 일어난 갖가지 일들을 주제로 삼아 그것을 바위에 새겨서 그린 그림이다. 주로 커다란 바위 등 집단의 성스러운 장소에 그렸는데, 사람들은 그곳에 모여서 각종 의례를 거행하였다고 추측된다.

울주 대곡리 반구대 암각화는 태화강의 한 지류인 대곡천의 소위 ‘건너 각단’이라고 부르는 곳에 그려져 있으며, 그림이 집중된 곳의 바위면의 크기는 너비 10m, 높이 3m이다. 그러나 그 좌우에서도 적지 않은 형상들이 확인되고 있어 암각화가 새겨진 바위는 모두 10여 개에 이른다. 이 암각화는 신석기시대부터 여러 시기에 걸쳐서 제작되었으리라 여겨지며 시대별 양식의 차이를 살필 수 있다. 표현 대상의 내부를 모두 쪼아낸 면쪼기 (面刻, 모두 쪼기) 기법과 윤곽만을 쪼아낸 선쪼기(線刻) 기법으로 나눌 수 있다. 새겨진 물상은 크게 바다동물과 육지동물, 사람, 도구 등으로 나눌 수 있다. 바다동물로는 고래, 물개, 거북 등이, 육지동물로는 사슴, 호랑이, 멧돼지, 개 등이 많이 보인다. 사람은 얼굴만 그려진 경우와 바로 선 모습, 옆으로 선 모습, 배에 탄 모습 등을 볼 수 있다. 도구로는 배, 울타리, 그물, 작살, 노(弩)와 비슷한 물건 등을 볼 수 있다. 이러한 모습은 선사인들의 사냥 활동이 원활하게 이루어지고 사냥감이 풍성해지길 바라는 마음으로 바위에 새긴 것으로 여겨지는데, 동물과 사냥 장면을 생명력있게 표현하고 사물의 특징을 실감나게 묘사한, 사냥미술인 동시에 종교미술로, 선사시대 사람의 생활과 풍습을 살필 수 있는 최고 걸작품으로 평가된다. 이처럼 인류 최초 포경기록이자 한반도 최초 미술작품 반구대 암각화 및 우리나라에서 최초로 발견된 암각화 천전리각석은 대한민국 대표 역사 관광 자원이다.',
        35.6037219778, 129.1774107206,
        'http://tong.visitkorea.or.kr/cms/resource/29/2675629_image2_1.jpg', '울산광역시 울주군 언양읍 반구대안길 285', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (402, '울주 천전리 명문과 암각화', '바위 위나 큰 절벽, 동굴 안 벽면 등에 사물이나 기호를 쪼기, 새기기, 칠하기 등의 기법으로 그린 그림을 바위그림, 암각화(巖刻畵)라고 한다. 바위그림은 후기 구석기시대의 유적에서도 발견되지만, 전형적인 사례들은 신석기 및 청동기시대 유적에서 주로 찾아진다. 바위그림은 대개의 경우, 풍요, 다산 제의의 산물로 이해하고 있다.
국보 제147호인 울주 천전리 명문은 태화강 물줄기인 대곡천 중류의 기슭에 각종 도형과 글, 그림이 새겨진 암석으로, 아래·위 2단으로 나누어 서로 다른 내용이 다른 기법으로 표현되어 있으며, 전체적으로 조각이 가득하다. 윗단에는 쪼아서 새기는 기법으로 기하학적 무늬와 동물, 추상화된 인물 등이 조각되어 있다. 사실성이 떨어지는 단순화된 형태인데 중앙부의 태양을 상징하는 듯한 원을 중심으로, 양 옆에 네 마리의 사슴이 뛰어가는 모습과 맨 왼쪽의 반인반수(半人半獸:머리는 사람, 몸은 동물인 형상)상이 눈에 띈다. 표현이 소박하면서도 상징성을 갖고 있는 듯한 이 그림들은 청동기시대에 제작된 것으로 보인다.
아랫단은 선을 그어 새긴 그림과 글씨가 뒤섞여 있는데, 기마행렬도, 동물, 용, 배를 그린 그림 등 다양한 내용으로 구성되어 있다. 이 중 기마행렬도는 세 군데에서 나타나고 있으며, 간략한 점과 선만으로도 그 모습이 잘 표현되어 있다. 배그림은 당시 신라인의 해상활동을 보여주는 중요한 자료가 된다. 글자는 800자가 넘는데 왕과 왕비가 이곳에 다녀간 것을 기념하는 내용으로, 법흥왕대에 두 차례에 걸쳐 새겨진 것으로 추정된다. 내용 중에는 관직명이나 6부 체제에 관한 언급이 있어 6세기경의 신라사회 연구에 귀중한 자료가 되고 있다.
오랜 시간에 걸쳐 여러 사람이 이루어 놓은 작품으로, 선사시대부터 신라시대까지의 생활, 사상 등을 생생하게 그리고 있다. 어느 특정 시대를 대표한다기보다 여러 시대의 모습을 담고 있어 더욱 의미가 깊은 유적이다.',
        35.6143401858, 129.1752272795,
        'http://tong.visitkorea.or.kr/cms/resource/14/3335114_image2_1.jpg', '울산광역시 울주군 두동면 천전리 산 210-2', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (403, '웅도', '* 행정자치부, 한국관광공사, 도서문화연구원 등에서 뽑은 ‘휴가철 가고 싶은 33섬’ - 2년 연속 선정(2016년, 2017년)

웅도는 본래 서산군 지곡면의 관할 지역에 편입되어 있었는데 1914년 행정구역 개편 시 웅도를 하나의 행정리로 독립시켜 웅도리라 하여 서산군 대산면에 편입시켰다. 이후 1991년 대산면은 읍으로 승격되었다. 웅도는 섬의 모양이 웅크리고 있는 곰과 같이 생겼다 해서 웅도 또는 곰섬이라 불리고 있다. 가로림만 내해의 정중앙에 자리하고 있으면서 대산읍의 7개 도서 중 유일한 유인도서이다.

웅도 마을은 조수간만의 차에 따라 육지와 연결된 마을이 되기도, 섬마을이 되기도 하여 시간에 따라 다양한 경관을 자랑하고 있다. 세계 5대 갯벌 중 하나인 가로림만을 중심으로 형성되어 있어 생태자원도 풍부하다. 웅도의 특산물로는 낙지, 바지락, 굴, 김 등이 있는데 6월 말에서 7월 초에 잡히는 낙지는 연하고 맛이 좋으며 바지락은 쌀뜨물같이 희고 맛이 시원하며 해장국 감으로 으뜸이다.',
        36.9193798994, 126.3832318437,
        '', '충청남도 서산시 대산읍 웅도1길 92', 34, '041-660-2601', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (404, '원대리 자작나무 숲 (속삭이는 자작나무 숲)', '원대리 자작나무 숲은 속삭이는 자작나무 숲이라고도 부르는 자연 생태관광지이다. 1974년부터 1995년까지 138ha에 자작나무 690,000본을 조림하여 형성된 이곳은 숲 속에 7코스의 탐방로와 숲 속 교실, 전망대, 생태연못, 인디언 집, 나무다리, 나무계단 등의 시설이 있어 산림욕과 힐링을 즐길 수 있다. 입구인 자작나무 숲 안내소에서 시작되는 숲길을 따라 걸어야 자작나무 숲에 닿을 수 있다. 숲길은 두 가지 길인데, 도보로 80분가량 소요되는 원정임도와 1시간가량 걸리는 원대 임도다. 등산로를 따라 오르면 수령이 20년 이상 되는 자작나무가 빽빽하게 들어찬 숲이 펼쳐진다. 자작나무 숲은 하얀 줄기와 잎이 빛나는 모습이 마치 눈이 내린 것처럼 보이며  하늘을 향해 뻗은 자작나무 숲은 이국적인 풍취를 느끼게 한다.

자작나무 숲 안에서는 다양한 난이도와 거리의 탐방로를 선택할 수 있다. 가장 인기 있는 코스는 순백의 자작나무 숲 집중 생육 지역을 볼 수 있는 1코스(자작나무 코스)로, 약 0.9km 거리이며, 약 50분 정도 소요된다. 겨울철에는 흰 눈에 뒤덮인 숲과 새하얀 자작나무가 어우러져 사진 촬영 장소로 인기가 좋다. 어린이가 있는 가족 단위의 방문객이라면 유아 숲 체험원에서 숲 속교실, 인디언집 등 자연을 주제로 한 다양한 체험 활동을 즐길 수도 있다.

※ 산불예방과 산림보전을 위해 일부 기간에는 입산 통제가 있을 수 있으니 사전 확인 후 방문요망',
        38.0092669329, 128.1943057023,
        'http://tong.visitkorea.or.kr/cms/resource/73/3072373_image2_1.jpg', '강원특별자치도 인제군 인제읍 자작나무숲길 760', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (405, '원조미륵산순두부', '익산 미륵사지석탑 인근에 있는 전통 순두부식당으로 가족 단위 여행객들은 물론, 현지민들도 많이 찾는 사계절 맛집이다. 계절에 따라 달라지는 다양한 기본 반찬과 함께 당일 생산한 국내산 생두부나 순두부를 맛볼 수 있다. 특히 순두부는 바지락이 들어가 있어 시원하고 칼칼한 맛을 자랑한다. 해물이 듬뿍 들어간 해물파전과 탱탱한 도토리묵 무침도 함께 먹기 좋다. 식사와 함께 인절미가 나와 두부가 나오는 동안 맛볼 수 있다.',
        36.0116063357, 127.0242981949,
        'http://tong.visitkorea.or.kr/cms/resource/88/2796188_image2_1.jpg', '전북특별자치도 익산시 금마면 미륵사지로 397', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (406, '원조소문난산곰장어', '3대째 대를 이어 운영하고 있는 식당이다. 곰장어 당일 입고, 당일 판매를 원칙으로 신선한 음식만 제공하고 있다. 정통 곰장어를 표방하고 있는 ‘소문난 곰장어’는 산지 직송으로 곰장어를 공급받기에 신선하고 쫀득한 육질을 자랑한다. 이곳에서는 곰장어를 양념구이와 소금구이 그리고 통구이의 3종류로 메뉴가 구성되어 있다. 1층은 일반 손님을, 2층은 단체를 위한 연회석으로 꾸며놓았다.',
        35.2206409979, 129.0815158420,
        '', '부산광역시 동래구 금강공원로26번길 34', 6, '051-554-8400', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (407, '원주한지테마파크', '원주한지테마파크는 한지를 주제로 각종 전시, 교육 체험을 제공하며 한지문화예술을 감상할 수 있는 문화공간이다. 2011년부터 한지문화재단이 위탁운영을 맡아서 한지 관련 문화예술교육 실행과 한지 전문인력 양성, 해외문화예술교류, ‘원주한지문화제’라는 축제를 통해서 다양한 활동을 펼쳤다. 이것은 시민과 함께 하는 한지가 현대와 어우러져 시민 문화예술로 성장하는 계기가 되었다. 원주한지테마파크는 본관 건물과 야외공원으로 구성되어 있으며 1층은 한지역사실, 한지체험실, 아트숍, 작은도서관, 2층은 기획전시실과 80명이 사용 가능한 회의실이 있다.',
        37.3343736211, 127.9353798084,
        '', '강원특별자치도 원주시 한지공원길 151', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (408, '원천항', '지방어항으로 지정된 원천항은 남해군 대표 관광지인 금산 자락 아래 위치한 항구이다. 남해바래길 본선 10코스 앵강다숲길의 시작점으로 길 항내 원천 방파제는 유명한 낚시 포인트 중 하나이다. 파도치는 소리가 앵무새 소리와 닮았다고 하여 ＂앵강만＂이라고 불리는 만곡진 바다를 한눈에 바라볼 수 있는 항구이다. 주변 명소로는 남해 가천 다랭이 논과 금산 보리암 등이 있으며 항구에는 횟집과 멸치쌈밥을 맛볼 수 있는 음식점들이 있다.',
        34.7630385142, 127.9476879781,
        'http://tong.visitkorea.or.kr/cms/resource/26/3373926_image2_1.JPG', '경상남도 남해군 이동면 신전리 1138-10', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (409, '월계동 장고분', '월계동장고분은 영산강 상류의 낮은 구릉지대에 있는 삼국시대 고분이다. 이 무덤은 모양이 전통악기인 장고 또는 장구와 비슷하다고 하여 예로부터 장고분이라고 불러왔고, 근처 마을도 장구촌이라고 했다. 이 무덤은 2기로 이루어져 있는데 1992년 이 일대가 첨단과학산업단지로 조성되는 과정에서 학계의 주목을 받았다. 이후1993년과 1995년에 전남대학교 박물관이 발굴 조사를 실시하여 무덤의 규모와 구조가 밝혀졌다.
1호분은 봉분의 길이가 44m이며, 주검을 안치한 석실은 원형의 분구에 있고, 그 남서쪽에 석실을 드나들 수 있는 통로가 있다. 발굴 당시에 석실의 천장은 무너져 내려앉은 상태였는데 1997년에 다른 고분을 참고하여 복원하였다. 분구는 깊이 1~2m 남짓한 방패모양의 도랑에 감싸여 있는데 이 도랑에서 많은 원통형 토기 조각들이 발견되었다. 2호분은 분구의 길이가 33m 로  1호분과 비슷하다. 무덤의 구조와 형태, 출토유물이 일본의 전방후원분과 비슷한 점이 많아 고대 한일관계사를 밝히는데 귀중한 자료를 제공하고 있고 만든 시기는 5~6세기 것으로 추정된다.',
        35.2139521320, 126.8421181890,
        '', '광주광역시 광산구 월계로 155 (월계동)', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (410, '월미도', '1989년 7월 문화의 거리가 조성된 이래 문화예술의 장, 만남과 교환의 장 그리고 공연놀이마당 등으로도 알려지기 시작한 월미도는 인천 하면 떠올릴 만큼 유명한 곳이다. 이곳은 카페, 회센터 등 조화를 이루며 늘어서 있어 시원한 바다를 바라보며 구미에 맞는 음식을 골라 즐길 수도 있다. 휴일엔 각지에서 수십만 명의 인파가 몰리며 인천시민과 그 주변 시민의 휴식 공간으로 자리하고 있다.',
        37.4754052899, 126.5975833151,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=2cd598bb-97b8-46aa-9e8c-2c5d24cd98d1', '인천광역시 중구 월미문화로 36 (북성동1가)', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (411, '월영교', '월영교란 명칭은 시민의 의견을 모아 댐 건설로 수몰된 월영대가 이곳으로 온 인연과 월곡면, 음달골이라는 지명을 참고로 확정되었다. 낙동강을 감싸듯 하는 산세와 댐으로 이루어진 울타리 같은 지형은 밤하늘에 뜬 달을 마음속에 파고들게 한다. 천공으로부터 내려온 달을 강물에 띄운 채 가슴에 파고든 아린 달빛은 잊힌 꿈을 일깨우고 다시 호수의 달빛이 되어 아름다운 기억으로 남아 있으려 한다. 월영교는 이런 자연 풍광을 드러내는 조형물이지만, 그보다 이 지역에 살았던 이응태 부부의 아름답고 숭고한 사랑을 오래도록 기념하고자 했다. 먼저 간 남편을 위해 아내의 머리카락으로 만든 한 켤레 미투리 모양을 이 다리 모습에 담았다. 그들의 아름답고 애절한 사랑을 영원히 이어주고자 오늘 우리는 이 다리를 만들고 그 위에 올라 그들의 숭고한 사랑의 달빛을 우리의 사랑과 꿈으로 승화시키고자 한다.',
        36.5768149396, 128.7601169125,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=337bf51c-a36e-495c-bd6b-a44c56c93b01', '경상북도 안동시 상아동', 35, '054-821-0649', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (412, '월출산 기찬랜드', '월출산의 맑은 계곡물을 활용해 자연형 풀장을 조성한 물놀이장으로 영암읍에 위치하고 있어 접근성이 좋다. 일반 풀장과 같이 다양한 편의시설을 보유하고 있으며, 월출산을 조망하면서 물놀이를 즐길 수 있는 전라남도 대표적인 하계휴가 관광지이다. 기찬랜드의 야외 풀장마다의 수심은 0.5m, 0.7m, 0.9m. 1.1m, 1.5m 등 다양하게 있으며 실내 물놀이장은 별도 비용을 내고 이용할 수 있다.
야외에서 물놀이를 하다가 기찬묏길 나무 그늘아래를 산책할 수 있으며 취사도 가능해서 마치 계곡에서 노는 듯한 분위기를 느낄 수 있다. 별도 비용을 부담하고 계곡물을 따라 줄지어 있는 평상과 정자를 이용하거나 평상 가격이 부담이 된다면 돗자리만 챙겨서 주변의 나무그늘에 자리를 잡으면 된다.
월출산 기찬랜드 안에는 물놀이장뿐만 아니라 인근 문화시설로는 가야금산조기념관, 낭산김준연선생기념관, 가야금테마공원, 게스트하우스(기찬재)등이 있다.',
        34.7919415671, 126.6867923386,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=b7a7144c-a1a9-409b-9731-b9fd5485acab', '전라남도 영암군 기찬랜드로 19-3', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (413, '월출산국립공원', '월출산은 ‘달 뜨는 산’이라는 이름에 걸맞게 아름다운 자연경관과 유수한 문화자원, 그리고 남도의 향토적 정서가 골고루 조화를 이룬 한반도 최남단의 산악형 국립공원이다. 소백산맥이 목포 앞바다로 흘러가다 평지에 돌출된 잔구 형태의 월출산은 천황봉(810.7m)을 중심으로 산 전체가 수석의 전시장이라 할 만큼 기암괴석으로 이루어져 있다. 백제의 왕인(王仁)박사와 신라말 도선(道詵) 국사의 탄생지이기도 하며, 호남의 소금강산이라 불린다. 또한 천황봉을 중심으로 남쪽에는 단아한 모습의 무위사, 서쪽에는 도갑사가 있는데 도갑사의 해탈문, 무위사의 극락전, 마애여래좌상 등을 비롯한 많은 문화재가 있다.
월출산의 입구인 도갑사를 지나 5km 정도 오르면 항상 물이 고여있어 용이 살았다고 전해지는 기이한 9개 단지모양의 구정봉(九井峰)에 이르며 그 아래로 500m쯤 내려가면 국보로 지정된 마애여래좌상이 있다. 월출산의 최고봉인 정상에 오르면 동시에 300여 명이 앉을 수 있는 평평한 암반이 있다. 월출산의 구름다리도 또 다른 매력을 준다. 이 다리는 지상 120m 높이에 건설된 길이 52m, 폭 0.6m의 한국에서 가장 긴 구름다리이다.
사자봉 왼쪽 산 중턱 계곡에서는 폭포수가 무려 일곱 차례나 연거푸 떨어지는 칠치폭포의 장관을 볼 수 있고, 도갑사(道甲寺)와 무위사(無爲寺)로 내려가는 길목에 펼쳐진 미왕재의 갈대밭은 가을이면 황홀한 절경을 이룬다. 특히 서해를 배경으로 펼쳐지는 일몰풍경은 말로 이루 표현할 수 없을 정도로 아름답다. 봄에는 진달래와 철쭉꽃이 만발하고, 여름에는 시원한 폭포수와 천황봉에 항상 걸려있는 안개, 가을에는 단풍, 겨울에는 설경 등 사시사철 다양하고 독특한 모습을 자랑한다.
왕인(王仁) 박사 유적지에서 구정봉(九井峰)으로 오르는 도중에 있는 도갑사(道岬寺)는 고려시대에 도선국사(道詵國師)가 창건한 절이라고 한다. 산문인 해탈문(解脫門:국보) ·석조여래좌상(石造如來坐像:보물) ·도선국사 수미선사비(守眉禪師碑) 등이 있다. 주위는 노송과 대나무가 울창하여 여름철의 서늘함과 늦가을의 단풍으로 관광객이 모여든다. 월출산 남쪽계곡에 있는 금릉경포대(金陵鏡布臺)는 명소로, 부근에 월남사지(月南寺址)가 있으며, 이곳에 모전석탑(模傳石塔:보물)·월남사지 석비(石碑:보물) 등의 문화재가 있다. 그 남서쪽에 무위사 극락전(無爲寺極樂殿:국보) ·선각대사편광탑비(禪覺大師遍光塔碑:보물) 등이 있다.',
        34.7752579126, 126.7217341104,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=4de8d68e-6f08-491b-a51c-761c582c9422', '전라남도 영암군 영암읍 천황사로 280-43', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (414, '유관순기념관(서울)', '유관순기념관은 유관순 열사의 독립 정신을 기리기 위하여 이화여자고등학교 내에 1974년 완공한 전시관이다. 이곳은 유관순 열사가 공부하며 거닐던 곳으로, 1919년 3월 1일 유관순을 비롯한 이화학당의 학생들이 이 교정의 기숙사 뒷담을 넘어 남대문 쪽으로 달려가 만세를 불렀던 역사의 현장이라는 점에서 장소의 역사성이 살아있는 곳이다. 완공 이후 여러 차례 증축을 통해 현재 1,700석 규모의 강당으로 되어 있으며, 이화인의 시작과 끝인 입학식과 졸업식이 이곳에서 거행된다. 또한, [유관순상] 시상식을 비롯한 각종 행사가 이곳에서 열린다.
2층 홀에는 유관순 열사의 사진과 유품 등을 전시하는 공간이 마련되어 있는데, 사진 자료의 양이나 규모 면에서 충남 천안에 있는 유관순기념관에 비해 매우 소박하지만, 서울 도심에서 유관순 열사의 흔적을 찾아볼 수 있는 귀중한 장소이다. 또한 학교 내에는 유관순 동상과 유관순 열사가 빨래했던 우물터가 남아 있다. 이 밖에도 이화여자고등학교 교내에는 심슨기념관, 이화여고 100주년기념관 등의 역사 관광지가 있어 유관순기념관과 함께 둘러볼 수 있다.',
        37.5644487857, 126.9702718302,
        'http://tong.visitkorea.or.kr/cms/resource/33/1569133_image2_1.jpg', '서울특별시 중구 통일로4길 30-1 (순화동)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (415, '유달산 둘레길', '다도해의 경관이 시원하게 펼쳐지는 길로 6km, 약 2시간 30분 정도 소요가 되는 둘레길이다. 한말의 대학자인 정만조 선생이 1907년에 세우고 지역 문인들의 모임 장소였던 목포시사, 달성사의 애틋한 철거탑, 국내 최초의 야외 조각 공원의 작품들을 감상할 수 있다. 낙조대에서 고하도 목포대교 부근을 빨갛게 물들이는 해넘이와 그 사이를 오고 가는 크고 작은 선박들의 모습이 아름답다.

(출처 : 목포 문화관광)',
        34.7973273985, 126.3693563356,
        'http://tong.visitkorea.or.kr/cms/resource/56/2511656_image2_1.jpg', '전라남도 목포시 죽교동', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (416, '유성온천지구', '유성온천지구는 대전 원도심에서 서쪽으로 약 11km가량 떨어진 유성구의 중심을 이루고 있는 대단위 온천관광타운이다. 우리나라 116개의 온천지구 가운데 가장 오래된 역사와 부존량, 사용량에서 전국 최대 규모를 자랑하고 있다. 유성의 온천수는 50~400m로 구성된 화강암 단층 파쇄대에서 생성된 물로 지하 200m 이하에서 분출되는 27~56℃ 정도의 고온 열천이다. 온천수에는 약 60여 종의 각종 성분이 함유되어 있으며, 산성도(pH) 7.5∼8.5의 약알카리성 단순천이다. 특히 실리카 (SiO2) 성분의 함량이 40mg/L이상이어서 실리카온천으로 명성을 떨치고 있다.',
        36.3553449050, 127.3448131576,
        'http://tong.visitkorea.or.kr/cms/resource/42/3035142_image2_1.JPG', '대전광역시 유성구 봉명동 574 일대', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (417, '유회당사당', '유회당사당은 보문산 남쪽 기슭에 위치한 조선시대 문화재이다. 조선 영조 때 호조판서를 지낸 유회당 권이진(1668∼1734) 선생이 건물 뒤 산에 있는 부모의 묘에 제사를 지내면서 독서와 교육을 하기 위해 1714년(숙종 40)에 지었다.
유회는 [부모를 간절히 생각하는 효성스러운 마음을 늘 품고자]하는 뜻을 담고 있으며, 중국 명나라 때 학자인 전목제의 [명발불매 유회이인]이라는 시에서 따온 말이다. 유회당은 앞면 4칸, 옆면 2칸 건물로 활수담이라는 작은 연못 뒤쪽에 있다. 앞면과 양쪽 면에 난간이 돌려진 툇마루가 있고 가운데 넓은 대청마루를 중심으로 양쪽에 온돌방을 배치하였다. 기궁재는 유회당, 삼근정사 등을 관리하기 위한 재실 건물로, 제사나 종친회 등에 사용된다. 넓은 대청을 중심으로 안방, 건넌방, 부엌 등이 있다. 이곳은 문화재적 가치를 인정받아 1989년 3월 18일, 대전광역시의 유형문화재로 지정되었다.',
        36.2788325595, 127.4083789899,
        'http://tong.visitkorea.or.kr/cms/resource/51/3063051_image2_1.JPG', '대전광역시 중구 운남로85번길 32-20', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (418, '육산', '육산은 세종에서 가장 쾌적한 갈빗집이다. 대표메뉴는 정성스럽게 조제하여 숙성시킨 수제 돼지양념갈비이다. 또한 육산에서 직접 개발한 함흥 물냉면과 함흥 비빔냉면은 돼지양념갈비와 함께 먹거나 고기를 다 먹은 후 식사로 먹으면 깔끔하게 입가심할 수 있다. 이외에도 점심 특선 메뉴도 많이 찾는다. 점심 특선 메뉴 중 하나인 궁중소갈비찜정식은 만원 상당의 냉면이 포함되어 있어, 달짝지근한 소갈비찜과 냉면을 합리적인 가격으로 먹을 수 있다.
육산은 직장 회식, 가족 단체모임, 외식을 하기에 최적의 장소를 제공하기 위해 다양한 단체석을 구비했다. 단, 창가석 및 단체석 예약은 전화 예약만 가능하다. 고급스러운 인테리어로 총 200석을 완비했으며, 금강뷰가 보이는 창가석과 개별 룸이 준비되어 있다. 그중 연회갑 40석은 프라이빗한 개별 룸에서 식사가 가능하고, 단체석은 창가석 4 테이블을 포함해 총 60명까지 수용 가능하다. 룸은 12인실 1개, 8인실 3개가 있으며, 중간 가벽이 개폐가 가능하다.',
        36.4833237791, 127.2932726334,
        'http://tong.visitkorea.or.kr/cms/resource/09/2869709_image2_1.jpg', '세종특별자치시  시청대로 209 금강르네상스 3층', 8, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (419, '윤봉길의사기념관', '예산 덕산온천 인근 충의사(매헌 윤봉길 의사 유적) 내에는 윤봉길의사기념관이 들어서 있다. 매헌윤봉길의사기념관은 서울에 있고, 윤봉길의사기념관은 예산에 있다.
이 기념관이 있는 충의사는 윤봉길 의사(1908~1932)가 태어나서 중국으로 가기 전까지 거주하던 곳이기도 하며, 지금은 사적지화 되어 윤 의사의 생가가 보존되고 그를 기리는 사당 및 기념비, 동상 등이 들어서 있다.
기념관에는 이곳에서 태어나 농촌 계몽운동을 하다가 중국으로 건너가서 1932년에 상하이 훙커우 공원에서 일본군 행사에 폭탄을 투척하여 일본군에 큰 피해를 입히고 우리나라의 독립 의지를 세계에 보여준 후 일본에서 사형당하기까지 윤 의사의 일생을 표현한 그림, 전시물, 영상, 디오라마 등이 전시되어 있다. 또한 윤 의사가 남긴 뒤 보존되어 보물로 지정된 유품들도 전시되어 있다. 그리고 관람 전 큐브 활동지를 만들어 관람 후기를 작성한 뒤 귀가하여 그림봉투 화분으로 활용할 수 있는 프로그램도 있다.
주변에는 충의사, 덕산온천 외에 보부상유품전시관(예덕상무사), 내포보부상촌, 덕산 메타세쿼이아길, 가야산, 덕숭산, 수덕사, 옥계저수지, 남연군묘 등이 있어 연계하여 관광할 수 있다.',
        36.6865252872, 126.6513693512,
        'http://tong.visitkorea.or.kr/cms/resource/18/3039818_image2_1.jpg', '충청남도 예산군 덕산면 덕산온천로 183-5', 34, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (420, '은하사(김해)', '김해시 삼방동의 신어산 중턱에 자리한 산지형 사찰로, 신어산의 옛 이름인 은하산에서 유래 되었다고 전해진다. 은하사는 가락국 시조 수로왕의 왕후 허황옥의 오빠인 장유화상이 창건하였다고 전해지며, 원래는 서림사라 불렀다고 한다.
조선 중기 이전까지 있던 건물은 임진왜란 때 화재로 소실되었으며, 1629년(인조 7)에 대웅전을 중수한 이후 1649년(효종 1), 1801년(순조 1) 두 차례에 걸쳐 보수되었다. 경상남도 유형문화재로 지정된 은하사의 대웅전 수미단에는 허황옥과 관련 있는 것으로 추정되는 쌍어 문양이 있으며, 단층 맞배지붕의 다포계 양식으로 정면 3칸, 측면 3칸으로 구성되어 있다. 건축물의 정면과 측면의 길이가 비슷하여 정사각형의 형태를 취하고 있어 다른 건축물과 다른 특이한 형태이다. 외부 쇠서받침의 윗몸에는 연꽃을 조각하였고 내외 살미로는 용과 봉황새의 머리를 새겨 화려하게 장식하였다. 건물 내부는 불상 위에 9개의 보개(寶蓋)를 설치 하였으며, 삼세불, 나한상 등 32점의 벽화가 있는데 이 벽화는 경상남도 유형문화재로 지정되어 있다. 은하사는 영화 [달마야 놀자]의 촬영지로도 알려져 있다.',
        35.2658911471, 128.9130181738,
        '', '경상남도 김해시 신어산길 167 (삼방동)', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (421, '응봉산(서울)', '응봉산은 조선시대 매를 풀어놓고 사냥을 즐겼던 곳으로 매봉이라고도 불리는 곳이다. 이곳은 잠실의 롯데월드타워부터 동호대교까지의 한강 주변 파노라마 뷰를 조망할 수 있어 많은 사람들이 찾는 곳이다. 특히 밤이 되면 서울숲을 중심으로 동부 간선도로와 강변북로가 교차하여 만들어 낸 수많은 도로 램프 구간과 성수대교, 동호대교의 알록달록한 조명까지 아름다운 서울의 야경을 만나볼 수 있다. 봄이면 개나리가 산을 가득 메워 봄꽃 명소로도 유명하고 가을에는 선선한 바람과 함께 산책을 즐길 수 있다.',
        37.5471796096, 127.0281781551,
        '', '서울특별시 성동구 금호동4가 1540 (일대)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (422, '이성당', '일본 시마네현 이즈모시에 살다가 1906년 조선으로 건너온 히로세 야스타로라는 일본인이 [이즈모야]라는 이름으로 문을 열어 영업한 것이 시초이다. 1945년, 광복 이후 이즈모야가 사라진 자리에는 한국인 이석우 씨가 자리를 잡았다. 이성당이라는 명칭은 [이 씨 성을 가진 사람이 운영하는 빵집]이라는 의미라고 알려졌지만 연구자에 따라 조금 다른 견해도 존재하는데, [이 씨 성을 가진 사람이 번성하는 집]이라는 의미로 이성당이라는 명칭을 사용했다는 견해를 관련 연구에서 밝히기도 했다.
현재 공식 한자 표기는 이성당[이 씨 성을 가진 사람이 번성하는 집]이다. 단팥빵과 야채빵이 대표메뉴이다.

◎ 한류의 매력을 만나는 여행 정보
‘이성당’은 오랜 역사와 전통을 지닌 빵집으로 <나 혼자 산다>, <맛있는 녀석들> 등 다수의 TV 프로그램 촬영지로도 유명한 곳이다. 군산 현지인은 물론 여행객의 입맛까지 사로잡은 이 빵집의 대표 메뉴는 단팥빵과 야채빵이다. 변하지 않는 맛을 유지하는 ‘이성당’은 평일에도 주말에도 대기 줄이 늘어설 정도이다. 군산 여행을 계획 중이라면 한국에서 가장 오래된 빵집 ‘이성당’에서 미식 여행을 즐겨볼 것을 추천한다.',
        35.9870898531, 126.7111823722,
        '', '전북특별자치도 군산시 중앙로 177', 37, '063-445-2772', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (423, '이월드', '* 유럽식 도시공원, 이월드(구 우방타워랜드)    이월드는 1987년 10월 타워건립 및 종합테마파크 조성공사 재 착공을 시작으로 1993년 종합 테마파크 마스트플랜을 확정한 후 1995년 3월 개장한 폭포, 분수, 조명, 꽃으로 장식된 유럽식 도시공원으로 남녀노소 누구나 즐길 수 있는 놀이기구, 전시. 예술공간, 깔끔한 식당가 등이 마련되어 있다. 이월드에는 각 테마별로 광장을 만들어 방문객으로 하여금 새로움과 즐거움을 선사하고 있다. 매표소를 거쳐 들어서는 입구에 진입광장, 랜드의 중앙에 위치한 중앙광장, 어린이들의 놀이터 어린이광장, 젊은이들을 위한 공간이 영타운광장 등이 대표적인 광장이다.    * 83타워(구 우방타워)개괄 현황    83타워랜드에는 전망대, 디지털 체험관, 기획전시관 등의 교양시설과, 어린이 놀이시설, 오락실 등의 놀이시설이 조성되어 있다. 그리고 랜드 내에 회전레스토랑, 스카이라운지 등 5개의 식음시설이 들어서 있어 다양한 먹거리를 제공하고 있다. ',
        35.8548822436, 128.5648324581,
        '', '대구광역시 달서구 두류공원로 200 (두류동)', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (424, '이월드 83타워', '83 타워(대구타워)는 대구를 상징하는 하늘의 궁전이며 대구시내를 한눈에 볼 수 있는 전망대와 통신 홍보관, 과학관, 기획전시관이 있다. 또한 호텔식 서비스를 경험할 수 있는 3대 스테이크 하우스와 온 가족이 부담 없이 즐길 수 있는 푸드코드가 있다. 전망대에서 사계절 다른 풍경을 볼 수 있으며 엘리베이터로 올라가는 방법도 있지만 별빛로드를 따라 올라가 보면 아름다운 야경을 함께 즐길 수 있다. 건물 내에 다양한 포토존이 있어 소중한 사진을 남길 수 있다.',
        35.8532824570, 128.5665879034,
        'http://tong.visitkorea.or.kr/cms/resource/75/1575475_image2_1.jpg', '대구광역시 달서구 두류공원로 200 (두류동)', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (425, '이응노 미술관', '대전 이응노미술관은 이 지역이 낳은 세계적인 작가 고암 이응노(1904-1989) 화백의 예술 연구와 전시를 맡아 이 시대 고암 정신을 확장하고 계승할 목적으로 2007년 5월 개관하였다. 이응노의 삶과 예술 활동을 재조명하고, 그의 예술세계를 연구함으로써 한국 미술의 발전에 기여하고자 하는 뜻을 내포하고 있다. 대전광역시 서구 만년동 396번지에 있으며, 지하 1층, 지상 2층 건물이다. 박물관 건축설계는 프랑스 건축가 로랑 보두앵이 맡았다.
2007년 5월 3일 개관하였고 개관 기념전으로 ‘고암, 예술의 숲을 거닐다-파리에서 대전으로’를 열었다. 그동안 잘 알려지지 않았던 고암 선생의 작품인 릴리프(종이 부조), 판화, 은지화, 페인팅, 몽돌 등 이응노 작품의 다양성에 초점을 두고 개관 3주년 기획전이 벌어지고 있다. 고암의 작품세계가 동양과 서양의 만남이었던 것처럼 이응노미술관의 건축물은 한국작가 이응노와 프랑스 건축가 로랑 보드앵의 만남이 자아낸 조화라는 점도 매우 흥미로운 일이다.',
        36.3668637804, 127.3870118474,
        'http://tong.visitkorea.or.kr/cms/resource/29/2834129_image2_1.jpg', '대전광역시 서구 둔산대로 157 (만년동)', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (426, '이장우가옥', '이장우가옥은 500여평의 대지에 안채, 사랑채, 행랑채, 곳간채, 문간채로 구성된 근대 한옥이다. 상류층 팔작 기와지붕 가옥으로 광주 지역의 부호였던 정낙교의 아들 정병호가 1899년 안채와 1935년 문간채를 건축하였고, 동신대학교와 동신중·고등학교 등을 설립한 동강 이장우 박사가 1959년 매입 후, 사랑채, 행랑채, 곳간채까지 완성하여 현재의 모습을 갖추었다. 해당 건축물의 안채 상량문에 [광무 삼년 을해 이월 십일 축시(光武三年乙亥二月十日丑時)]로 기록된 점을 보아 근대화 시기인 1899년 건축된 것으로 추정된다. 안채는 120.12㎡(약 36평) 규모이며, 툇마루, 작은방, 대청, 큰방, 부엌, ㄱ자형으로 꺾인 작은방으로 구성된 독특한 구조이다. 남부지역 가옥들의 평면 형태가 ㅡ자형인 것과 달리 ㄱ자형인 점과, 부녀자의 활동을 편리하게 하고 가족의 공간을 확보한 근대 한옥의 특징을 갖추었다는 점을 인정받아, 안채가 1989년 광주광역시 민속문화재로 지정되었다. 곳간채는 한때 화재로 인해 소실되었다가 2009년 복원되었다. 마당에는 일본식 정원이 조성되어 있었으나, 2009년 광주 디자인비엔날레의 특별 전시 장소로 활용하면서 한국식 정원으로 재조성하였다. 이장우가옥은 원형이 잘 보존되어 있어 근대 초기 전통 상류 가옥의 모습을 살펴볼 수 있는 귀중한 문화유산이다.',
        35.1406171882, 126.9141331006,
        'http://tong.visitkorea.or.kr/cms/resource/60/3368360_image2_1.jpg', '광주광역시 남구 양촌길 21', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (427, '이천동 99계단 벽화마을', '99계단 벽화마을은 서울의 인사동 거리처럼 고미술 거리로 알려진 대구 남구 이천동 고미술 거리 건너편에 있다.
남구 이천동 수도산 자락에 나지막하면서 오래된 한옥이 밀집해 있는 산자락에 어둡고 칙칙한 시멘트 담장만 있던 골목 99계단에 벽화를 조성하여 밝은 모습의 벽화 골목으로 조성했기 때문에 더욱 의미가 있는 곳이다.
대구지하철 3호선 건들바위역(2번출구) 근처에 있어서 대중교통으로 찾아오기 편리한 곳이며, 벽화 그림은 대구고등학교 미술 동아리, 대구지역 고등학교 학생들이 2013년에 그렸다.
99세까지 88하게라는 타이틀의 99계단은 고전적인 풍경(소나무, 학, 가을 들판, 억새)부터 아이들이 좋아할 만한 그림(스누피, 원숭이, 토끼, 날개)까지 다양한 소재의 그림들로 구성 되어 있으며, 벽화를 감상하며 계단을 오르면 수도산 전통사찰인 서봉사에 닿으니 사찰에서  바라 볼 수 있는 탁 트인 전경은  99계단을 오른 후  얻는덤이다.서봉사 바로 아래에 위치한 카페 작은도서관에서 전망도 즐기고 차 한잔의 여유로 조용하고 한가한 시간을 가질 수도 있다.',
        35.8534218907, 128.5970157747,
        '', '대구광역시 남구 이천로29길 28-1 (이천동) 일대', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (428, '이천동고미술거리', '1960년대부터 형성된 문화재매매업소가 모여있는 곳으로 과거 우리선인들이 사용한 민속품,도자기,고가구를 많이 보유하고 있고 현대인에게 생소한 조상들의 생필품들이 가득하여 구경하는 재미가 쏠쏠하다. 외국인에게 인기가 많으며 특히, 일본인은 지도와 정보를 입수하여 찾아온다고 한다. 외국인에게 많은 사랑을 받으며 경외감을 주는 우리 고미술품에 우리나라사람들의 관심과 사랑이 어느때보다 필요하다. 고미술품에 관심이 있다면 꼭 한번 방문할 만한 관광지로 추천한다.',
        35.8553467018, 128.5985933601,
        '', '대구광역시 남구 명덕로 262', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (429, '이태원 관광특구', '이태원은 1997년에 서울시에서 최초로 지정된 관광특구로 2만 명 이상의 외국인이 거주하고 있는 다국적·다문화 지역이다. 1980년대 들어 각종 국제회의를 비롯해 86 아시안게임과 88 올림픽이 서울에서 개최되면서 세계적으로 그 이름이 알려지게 되어 관광객들의 명소가 되었다. 특히 가죽 제품과 독특한 디자인의 보세 의류, 소품들이 유명하며 외국인들의 쇼핑 투어에 꼭 포함되는 쇼핑과 유흥의 거리다. 구두, 의류, 가방 등을 파는 쇼핑 상가는 물론 호텔을 비롯한 숙박 시설, 각종 음식점, 유흥 오락 시설과 여행사 등의 상가 2,000여 개가 골목골목에 밀집해 있다. 해밀턴호텔부터 보광로 방면으로 100여 개의 가게가 몰려 있는 앤티크 가구 거리도 유명하다. 또한 외국인이 직접 운영하는 40여 개국의 음식점이 모여 조성된 세계음식거리에서는 이국 문화와 음식 체험이 가능하다. 1990년대 후반부터는 동남아시아와 중동 지역의 관광객이 늘어나면서 미군 중심의 거리에서 다국적 ‘퓨전’ 거리로 변모하고 있다. 매년 10월에는 다양한 공연과 행사가 열리며 특히 ‘이태원지구촌축제’가 유명하다. 미화와 엔화 등이 자유롭게 통용되며 영어와 일어, 중국어 등 외국어로도 상인과 쉽게 거래할 수 있다.',
        37.5343944032, 126.9958259074,
        '', '서울특별시 용산구 이태원로 196 (이태원동)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (430, '익산 미륵사지 [유네스코 세계유산]', '백제 최대의 가람인 미륵사 창건에 대해서는 삼국유사에 기록되어 있다. 신라 선화공주와 혼인한 후 왕이 된 마동 즉, 무왕(백제 30대 왕 600~641)이 선화공주와 함께 용화산(현재의 미륵산) 사자사의 지명법사를 찾아가던 중이었다. 그때 갑자기 연못 속에서 미륵삼존이 출현하여, 이를 계기로 미륵사를 창건하게 되었다. 삼존을 위하여 전(금당), 탑, 낭무(화랑)를 세웠다고 한다. 이와 달리 미륵사의 창건에는 무왕과 선화공주의 신앙만이 아니라 정치적 목적이 있었을 것이라는 견해도 있다. 즉 백제의 국력을 확장하기 위해 마한 세력의 중심이었던 이곳 금마에 미륵사를 세웠을 거라는 추측이다. 백제 최대의 가람인 미륵사를 세우는 데에는 당시 백제의 건축·공예 등 각종 문화 수준이 최고도로 발휘됐을 것으로 짐작할 뿐만 아니라, 신라 진평왕이 백공을 보내 도와주었다는 삼국유사의 기록에서 알 수 있는 바와 같이 당시 삼국의 기술이 집결되었을 것이다. 미륵사가 백제불교에서 미륵신앙의 구심점이었음은 분명하며, 신라최대의 가람인 황룡사가 화엄사상의 구심점이었던 것과 대비된다. 황룡사가 1 탑 3 금당식인 것과 달리 미륵사는 3 탑 3 금당식 가람배치이다. 황룡사는 왕을 정점으로 하는 화엄사상, 미륵사는 미륵사상을 가람에 구현하고 있다. 미륵사는 일반평민 대중까지 용화세상으로 인도하겠다는 미륵신앙이 바탕을 이루고 있다.

* 미륵사지의 또 다른 유물, 미륵사지 석탑과 당간지주 *
미륵사지에는 두 가지의 유물이 있어 볼거리를 더한다. 그 하나는 미륵사지 석탑이고, 다른 하나는 미륵사지 당간지주이다. 미륵사지 석탑은 국보로 현재 높이 14.24m이고 우리나라 최고, 최대의 석탑이다. 석탑은 거의 전면이 붕괴되어 동북면 한 귀퉁이의 6층까지만 남아있으나 본래는 9층으로 추정된다. 미륵사지 석탑은 1974~1975년 원광대학교 마한백제문화연구소 조사에 의하여 동쪽탑은 목탑이 아니라 석탑임이 밝혀져 이른바 동서 쌍탑의 배치였음을 알게 되었다. 동탑은 그 뒤 발굴조사에 의해서 9층으로 확인되어 1993년에 9층(높이 27.67m)으로 복원되었다. 이 석탑을 우리나라 최고의 석탑으로 보고 있는 이유는 이 탑의 양식이 그 이전에 성행하였던 목탑의 각부 양식을 나무 대신 돌로써 충실하게 재현하였기 때문이다. 당간지주란 당간을 지탱하기 위하여 당간의 좌·우에 세운 기둥, 즉 지주를 말한다. 미륵사지 당간지주는 보물로, 높이 395cm이며 통일신라시대 중기 이전에 조성된 것으로 추정된다. 미륵사지 석탑 남쪽에 2개의 지주가 동서로 약 90m 간격을 두고 동서로 마주보고 있다.',
        36.0125952250, 127.0308309191,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=59eff19f-0e55-4345-9f18-69c8d31f2a9a', '전북특별자치도 익산시 금마면 미륵사지로 362', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (431, '익산 왕궁리유적 [유네스코 세계유산]', '전북특별자치도 익산시 왕궁면에 위치한 왕궁리유적은 백제 30대 무왕(600 ~ 641)대에 조성되어 그 후 사찰로 변화했다. 백제 말기 익산 경영과정 정치, 경제, 문화의 중심지인 왕궁으로 사비(부여)와 함께 복도로 이해하고 있다. 백제 왕궁은 용화산에서 발원한 능선 끝자락의 낮은 구릉 위에 조성되었다. 1989년부터 전면적인 발굴조사를 통해 확인된 내용에 의하면 백제 말기 왕궁으로 조성되어 일정기간 시용된 후 왕궁의 중요 건물을 철거하고 탑과 금당, 강당 등 사찰이 들어선 복합유적이다.

왕궁리유적의 왕궁은 백제왕궁으로서는 처음으로 왕궁의 외곽 담장과 내부구조가 확인되어 체계적이고 계획적으로 조성된 백제왕궁의 구조를 확인할 수 있는 유일한 유적이다. 왕궁의 외곽에는 폭 3m의 담장을 동서 245m, 남북 490m인 장방형으로 두르고 있다. 왕궁의 남측 절반은 국가의 중요 의례나 의식을 행하던 건물, 왕이 정사를 돌보던 건물, 왕과 왕의 가족의 생활을 위한 건물들이 4개의 동서석축을 쌓아 구분·배치하였다. 북측 절반은 왕의 휴식을 위한 공간인 정원과 후원, 왕궁의 서북측에는 백제시대 가장 귀중품인 금과 유리를 생산하던 공방지가 위치하고 있다. 이와 같이 왕궁의 남측에 의례나 의식, 정무, 생활을 위한 공간을 배치하고, 북쪽에 후원을 배치하는 것은 고대 중국이나 일본 왕궁에서도 확인되고 있어서 당시 고대 동아시아 국가에 문화교류 사실을 확인할 수 있다. 왕궁으로 일정 기간 사용한 후 사찰로 바뀌어 통일신라 후기까지 유지되었으며, 고려시대에는 사역 동남측 일부에서는 유적이 확인되고 있다.

(출처 : 백제왕궁박물관)',
        35.9716308843, 127.0557381055,
        '', '전북특별자치도 익산시 왕궁면 궁성로 666', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (432, '익산교도소세트장', '익산시 성당면 와초리 성당초등학교 남성분교 폐교부지 위에 세워진 국내 유일의 영화 촬영용 교도소 세트장이다. 이곳에서 영화 ‘홀리데이’의 현실감 넘치는 교도소 장면이 연출되었고, 이외에도 ‘아이리스’,‘전설의 마녀’, ‘7번 방의 선물’,‘내부자들’ 등 200여 편의 영화와 드라마가 촬영되었다. 교도소 세트장에 들어가면 ‘영화가 탄생하는 마술상자’라는 말을 증명이라도 하듯 입구에는 그동안 촬영된 영화 포스터들을 전시하고 있다.
세트장은 1층과 2층으로 이루어져 있고, 건물 내부와 외벽에는 교화에 필요한 문구들이 여기저기 붙어 있다. 국내 유일의 영화촬영용 교도소인 이곳은 새로운 관광명소로 각광받고 있으며, 운동장에 깔린 천연잔디는 관광객들의 휴식공간과 포토존으로 인기가 많다.

◎ 한류의 매력을 만나는 여행 정보
한국에서 유일한 영화 촬영용 교도소 세트장으로 드라마 <이로운 사기>, <신성한 이혼>, 영화 <타짜>, <7번 방의 선물> 등을 촬영했다. 폐교를 활용해 만든 세트장이지만 법정, 독방, 취조실, 고문실 등에서 사진을 찍다 보면 오싹해진다. 의상을 대여해 교도관이나 죄수가 되어 보면 더 실감 나게 교도소 체험을 할 수 있다.',
        36.0695231043, 126.9306828160,
        'http://tong.visitkorea.or.kr/cms/resource/33/2643533_image2_1.jpg', '전북특별자치도 익산시 성당면 함낭로 207', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (433, '인동촌 아나고골목', '인동촌 아나고골목은 달성공원 담벼락에서 북비산네거리 쪽으로 150m에 이른다. 1973년부터 아나고 가게들이 자리 잡기 시작하여 지금은 ‘인동촌 아나고 묵자골목’으로 불리고 있다고 한다. 대구에서 맛있는 음식점들이 모여 있는 곳으로 알려져 있다. 현재 20여 개의 식당이 새벽까지 손님맞이에 여념이 없다. 아나고는 회로 먹어도 좋고, 구워 먹어도 그 맛이 일품이다. 술안주로도 제격이며, 양념장에 버무려 시원한 물김치와 함께 먹기도 한다. 단백질과 아미노산이 풍부해 여름철 기력회복에 좋다.',
        35.8768216663, 128.5778612559,
        'http://tong.visitkorea.or.kr/cms/resource/40/3377540_image2_1.jpg', '대구광역시 서구 북비산로74길 49 (비산동)', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (434, '인사동', '인사동은 도심 속에서 낡지만 귀중한 전통의 물건들이 교류되는 소중한 공간이다. 인사동에는 큰 대로를 중심으로 사이사이 골목들이 미로처럼 얽혀 있다. 이 미로 속에는 화랑, 전통공예점, 고미술점, 전통찻집, 전통음식점, 카페 등이 밀집되어 있다. 특히 인사동의 가게들은 저마다의 독특한 멋으로 젊은이들은 물론 중년층에게까지 매우 인기가 많다. 그 중에서도 화랑은 인사동의 맥을 이어온 중심이다. 이 곳에는 100여 개의 화랑이 밀집되어 있는데 한국화에서 판화, 조각전까지 다양한 전시회를 감상할 수 있다. 대표적인 화랑으로는 민중미술의 중심역할을 했던 학고재, 재능있는 작가들의 터전이 되었던 가나화랑, 가나아트센터 등이 있다. 만약 인사동의 유명한 화랑만을 골라서 편하게 구경하고 싶다면 미술관 순화버스 를 이용해보자. 저렴한 가격으로 유명 화랑 10여곳을 둘러볼 수 있다.   화랑과 함께 인사동 거리를 메우고 있는 것은 바로 전통 찻집과 음식점이다. 처음에는 찾기가 힘들지만, 산책하듯이 천천히 골목골목을 둘러보면 매우 재미있다. 그 중에서 대표적인 명소를 꼽는다면 전통찻집 귀천이 있다. 귀천(歸天)은 고 천상병(千祥炳) 시인의 부인이 운영하는 전통찻집으로 내부는 매우 작지만 당시 시인의 책과 사진을 접할 수 있는 곳이다. 이곳은 모과차가 가장 향기가 좋아 인기인데, 지금도 가끔 유명 인사들이 다녀가는 곳이다.인사동은 매주 토/일요일마다 차 없는 거리로 지정되어 시민과 함께 하는 문화공간으로 다시 태어났다. 이날은 기존 가게는 물론 거리에는 전통공연과 전시는 물론 엿장수, 사주와 궁합을 보는 할아버지가 함께 한다. 이 속에서 즐거움을 찾는 이들은 바로 각국에서 몰려든 외국인들이다. 한국의 전통문화를 보기 위해 찾아든 외국인 관광객들은 전통가게에서 고물건을 보거나 고미술을 산다. 그리고 거리에 있는 엿을 사고 전통 파전을 먹는다. 도심에서 느끼는 전통의 멋에 빠진 외국인들의 모습도 많이 볼 수 있다. ',
        37.5754310780, 126.9833758804,
        'http://tong.visitkorea.or.kr/cms/resource/26/2612526_image2_1.jpg', '서울특별시 종로구 인사동길 62 (관훈동) 일대', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (435, '인왕사(서울)', '인왕사(仁王寺)는 서울시 종로구 무악동 인왕산 자락에 자리한 전통사찰이다.  성곽을 따라 이어진 길은 소나무 숲 사이로 간간이 빌딩숲이 내려다 보이곤 하더니 곧 도로를 만나 속세로 이어진다. 성곽에 기대서 바라보는 인왕산의 모습은 옛 정선의 인왕제색도처럼 정겹다. 서울 중심에서 가장 가깝고 손쉽게 오를 수 있는 산. 그 중턱에 수많은 기자신앙과 토속신앙의 상징이 어우러진 선바위와 호국도량 인왕사가 있다. 풍수상으로 조산인 북한산에서 주산인 북안산에 연결되고 낙산이 좌청룡이며, 인왕산이 우백호가 되는 산. 동쪽 기슭은 아늑하고 풍치가 빼어나 장안 제일의 명승지가 되며, 북쪽은 무계동(武溪洞)이라 불리워 중국의 무릉도원에 버금갈 정도로 아름다운 경치를 자랑하는 곳이다. 인왕산 호랑이하면 서울 시민 중 모르는 이들이 없으며, 서울을 한눈에 조망할 수 있는 곳 하면 모르는 이 없는 산이다.    조선 초기 태조가 경복궁을 수호하는 호국도량으로 창건한 인왕사는 많은 서울 시민들이 즐겨 찾는 서울 사대문 안에 자리한 도량이었다. 인왕사에 올라 선바위에서 내려다 보는 서울의 조망은 마치 레고를 쌓아 놓은 조형물의 전시장 같지만, 멀리 우뚝 선 보현봉을 중심으로 서쪽으로 이어지는 비봉능선의 선경들은 마치 먼 옛날의 조선과 현재의 서울이 교차하는 타임머신을 탄 듯한 느낌을 갖게 한다. 이처럼 누구나 쉽게 오를수 있고 도심에서 쉽게 찾을 수 있는 서울의 명산에 자리한 인왕사는 선바위ㆍ국사당으로 불리는 무불습합의 신앙지와 함께 특이한 가람을 구성하고있다. 5개종단의 11개 암자가 하나의 인왕사라는 하나의 명칭 아래 공존하는 곳. 처음 사찰을 찾는 이들은 여느 사찰에서 느낄 수 없는 특이한 공간에 어색함을 느끼지만, 선바위로 오르는 여정에 다양한 암자들의 독특한 모습과 부처님들을 친견하는 묘미가 있는 곳이기도 하다.자그마한 근심이나 먼 옛날 호국도량의 의지를 맛 보고 싶을 때 언제나 오를 수 있는곳. 그 곳 인왕산에 먼 옛날 솟아 오른 영험한 선바위와 한양을 지키며 자리한 인왕사가 500여 년에도 그랬듯이 현재도 여전히 경복궁 수호하며 그 자리를 지키고 있다. ',
        37.5786358368, 126.9597168346,
        'http://tong.visitkorea.or.kr/cms/resource/01/1568101_image2_1.jpg', '서울특별시 종로구 통일로18가길 16-1 (무악동)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (436, '인제나르샤파크(구. 인제 밀리터리 테마파크)', '사이버 게임 서든어택을 실제 서바이벌 게임으로 구현한 서든어택 얼라이브 경기장은 2008년 인제 서든어택 얼라이브 대회를 처음 개최하면서 모험레포츠의 새 지평을 열었다. 모험레포츠 시설과 다양한 즐길거리가 가득한 인제 밀리터리테마파크가 국내 최초로 50m 높이의 줄 없는 번지점프 타워인 스캐드 다이빙 등을 추가하고 인제나르샤파크로 새롭게 단장하였다.',
        38.0605604607, 128.1678565439,
        '', '강원특별자치도 인제군 인제읍 비봉로44번길 81', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (437, '인천 차이나타운', '인천 차이나타운은 1883년 인천항이 개항되고 1884년 제물포지역이 청나라의 치외법권 (治外法權, extraterritoriality) 지역으로 지정되면서 형성된 곳이다. 120년이 넘는 역사 동안 화교 고유의 문화와 풍습을 간직하고 있는 곳으로, 붉은색으로 치장된 골목 구석구석을 둘러볼 때마다 마치 중국으로 여행을 온듯한 기분이다. 과거에는 중국에서 수입된 물품들을 파는 상점들이 대부분이었으나, 현재는 중국 음식점들이 많다. 현재 이 거리를 지키고 있는 한국 내 거주 중국인들은 초기 정착민들의 2세나 3세가 이어 운영하고 있기 때문에 1세들이 지키고 있던 전통문화를 많이 지키지 못하고 있지만 중국의 맛만은 고수하고 있다. 남녀노소 사랑하는 짜장면이 탄생된 곳이기도 하며, 하얀 짜장과 양꼬치, 공갈빵, 화덕만두 등 다양한 먹거리들이 많다.',
        37.4738502955, 126.6191619441,
        '', '인천광역시 중구 차이나타운로59번길 20', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (438, '인천내동성공회성당', '영국 해군 종군 신부 고요한 주교 등 선교 동역자 6명이 1890년 9월 29일 인천항에 첫발을 내디뎌 선교활동을 시작하였으며, 초대 성당 건물은 1891년 9월 30일 성당을 준공하였다. 1902년에는 한때 러시아 영사관으로, 1904년에는 성공회 신학원으로 1956년까지 운영되었으며, 당초 건물은 6.25 전쟁 시 소실되었으나 1956년 6월 23일 복원하여 오늘에 이르고 있다. 건물 형태는 지붕의 목조 트러스를 제외하고, 외벽과 주요 부재는 화강암으로 견고하게 쌓아 올린 중세풍의 석조이나 한국의 전통적인 목구조 처마 양식을 가미하였으며 창호 및 벽체 부분의 처리가 뛰어나다.',
        37.4739837596, 126.6274500730,
        'http://tong.visitkorea.or.kr/cms/resource/19/3351619_image2_1.jpg', '인천광역시 중구 개항로45번길 21-32', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (439, '인천도시역사관', '인천은 1883년 개항 후 우리나라 최초의 계획도시로 시작하여 130년이 넘는 시간이 흐르는 동안 다양한 변화와 확장을 거쳐 지금은 전국 8대 광역시 중 가장 넓은 면적에 300만 인구의 무한한 잠재력을 가진 도시로 성장해 왔다.
개항기 근대도시로 시작해 현재의 국제적인 도시로 발전한 인천이라는 도시의 역사와 변화 과정을 소개하는 전시관이다. 1층의 근대도시관, 2층의 인천모형관, 3층의 IFEZ(인천경제자유구역)모형관으로 구성되어 있다. 3개 상설전시실 외에도 2층 기획전시실 아암홀과 소암홀에서 시기별로 흥미로운 특별 전시를 진행하고 1층 로비에는 클래식 자동차를 전시한다.
인천도시역사관은 송도국제도시 트레이드마크인 트라이보울과 송도 센트럴파크를 함께 여행하기 편리한 위치이고 인천1호선 지하철역(센트럴파크역)과 바로 인접해 접근성도 좋다.',
        37.3928266701, 126.6358737757,
        '', '인천광역시 연수구 인천타워대로 238', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (440, '인흥마을', '남평 문 씨의 중시조로 알려진 삼우당(三憂堂) 문익점(文益漸)의 후손이 대구에 입향한 것은 대체로 500년 전으로 전해지고 있으며 이곳 인흥에 자리를 잡은 것은 그의 18세손인 인산재(仁山齋) 문경호(文敬鎬, 1812~1874) 때이다. 그는 문 씨만의 마을을 만들 것을 계획하고 원래 고려의 대찰(大刹)인 인흥사가 있었던 자리에 터를 잡아 오늘의 인흥마을을 새로 열었다고 한다. 세거지에 최초로 건립한 건물은 1820년을 전후해서 지은 재실 용호재(龍湖齋)로 현재 광거당이 있는 곳이다. 저택이 들어선 것은 1800년대 후반으로 처음에는 초가로 시작했으나 그 후 100년에 걸쳐 지금의 세거지가 형성되었다. 현재 70여 채의 기와집이 한울 안에 정연히 들어서 있는 이 마을은 건축연대가 200년 미만이지만 전통적인 영남 지방 양반 가옥의 틀을 지키고 있으며 세거지 구성과 주위 경관의 조화는 어느 곳에서도 흔히 볼 수 없는 마을이라 할 수 있다.',
        35.7932244734, 128.5181077268,
        '', '대구광역시 달성군 화원읍 인흥3길 16', 4, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (441, '일번지식당', '전라남도 영광군 법성면에 위치한 일번지식당은 영광의 대표특산물인 굴비를 재료로한 한정식집이다. 다양한 가격대의 한정식이 준비되어있으며, 다양한 종류의 반찬과 음식들이 한상 가득 차려진다.',
        35.3596651197, 126.4468107373,
        '', '전라남도 영광군 법성면 굴비로 37', 38, '061-356-2268', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (442, '자규루 및 관풍헌', '자규루는 단종(재위 1452∼1455)이 세조(재위 1455∼1468)에게 왕위를 빼앗기고 유배되었을 때 잠시 지내던 누각이다. 단종은 이 누각에 자주 올라가 자규시를 지었다고 한다. 자규란 피를 토하면서 구슬피 운다고 하는 소쩍새를 가리키는 말로 자신의 처지를 견주어 지은 것이다. 원래는 세종 10년(1428) 군수 신권근에 의해 지어져 [매죽루]라 불리웠으나 단종의 자규시가 너무 슬퍼 누각 이름을 매죽루에서 자규루로 바꿨다고 한다. 그 후 많이 퇴락해 민가가 들어섰는데, 정조 15년(1791) 강원도 관찰사 윤사국이 그 터를 찾아 복원하였다. 앞면 3칸, 옆면 2칸 규모의 2층 누각이며, 팔작지붕집으로 되어 있다. 자규루에서 약 50m 떨어진 곳에는 관풍헌이라는 건물이 있는데, 이곳은 조선시대 지방의 업무를 처리하던 관청 건물로, 영월 객사의 동헌 건물이다.
단종은 본래 청령포에 유배되어 있었는데, 마침 홍수가 나서 이곳으로 옮겨와 생활을 하게 되었다.  그 즈음 단종 복위 운동이 계속해서 일어나자 세조는 결국 단종을 죽이고자 했다. 세조 3년(1457)  단종은 금부도사 왕방연이 가지고 온 사약을 먹고 공생 화득에게 목이 졸려 죽임을 당하게 되었다.
단종이 비참하게 죽은 역사의 현장이 바로 이 관풍헌 앞마당이다. 현재는 보덕사 소유로 되어 있으며 포교당으로 이용되고 있다. 자규루와 관풍헌은 강원도의 유형문화재 제26호로 지정되어 있다.',
        37.1836427012, 128.4705055391,
        '', '강원특별자치도 영월군 영월읍 중앙로 61', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (443, '자유공원(인천)', '자유공원은 인천항 개항 5년 만에 만들어진 우리나라 최초의 서구식 공원이다. 지대가 높은 데다 터가 넓고 숲이 울창해 산책하기 알맞다. 정상엔 한미수교 백주년기념탑이 있다. 1882년 4월 우리나라와 미국 사이에 조인된 한미수호 통상조약체결을 기념하기 위해 100주년이 되는 1982년에 세운 것이다. 한국전쟁 당시 인천상륙작전을 성공시킨 맥아더장군의 전공을 기리는 맥아더장군 동상도 그 옆에 있다. 인천상륙작전 성공 이후 7주년이 되는 1957년 9월 15일에 완공됐다. 자유공원 정상에서는 멀리 인천 앞바다까지도 훤히 내려다보인다. 자유공원 안에는 소규모 동물원과 팔각정, 연오정, 의자 등 쉼터가 마련되어 있다. 매년 4월이면 자유공원으로 오르는 길은 벚꽃으로 만발한다. 이를 기념해 벚꽃축제가 열린다. 공원 정상에서 인천항과 월미도를 바라보는 맛도 그만이다. 늦은 밤 이곳에서 바라보는 인천항의 밤 경치는 연인들의 데이트코스로도 유명하다.',
        37.4752946269, 126.6222755091,
        'http://tong.visitkorea.or.kr/cms/resource/84/3396084_image2_1.JPG', '인천광역시 중구 제물량로232번길 46', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (444, '자유의 다리(파주)', '임진각 광장 앞 망배단 뒤편에 놓인 다리로, 1953년 한국전쟁 포로 1만 2,773명이 이 다리를 건너 귀환하였기 때문에 ‘자유의 다리’라고 명명되었다. 원래 경의선 철교는 상·하행 2개의 다리가 있었으나 폭격으로 파괴되어 다리의 기둥만 남아 있었는데 전쟁 포로들을 통과시키기 위하여 서쪽 다리 기둥 위에 철교를 복구하고 그 남쪽 끝에 이 임시다리를 설치했다. 당시에는 포로들이 차량으로 경의선 철교까지 와서는 걸어서 이 다리를 건너왔다고 한다.
자유의 다리는 길이 83m, 폭 4.5m, 높이 8m 내외이다. 다리는 나무를 짜 맞추어 만들었는데 특히, 힘을 많이 받는 부분은 철재를 혼합하여 사용하였다. 임시로 설치한 다리이므로 건축적으로 뛰어난 점은 없으나 ‘자유로의 귀환’이라는 상징적 의미를 담고 있다는 점에서 한국전쟁의 대표적인 유산이라 할 수 있다.',
        37.8899150642, 126.7389615126,
        'http://tong.visitkorea.or.kr/cms/resource/75/3058375_image2_1.jpg', '경기도 파주시 문산읍 임진각로 148-53', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (445, '장동산림욕장', '장동산림욕장은 대전 대덕구 계족산에 위치한 삼림욕장이다. 계족산의 자연 산림을 그대로 활용하여 1995년에 개장한 이곳은 대전 시민들과 관광객이 쉽게 찾을 수 있는 휴식공간으로 삼림욕을 통해 계족산의 맑은 공기와 피톤치드를 마실 수 있다. 계족산성 아래 숲 골짜기에 있으며, 체육시설, 등산순환로, 모험놀이 시설 등이 있어 시민들의 휴식공간으로 이용된다. 체육, 모험, 놀이시설 20여 종과 등산순환로, 물놀이장, 잔디광장 등의 기타 편의시설 등이 구비되어 있고 계족산 황톳길의 시작점 역할도 한다. 삼림욕장에서 시작되는 계족산 황톳길에서는 맨발 걷기 체험을 할 수 있는 곳으로, 자연이 주는 건강을 경험할 수 있다. 인근 관광지로는 계족산, 계족산성 외에 산디마을 캠핑장, 회덕 메타세쿼이아길 등이 있다.',
        36.4055714493, 127.4425308280,
        'http://tong.visitkorea.or.kr/cms/resource/44/3033644_image2_1.jpg', '대전광역시 대덕구 산디로 79-70', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (446, '장릉보리밥집', '영월장릉보리밥집은 영월읍에 위치한 곳으로, TV 프로그램에도 소개된 강원도의 전통 음식을 즐길 수 있는 맛집이다. 보리밥, 감자 메밀부침, 묵채, 도토리묵 등 인근에서 나는 먹거리로 만든 강원도 전통 음식을 메뉴로 하고 있다. 60년 이상의 전통을 가지고 있는 노포로 대를 이어 운영하고 있으며, 가정집을 개조하여 외관부터 편안하고 고풍스러운 멋을 간직하고 있다.
장릉보리밥집의 특징은 감자가 들어간 보리밥과 들기름이 듬뿍 들어간 묵채로 보리밥은 감자와 함께 밥솥에서 익혀서 쫄깃하고 고소한 맛이 나고, 묵채는 들기름과 양념을 섞어서 색다른 맛을 낸다. 이 밖에도 인근에서 채취한 나물이나 약초를 포장 판매한다.',
        37.1955966127, 128.4563147919,
        '', '강원특별자치도 영월군 영월읍 단종로 178-10', 32, '033-374-3986', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (447, '장생포 고래문화마을', '2015년 조성된 고래문화마을에서는 예전 장생포 고래잡이 어촌의 모습을 그대로 재현하였으며, 고래광장, 장생포 옛 마을, 선사시대 고래마당, 고래조각 정원, 수생 식물원 등 다양한 테마와 이야기를 담은 공원을 둘러볼 수 있다. 실물크기의 고래를 형상화하여 다양한 고래를 경험하고 학습할 수 있는 이색적인 체험공간뿐만 아니라 고래와 관련된 교감, 공존 등 다양한 테마를 다루고 있는 스토리텔링 포토존, 반구대 암각화와 고래잡이 벽화 등 선사시대 고래역사 문화를 체험할 수 있는 야외 학습 공간 등 다양한 학습과 놀이가 가능한 공간이다.',
        35.5059840591, 129.3831649569,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=a0474e7c-d774-4928-bf39-df3f524a6fae', '울산광역시 남구 장생포고래로 271-1', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (448, '장생포 고래박물관', '옛 고래잡이 전진기지였던 장생포에 국내 유일의 고래박물관을 2005년 5월에 개관을 하였다. 1986년 포경이 금지된 이래 사라져 가는 포경유물을 수집, 보존, 전시하고 고래와 관련된 각종 정보를 제공함으로써 해양생태계 및 교육연구 체험공간을 제공한다. 전시실부터 고래탐험실, 고래연구실까지 다양한 볼거리와 체험을 제공하고 있다.',
        35.5027954846, 129.3808374721,
        '', '울산광역시 남구 장생포고래로 244 (매암동)', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (449, '장원식당', '맛의 고향 전라남도 순천에 위치한 장원식당은 선암사를 대표하는 오래된 맛집이다. 선암사 앞에는 많은 식당들이 모여 있는데 그 중 하나로 선암사가 관광단지로 조성되기 전부터 선암사와 함께 하였다. 메뉴로는 다채로운 나물들과 깻잎, 고추장아찌, 꼬막 등 25여가지의 맛깔스러운 반찬들과 함께하는 산채정식과 토종닭요리, 간단하면서 든든하게 하는 산채비빔밥 등을 즐길 수 있다. 특히, 장원식당은 모든 장을 손수 만들어 깊은 장 맛을 느낄 수 있는 곳이다.',
        34.9916635038, 127.3432629022,
        '', '전라남도 순천시 승주읍 승암교길 15', 38, '061-754-6362', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (450, '장태산자연휴양림', '장태산 자연휴양림은 국내 유일의 메타세쿼이아 숲이 울창하게 형성되어 있어 이국적인 경관과 더불어 가족 단위 산림욕을 즐기기에 적합한 곳이다. 천혜의 자연경관과 잘 어우러진 이곳은 산책길이 잘 조성되어 있을 뿐만 아니라 건강지압로, 추억의 놀이, 출렁다리, 교과서 식물원, 생태연못, 숲 속 교실 등을 갖추고 있어 자연과 더불어 자연학습과 휴식을 동시에 즐길 수 있다.
또한 단체오락, 미니축구, 농구 경기 등을 즐길 수 있는 소규모(380㎡) 운동공간과  스카이웨이, 전망대, 비탈 놀이 시설을 통해 메타세쿼이아 숲을 특별히 체험할 수 있는 공간도 마련되어 있으며 장태산 정상의 형제바위 위에 있는 전망대에서는 낙조를 바라볼 수 있고 장군봉, 행상바위 등 기암괴석도 볼 수 있다.',
        36.2159580005, 127.3412755693,
        'http://tong.visitkorea.or.kr/cms/resource/12/3320712_image2_1.jpg', '대전광역시 서구 장안로 461', 3, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (451, '장포항', '장포항은 남해군 창선면 진동리에 속한다. 창선면은 본래 창선도라 불리는 작은 섬이다. 지금은 연륙교 덕분으로 교통이 편리한 섬 아닌 섬이 되었다. 남해군 지족마을과는 1980년에 개통한 창선교로 이어지며, 북동쪽으로는 2003년 삼천포대교와 늑도대교, 창선대교로 육지와 연결되었다. 장포항은 창선도에서 가장 동쪽에 위치한 방파제 길이 약 300m의 작은 항구다. 소형 낚싯배만이 고요한 장포항에 오고 간다. 빨간 등대와 하얀 등대, 항구 주변 갯바위는 잘 알려진 장포항권 낚시 포인트다. 장포항 주변 갯바위에선 감성돔, 볼락, 열기 등이 잘 잡혀 주말이면 낚시꾼을 갯바위 포인트로 날라주는 어선들로 항구는 붐빈다, 항구 주변은 언제나 깨끗한 편인데, 마을 주민들이 관리하기 때문이다. 항구 뒤편 언덕에 골프장이 운영되고 있으며, 이곳 옆으로 2km쯤 걸으면 모상개해수욕장에 닿을 수 있다.',
        34.8442759456, 128.0663227446,
        'http://tong.visitkorea.or.kr/cms/resource/95/2787795_image2_1.jpg', '경상남도 남해군 창선면 흥선로1505번길 7-25', 36, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (452, '장항송림산림욕장 (장항 솔숲)', '장항 솔숲은 하늘을 가린 울창한 소나무숲이 해안을 따라 이어져 아무런 방해도 받지 않고 고즈넉한 산책을 즐길 수 있다. 마른 솔잎을 밟으며 천천히 걷는 느낌은 장항 솔숲에서만 맛볼 수 있는 묘미다. 솔숲 사이에 벤치와 원두막이 있어 잠시 앉아 쉬거나 가족과 둘러앉아 간식을 먹기에도 좋다. 숲과 바다 사이 백사장은 자동차가 다닐 수 있을 정도로 단단해 바다 풍경을 감상하며 걷기에 그만이다. 눈을 어지럽게 하는 식당이나 위락시설, 방파제도 없이 오로지 바다와 백사장만 존재하는 해안 풍경이 여행자에게 힐링의 시간을 선물한다. 소나무가 뿜어내는 향기에 몸과 마음을 씻고 사랑하는 가족과 대화를 나누며 오래 머물기 좋다. 또한 높이 15m, 길이 250m의 스카이워크에서 서천 바다의 풍경과 노을을 감상할 수 있으며, 세계자연유산인 서천갯벌에서 진행되는 ‘갯벌·바다 덕분에’ 체험 프로그램도 인기이다.',
        36.0184827112, 126.6655745301,
        '', '충청남도 서천군 장항읍 장항산단로34번길 122-16', 34, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (453, '재미난 박물관', '재미난 박물관은 이름처럼 재미가 가득한 아이들을 위한 박물관이다. 신기한 전시물을 아이들이 직접 만지고 체험할 수 있다. 빛·소리·움직임, 생활·날씨·우주, 인터랙티브 로봇 체험, 수학적 도구로의 체험, 감성을 키워주는 체험, 빅 버블 체험, 세계 민속타악기 체험을 할 수 있으며 다양한 모양과 크기의 비눗방울을 만들고 빅 버블 안에 들어가는 체험은 아이들이 특히 좋아한다. 재미난 박물관에서 이런저런 물건을 만지고 놀다 보면 시간이 훌쩍 지나가며 재미있게 놀았을 뿐인데 과학, 수학, 물리적 원리도 배우게 되는 신기한 공간이다.',
        37.4731615167, 126.6208756957,
        'http://tong.visitkorea.or.kr/cms/resource/89/3309189_image2_1.jpg', '인천광역시 중구 신포로23번길 83 (중앙동1가)', 2, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (454, '재인폭포 (한탄강 유네스코 세계지질공원)', '재인폭포는 오래전부터 명승지로서 널리 알려져 있다. 재인폭포는 북쪽에 있는 지장봉에서 흘러 내려온 작은 하천이 높이 약 18m에 달하는 현무암 주상절리 절벽으로 쏟아지는 것이 그야말로 장관을 이룬다. 또한, 재인폭포 주변에는 천연기념물 어름치와 멸종위기종인 분홍장구채 등의 서식지로도 알려져 있으며 폭포의 이름과 관련된 아름다운 사랑이야기도 전해오고 있다. 재인폭포에서는 다양한 현무암의 특징들을 관찰할 수 있는데, 대표적으로 주상절리를 비롯하여 하식동굴과 포트홀, 가스튜브 등을 볼 수 있다. 높이 약 18m에 달하는 폭포는 계속해서 폭포 아래를 침식시켜서 수심 5m에 달하는 포트홀을 만들었다. 포트홀이란 하천에서 암석의 오목한 곳이나 깨진 곳에 와류(물이 회오리 치는 현상)가 발생하여 깊은 구멍이 생겨난 것을 말한다.
<출처 : 한탄강지질공원>',
        38.0772227105, 127.1427246484,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=964323aa-a492-4f8a-b832-a871b2212a4d', '경기도 연천군 연천읍 부곡리 192', 31, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (455, '전사벌왕릉', '전사벌왕릉(傳沙伐王陵)은 경상북도 상주시 사벌국면 화달리에 있는 무덤이다. 상주 화달리 삼층석탑(보물) 동북쪽에 있으며, 왕릉이라고 전해지지만 정사에 기록이 없어 누구의 묘인지 정확히 알 수는 없다. <신증동국여지승람>과 <상주군읍지>에 의해 신라 54대 경명왕의 다섯 번째 왕자 박언창의 묘로 추정한다. 박언창은 사벌주의 대군으로 책봉되었으나 후에 이곳을 사벌국이라 칭하고 자립왕으로서 11년간 다스리다가 견훤의 침공으로 패망하고 이곳에 묻혔다고 전해진다.
삼층석탑 옆에 왕릉에 속한 신도비가 세워져 있으며 석탑의 서북 편에는 상산 박씨 문중에서 건립한 재실이 있다. 현재 능 앞에 세워져 있는 석물들은 오래된 것이 아니며 신도비는 1954년에 세워진 것이다. 상주시가 1981년 12월 능역을 확장하고 왕릉, 영사각 등 주변을 보수하고 정화하였다.',
        36.4448477833, 128.2239239046,
        '', '경상북도 상주시 사벌국면 화달리 산45', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (456, '전설의 쌍화차 거리', '세종실록지리지와 신동국여지승람 등 옛 문헌에 정읍의 토산품으로 차가 기록 되어 전해져 올 정도로 역사를 간직한 정읍은 차문화도 오래된 고장이다. 그중에서도 쌍화차를 주 메뉴로 하는 전통찻집이 새암로를 따라 자생적으로 형성되어 있다. 쌍화차의 깊은 맛과 건강식으로 각광받으면서 장명동 주민센터 인근지역에 점진적으로 확대되면서 조성되었다. 쌍화차거리에는 30년을 훌쩍 넘긴 쌍화탕 찻집이 아직도 건재하고, 크고 작은 쌍화탕 찻집이 40여 곳이나 성업 중이다. 소박하고 편안한 분위기가 그대로 남아 있는 골목길에 옹기종기 자리한 찻집들을 기웃거리다 보면 특유의 쌍화탕 향기가 발길을 잡는다. 이곳의 쌍화차는 넉넉한 한약재에 밤, 생강, 대추, 숙지황, 견과류 등 총 20여 가지의 재료를 넣어 만든 제대로 된 전통 한방탕이다. 쌍화차 거리의 가게들은 인심도 넉넉하여 차 한 잔으로는 왠지 서운해서 구운 가래떡이나 제철 과일을 함께 내고, 입가심으로 식혜나 오미자차를 내기도 한다.',
        35.5673234605, 126.8567468461,
        '', '전북특별자치도 정읍시 중앙1길 147 (장명동) 일대', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (457, '전일빌딩245', '‘전일빌딩 245’ Bl는 역사적 현장으로서의 건축형태와 공간을 ‘245’로 상징화하여 전일빌딩의 의미를 되새기고 미래로의 스토리텔링을 담고자 하였다. ‘245’ 정중앙의 원은 5·18 민주화운동 당시 헬기 사격의 선명한 탄흔을, 4가지 컬러 구성은 전일빌딩의 콘텐츠 공간을 형상화하였다.
전일빌딩에 헬기 사격이 가해졌다는 사실은 수많은 목격자와 증거, 탄흔이 기억하고 있다. 전일빌딩에서 발견된 245개 탄흔이 가장 명확한 증거이며 헬기 사격을 증거 하는 문서 등이 이를 뒷받침한다. 전일빌딩 245의 탄흔을 지속 가능하게 보존해야 하는 이유는 5·18 민주화운동을 후대에 온몸으로 알리는 주인공이기 때문이다.',
        35.1483997205, 126.9187324992,
        '', '광주광역시 동구 금남로 245 (금남로1가)', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (458, '전평제', '전평제는 광주광역시 서구 매월동에 있는 저수 시설이다. 전평제(前坪堤)라는 이름은 회재 박광옥 (1526~1593)의 유허비 (선현의 자취가 있는 곳을 후세에 알리거나 추모하기 위하여 세운 비)에 등장한다. 회재가 나이 43세 때 농사를 짓기 위해 개산 남쪽의 물을 끌어들여 만든 것이 전평제라고 알려져 있다. 1943년에 매월동, 벽진동 농경에 농업용수를 공급하고 재해 방지를 위해 축조해서 도심 근교에 방치된 저수지를 1999년부터 2002년까지 국토공원화 시범사업으로 쉼터를 조성하였다.',
        35.1146356961, 126.8493672528,
        'http://tong.visitkorea.or.kr/cms/resource/81/3367281_image2_1.jpg', '광주광역시 서구 전평길 1', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (459, '절영해안산책로', '영도 서쪽 편 봉래산 아래 해안선을 따라 이어져 있는 3km의 해안산책로이다. 원래 지형이 가파르고 험난하여 접근이 어려웠으나, 시민들의 관광 욕구를 충족시키기 위한 공공근로사업의 일환으로 2001년 해안선을 따라 산책로를 개설하였다. 경치 좋은 절영해안 산책로에는 곳곳에 있는 장승과 돌탑, 뱃놀이 터, 산책로 우측의 넓은 바다, 담벼락의 다양한 모자이크 타일 문양 등 다양한 볼거리가 있다. 2014년 국토해양부가 선정한 대한민국 5대 해안 누리길로 선정되었고 2021년 부산 안심 관광지에도 선정되었다.',
        35.0812475294, 129.0411870536,
        '', '부산광역시 영도구 해안산책길 52', 6, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (460, '정기룡장군유적지(충의사)', '충의사는 조선 선조 때 무장으로 임진왜란시 육전의 명장으로 큰 공을 세우신 정기룡장군의 위패를 모신 곳이다. 정기룡 장군은 1562년 (명종17년) 경남 하동군 금남면에서 출생하여, 20세 때 상주로 천거, 25세에 무과에 급제하여, 왕명에 의해 기룡으로 개명하였다. 임진왜란이 일어나자 적은 군사로 크고 작은 60여회의 싸움에서 많은 왜병을 물리쳤으며 상주판관으로 있을 당시 격전 끝에 상주성을 탈환하였다. 이후 경상우도병마절도사, 경상도방어사, 3도통제사 겸 경상우도수군절도사로 재직하다가 병사하였다. 시호는 충의(忠毅)이다.
이 충의사는 신도비, 장군 묘소와 같이 1974년 지방문화재 기념물로 지정되었고, 1978년 호국유적 정화 사업으로 당초 16.5m² 정도의 사당을 약 13,223m²의 부지에 사당, 전시관, 관리사무소 등으로 확장 정비한 호국사상의 성지이다. 전시관에는 보물호인 5점(교서 2점, 교지, 신패, 옥대 각 1점)과 동산문화재(교지 19점, 매헌실기 판목 58판)를 전시하고 있다.
또한 매년 10월 무렵 지역 주민이 임진왜란 당시 상주성을 탈환하던 장면을 재현하여 장군의 호국사상을 홍보, 계승하고 있다/',
        36.4445865061, 128.2132887471,
        'http://tong.visitkorea.or.kr/cms/resource/61/3042661_image2_1.jpg', '경상북도 상주시 사벌국면 충의로 230', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (461, '정방폭포', '천지연, 천제연과 더불어 제주도 내 3대 폭포 중의 하나인 정방폭포는 서귀포시 중심가에서 약 1.5㎞ 동남쪽에 위치해 있다.
바다로 직접 떨어지는 동양 유일의 해안폭포로서 높이 23m, 폭 8m, 깊이 5m에 이른다. 웅장한 폭포음과 쏟아지는 물줄기에 햇빛이 반사되면, 일곱 색깔의 무지개가 푸른 바다와 함께 어우러져 신비의 황홀경을 연출한다. 예로부터 이곳을 정방하폭(正房夏瀑)이라 하여 영주 십 경의 하나로 삼았다. 중국 진나라 시황제가 ‘서불(徐 )’에게 동양의 삼신산의 하나인 한라산에 가서 불로초를 캐어 오도록 하였으나 불로초를 찾지 못하고 정방폭포의 절벽에 ‘서불과지(徐市過之)’라는 글을 새기고 서쪽으로 돌아갔다는 이야기가 있다. 해변을 끼고 높이 솟은 절벽에는 노송이 바다로 나뭇가지를 드리워 넘어질 듯 서 있으며 각종 수목이 울창하다.',
        33.2450398180, 126.5716052770,
        'http://tong.visitkorea.or.kr/cms/resource/78/2665278_image2_1.jpg', '제주특별자치도 서귀포시 칠십리로214번길 37', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (462, '정지용 문학관', '충북 옥천군 옥천읍 하계리는 우리에게 ＂향수＂로 잘 알려진 시인 정지용이 나고 자란 고향이다. 정지용 생가 옆에는 정지용의 삶과 문학을 이해하고 대표적인 작품을 다양한 방법으로 감상하며 체험할 수 있는 공간인 정지용문학관이 이웃해 있다. 문학전시실은 테마별로 정지용의 문학을 접할 수 있도록 지용연보, 지용의 삶과 문학, 지용문학지도, 시·산문집 초간본 전시 등 다양한 공간을 마련하고 있다. 정지용 문학관을 들어서면 안내데스크가 정면에 있고 우측으로 정지용의 밀랍인형이 벤치에 앉아 있는데 양옆에 빈자리가 마련되어 있어서 방문객이 인형과 함께 기념촬영을 할 수 있도록 마련된 소품이다.

문학전시실은 테마별로 정지용의 문학을 접할 수 있도록 지용연보, 지용의 삶과 문학, 지용문학지도, 시ㆍ산문집 초간본 전시 등 다양한 공간을 마련하고 있다. 다음으로 흥미성과 오락성을 갖춘 문학체험 공간이 마련되어 있는데 다양한 멀티미디어 기법을 활용하여 관람객이 즉석에서 문학을 체험할 수 있다. 그 외에 정지용 시인의 삶과 문학, 인간미 등을 서정적으로 회화적으로 그린 다큐멘터리 형식의 영상이 상영되는 영상실과 강좌, 시 토론, 세미나, 문학 동아리 활동 공간이며 단체관람객을 위한 오리엔테이션을 진행할 수 있는 열린 문학공간인 문학교실이 마련되어 있다.',
        36.3149342963, 127.5816069833,
        'http://tong.visitkorea.or.kr/cms/resource/10/2622310_image2_1.jpg', '충청북도 옥천군 옥천읍 향수길 56', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (463, '정지용 생가', '한국 현대시의 대표 시인인 [향수] 정지용의 생가이며 정지용문학관 바로 옆에 있다. 1902년 11월 20일 정지용이 태어나고 어린 시절을 보낸 곳으로, 1974년에 허물어졌으나 1996년 7월에 옛 모습 그대로 복원되었다. 정지용은 일제 강점기 우리 민족의 아픔을 안타까워하며 순수시를 쓴 시인이다. 이곳은 한국 현대시의 대표 시인인 정지용의 삶과 문학을 이해할 수 있는 중요한 공간이다.
생가는 초가로 지어진 단층 한옥으로, 앞면 3칸, 옆면 3칸의 규모다. 안채는 부엌과 방 2칸으로 이루어져 있으며, 사랑채는 방 2칸과 마루로 이루어져 있다. 생가 주변에는 정지용 시인의 동상과 시비, 그리고 실개천이 흐르고 있다.
정지용문학관에는 정지용 시인의 유품과 관련 자료들이 전시되어 있다. 유품으로는 시인의 유복, 붓, 먹, 책 등이 전시되어 있으며, 관련 자료로는 시인의 생애와 작품, 옥천과 관련된 자료들이 전시되어 있다. 문학 해설가가 상주해 있어 자세한 설명을 들을 수 있다. 근처에 장계관광지, 옥천 향수 100리 자전거길 등이 조성되어 있고 멀지 않은 곳에 육영수 생가가 있다.',
        36.3150731793, 127.5821652630,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=8bf0fce2-c2c5-45bd-ae67-eb35bf64245b', '충청북도 옥천군 옥천읍 향수길 56', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (464, '정혜사(순천)', '순천시 서면 청소리 계족산 중턱에 자리한 정혜사는 통일신라 시대 말 보조국사 체징(804~880)이 창건하였다는 설과 고려시대에 창건되었다는 설이 있다. 이 지방에서는 아주 오래된 사찰이라는 뜻으로 ‘고사절’이라는 이름으로도 불렸으며 대한불교조계종 제19교구 본사 화엄사 말사이다. 또한 송광사 원감국사 충지스님이 서기(1226~1293년)에 스님의 스승에 대해 쓴 ‘혜소국사 제문’ 등에 보면 정혜사는 혜소국사가 창건했다는 기록도 있다. 임진왜란 때인 선조 20년(1592)에 소실되어 흔적만 남아있는 것을 임진왜란 이후에 신욱스님께서 정사년(1671년, 광해군 9년)에 법당 3칸을 중창하셨다는 기록이 대웅전 상량문에 기록으로 남아 있다.
대웅전은 1984년 11월 30일 보물로 지정되었으며 정혜사의 역사가 깊고 대웅전 건물이 조선 시대 목조 건축의 여러 양식을 찾아볼 수 있는 중요 자료로서 상당한 격조를 갖추었음을 알 수 있다.',
        35.0620196437, 127.5143127353,
        'http://tong.visitkorea.or.kr/cms/resource/64/3016564_image2_1.jpg', '전라남도 순천시 서면 정혜사길 32 (서면)', 38, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (465, '제일반점', '광주의 제일반점은 충장로 거리에 있는 중국요리 전문점이다. 1960년대에 광주에서 시작하여 60여 년간 영업 중인 중식당이자 노포이며 화교분이 운영한다. 볶은 춘장과 야채, 감자, 돼지고기 특수부위를 넣어 요리하는 옛날식 자장면, 돼지고기 등심 부위로 만드는 탕수육 등 다양한 중국요리 메뉴를 제공한다. 특히, 군만두, 오징어튀김은 강력 추천 메뉴이며 같이 나오는 양파소스가 별미다.
매장이 넓긴한데 현지인 맛집이라 사람이 많아 점심에는 시간을 잘 맞춰가는 것이 좋겠다.
광주 지하철 1호선 금남로5가역과 금남로4가역 사이에 있으며 도보2분 거리에 위치하여 대중교통 접근성도 좋다.',
        35.1515409291, 126.9125968520,
        'http://tong.visitkorea.or.kr/cms/resource/80/3029180_image2_1.jpg', '광주광역시 동구 구성로 174', 5, '062-224-6670', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (466, '제주 노루생태관찰원', '제주 노루생태관찰원은 2007년 8월 3일 개장하였다. 각종 동식물이 자연 그대로 보존된 52ha의 공간에 오름에서 자유롭게 뛰노는 노루를 관찰할 수 있는 숲길 관찰로와 관람객이 가까이에서 직접 노루를 만져보고 먹이 주기 체험을 할 수 있는 상시관찰원이 조성되어 있다. 전시관에는 제주의 다양한 동식물과 한라산의 식생분포 등 제주의 자연에 대한 동물 이야기관과 노루의 특징, 역사 속에 나오는 노루의 기록 등 노루에 대한 지식을 제공하는 노루의 생태관, 노루의 생태와 생활상을 영상으로 보여주는 노루이야기 영상관이 있다. 천혜의 자연환경을 바탕으로 제주의 상징인 노루를 직접 관찰하고 휴식을 취하면서 노루에 대한 지식을 얻을 수 있는 노루생태관찰원은 자연학습과 생태체험을 동시에 즐길 수 있는 우리들의 공간이다. 거친 오름 중턱을 따라 조성된 숲길 관찰로는 온 가족이 쉽게 이용할 수 있는 트래킹 코스로 약 40분이 소요되며 탁 트인 전망과 함께 자연 속의 휴식공간을 제공한다. 노루생태관찰원에서는 노루먹이 주기 체험과 나무를 이용한 노루 만들기 체험 등을 진행하고 있다.',
        33.4444547805, 126.6266209993,
        'http://tong.visitkorea.or.kr/cms/resource/67/3389767_image2_1.jpg', '제주특별자치도 제주시 명림로 520', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (467, '제주4·3평화공원', '제주 4.3평화공원은 4.3사건으로 인한 제주도 민간인 학살과 처절한 삶을 기억하고 추념하며, 화해와 상생의 미래를 열어가기 위한 평화 인권기념공원이다. 제주 4.3평화공원 조성사업은 제주4.3사건에 대한 공동체적 보상의 하나로 이루어졌으며, 4.3특별법 공포(2000), 공원부지 매입, 조성기본계획 연구 용역, 현상공모, 건축공사, 전시물 제작 및 설치 등이 이어져 2008년 3월 28일 개관하게 되었다.
공원 안에는 제주 4.3평화기념관, 위령제단, 위령탑, 봉안관 등이 이곳을 지키고 있다. 위령제단은 연중 4.3 희생자에 대해 참배를 진행하는 곳이며, 그들을 모시고 있는 위패봉안실이 따로 마련되어 있다. 봉안관은 4.3 유해발굴 사업 시기에 발굴된 유해를 봉안하는 장소로 현재 380기가 안치되어 있다. 각 명비원에는 희생자의 성명과 성별, 당시 연령 등을 기록해 두었다.
제주 4.3평화기념관에는 총 6개의 특별 전시관이 있다. 제1관에서는 주민들의 피신처로 활용되었다는 천연동굴을 주제로 한 역사관이 있으며, 제2관에서는 해방과 좌절이라는 주제로 해방 후 3.1절 기념행사에서 사망한 6명의 민간인의 이야기를 담아내고 있다. 제3관에서는 무장봉기와 분단 거부라는 주제로 1948년 4월 3일에 일어난 무장봉기에 대한 이야기가 있고, 제4관에서는 학살에 관한 내용을 다루고 있다. 마지막으로 5관과 6관에서는 진상 규명 운동으로 상처를 극복해 내는 과정과 관람 후의 소감문이 걸려 있다.',
        33.4498540414, 126.6177605342,
        'http://tong.visitkorea.or.kr/cms/resource/44/1806044_image2_1.jpg', '제주특별자치도 제주시 명림로 430', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (468, '제주돌문화공원', '제주돌문화공원은 돌의 고장 제주에 있는 돌문화를 종합적이고 체계적으로 보여주는 박물관이자 생태공원이다. 2020년까지 전체공원이 조성되며, 제1단계로 제주 돌 박물관, 제주 돌문화 전시관, 제주의 전통 초가 등의 전시관이 완공되어 공사가 시작된 지 7년 만인 2006년 6월 3일 문을 열었다. 제주돌문화공원은 제주만의 독특한 자연유산인 오름 앞에 자리 잡고 있으며, 제주의 돌문화를 한곳에서 볼 수 있는 곳이다. 각종 돌 조형물들과 거석 사이를 통과하여 숲 속 오솔길을 따라가다 보면 박물관과 제주의 전통 초가들을 볼 수 있다. 야외 전시장에는 48기의 돌하르방, 사악한 기운과 액운을 몰아낸다는 방사탑, 도둑이 없어 대문도 없다는 제주의 상징인 정주석, 무덤 주위에 세워 망자의 한을 달래준다는 제주만의 내세관을 보여주는 동자석 등을 볼 수 있다. 제주의 역사와 전통 자연미를 함께 느낄 수 있는 자연과 문화의 쉼터이다.

◎ 한류의 매력을 만나는 여행 정보 - 영화 <마녀2>
마녀 프로젝트를 진행하는 비밀연구소 아크의 배경이 된 장소는 돌문화공원 내에 있다. 공원 부지 100만 평 중 70%는 돌, 나무, 덩굴이 어우러진 곶자왈로 제주의 정체성과 예술성을 잘 담아낸 곳이다.',
        33.4513683869, 126.6567710025,
        '', '제주특별자치도 제주시 조천읍 남조로 2023', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (469, '제주민속촌', '제주민속촌은 2001년에 개관한 제1종 전문 박물관으로 1890년대를 전시 기준 연대로 하여 제주의 민속자료를 종합적으로 정리하여 전시하고 있으며, 100여채에 달하는 제주 전통가옥은 제주도민이 생활하였던 가옥과 돌, 기둥 등을 그대로 옮겨와 복원해 전시하고 있다. 이 전통 가옥에는 생활용구, 농기구, 어구, 가구, 석물 등 민속자료가 전시되어 있다. 제주민속촌에는 제주는 물론 우리나라의 향토수종 및 꽃들을 식재하여 사계절 내 꽃들을 볼 수 있으며 재래흑돼지 및 가금, 가축을 사육하고 있어 자연학습장 기능도 겸하고 있다. 이외에도 다양한 테마별 전시장과 체험가옥, 전통민속놀이 체험장을 통해 다양한 볼거리와 체험거리를 제공하고 있다. ',
        33.3212774115, 126.8431355929,
        'http://tong.visitkorea.or.kr/cms/resource/78/2659578_image2_1.bmp', '제주특별자치도 서귀포시 표선면 민속해안로 631-34', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (470, '제주테지움', '테지움 사파리는 세계 최초로 야생동물과 수중동물 꽃과 새들을 모두 봉제인형으로 제작하여 전시되는 새로운 개념의 박물관이다. A동 1층의 야생동물존에는 호랑이, 코끼리, 사자 표범 등의 동물인형과 다양한 포즈의 실물크기의 테디베어와 함께 사진을 찍고 직접 만지고 느낄 수 있도록 전시되어 있고 2층에는 돌고래, 상어를 비롯하여 문어, 거북이 등 수중 동물인형과 홍학, 학, 오리 떼 등 다양한 인형들이 있다. B동 2층으로 넘어오게 되면 4m의 엎드린 테디베어에서 어린이들이 자유롭게 뛰어놀 수 있는 놀이터와 드라마 뉴하트에 등장했던 모든 테디베어와 테지움아트갤러리에는 영화패러디, 그리스신화 테디베어, 세계의상 테디베어들이 전시되어 있다. B동 1층의 선물코너에서는 다양한 형태의 테디베어 소품들과 사랑스러운 여러 가지의 테디베어를 구매할 수 있다.',
        33.4120435500, 126.3935836397,
        'http://tong.visitkorea.or.kr/cms/resource/98/3034398_image2_1.jpg', '제주특별자치도 제주시 애월읍 평화로 2159', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (471, '제주허브동산', '2000년 부지매입을 시작으로 약 5년간 59,504m²(18,000평)의 대지 위에 허브동산을 만들어왔다. 현재 약 180여 종의 허브와 우리 산하의 야생화로 채워진 각양각색의 정원들과 작은 동산들, 그리고 611.6m²(2,000평)의 체험 감귤농장 등 다양한 형태의 테마공원이 조성되어 있다. 또한 허브동산을 휘어감은 산책로, 허브 관련 제품을 구입할 수 있는 허브샵, 허브를 이용한 다양한 퓨전 음식을 맛볼 수 있는 카페, 허브에 대한 지식과 정보를 공유하고, 더욱 다양한 체험을 할 수 있는 허브체험관 등의 시설이 잘 구비되어 있어 연인 및 가족단위 혹은 기업의 단체에서 자연학습 및 데이트 코스, 워크샵 등 여러 가지 목적으로 이용 가능한 편안한 휴식처가 될 것이다.
그리고 허브 동산의 매력을 만끽할 수 있도록 설계된 6개 동의 각 숙소들은 허브정원 사이에 자리를 잡고 쾌적하고 안락한 여행이 될 수 있도록 최적의 환경을 구비하고 있다. 각 동 2층 침실 어디에서나 일출과 바다를 조망할 수 있고, 뒤로는 한라산이 한눈에 들어온다. 허브동산의 주요 테마는 “쉴休”, 봄날 오후 같은 편안하고 조용한 휴식. 번잡한 일상과 긴장된 도시 생활의 피로를 털어버리고, “몸과 마음에 쉼을 주자, 실컷 게으름을 피워도 하나 바쁠 것 없는, 느리게 사는 여유를 신이 인간에게 준 선물 가운데 마음껏 누리자.” 이것이 허브동산이 추구하는 방향이다.',
        33.3304565115, 126.8155933234,
        '', '제주특별자치도 서귀포시 표선면 돈오름로 170', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (472, '제천 의림지와 제림', '충청도를 호수의 서쪽이라 하여 호서지방이라고도 부르는데, 여기서 말하는 호수가 바로 의림지를 가리킨다. 그만큼 의림지는 오래되고 유명한 저수지이다. 충청북도 기념물인 의림지는 삼한시대에 축조된 저수지로, 본래 임지라고 했다. 신라 진흥왕 때 악성(樂聖) 우륵(于勒)이 개울물을 막아 둑을 쌓았다는 이야기가 전하고, 그로부터 700년 뒤 이곳에 온 현감 박의림(朴義林)이 좀 더 견고하게 새로 쌓은 것이라고도 한다. 호반 둘레 1.8km, 만수면적 151,470㎡, 저수량 6,611,891㎡, 수심 8~13m의 대수원지로 289.4 정보의 농지를 관개한다. 김제의 벽골제, 밀양의 수산제와 함께 삼한시대 수리 시설 가운데 하나로, 수구를 옹기로 축조하여 당시의 농업기술을 연구하는데 귀중한 자료가 되고 있다. 현재는 제천 지방의 경승지로, 호수 주변에는 순조 7년(1807년)에 세워진 영호정과 1948년에 건립된 경호루, 그리고 수백 년을 자란 소나무와 수양버들, 30m의 자연폭포 등이 어우러져 풍치를 더하고 있다.',
        37.1741582990, 128.2080988180,
        'http://tong.visitkorea.or.kr/cms/resource/09/3309809_image2_1.JPG', '충청북도 제천시 의림지로 33', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (473, '제천 자양영당', '제천 자양영당은 조선 후기의 유학자인 유중교(1832~1893)가 후진을 양성할 목적으로 1889년(고종 26)에 창주정사(滄州精舍)를 세운 강학처를 기반으로, 1906년 이소응의 주도하에 화서학파에서 존중하는 선현들의 제사를 모시기 위해 위패와 영정을 봉안하면서 창건되었다. 1977년에 있었던 제천 자양영당 전면 보수 공사 전에는 지붕이 돌기와로 되어 있었다고 한다. 1995~2001년에 제천 자양영당 보수, 의병 사당인 숭의사 신축, 의병 전시관과 의병 탑 건립 등 제천 자양영당 성역화 사업을 추진하여 현재에 이르고 있다.',
        37.0988730375, 128.0867591339,
        'http://tong.visitkorea.or.kr/cms/resource/34/3062634_image2_1.jpg', '충청북도 제천시 봉양읍 의암로 566-7', 33, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (474, '조각미술관 바우지움', '조각미술관 바우지움은 강원 고성군 토성면에 위치한 조각가 김명숙의 사립 미술관이다. 현대 조각의 대중화와 발전에 기여하기 위해 치과의사 안정모 박사와 조각가 김명숙 관장 부부가 2015년에 건립하였다. 조각가 김명숙의 작품 및 한국 근·현대 조각 작품들을 감상할 수 있다. 자연과 건축, 예술이 어우러진 아름다운 공간이다. 다양한 전시와 교육 프로그램을 진행하고 카페와 아트샵을 운영한다. 세 동으로 이루어진 갤러리와 다섯 가지 테마로 이뤄진 5000평 부지의 야외정원으로 구성되어 있다.',
        38.2124022405, 128.5151758377,
        '', '강원특별자치도 고성군 토성면 원암온천3길 37', 32, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (475, '조계사(서울)', '조계사는 대한불교 직할 교구 본사로서 조계종 총무원, 중앙종회 등이 자리하고 있는 한국 불교의 중심 사찰이다. 원래 고려 말 현 수송공원(전 중동고등학교 위치)에 창건된 사찰로서 화재로 소실되었다가 1910년 한용운, 이희광 스님 등에 의해 각황사란 이름으로 중창되었다. 그 뒤 태고사로 개칭되어 1936년 한국 불교의 최고기관으로서 역할을 수행하다가 1954년 일제의 잔재를 몰아내려는 불교 정화 운동이 일어난 후 지금의 조계사로 바뀌었다.
조계산은 중국 선불교의 육대 조사인 혜능대사(638~713)가 주석하셨던 산의 이름이다. 육조 혜능대사는 여러 선사 가운데에서도 특히 존경받는 분으로 그 일생과 가르침은 끊임없이 연구되어 수행의 지표가 되고 있다. 오늘날 조계사는 한국불교와 그 대표 종파인 조계종의 주요 사원으로서 역할을 수행하고 있다. 조계사 법당은 불교 행사의 중심 장소로서 거의 일 년 내내 법문이나 염불, 강좌, 또는 제사라든가 그 밖의 의식들이 거행되고 있다. 조계사 법당 뒤에 자리한 건물은 조계종 총무원으로서, 조계종단 전체를 관할하는 행정기관인 만큼 다양한 행사와 활동으로 분주하다.
1층의 불교회관은 전시회나 각종 행사 장소로 이용되고 있으며, 이 건물 안에 자리한 불교 신문사에서는 주간으로 불교 신문을 발행하고 있다. 법당 앞에는 1930년에 조성된 7층 석탑이 있다. 석탑 안에는 부처님의 사리가 봉안되어 있는데, 이 사리는 스리랑카의 달마바라 스님이 1914년 한국에 모셔 온 것이다. 이 석탑 양편에는 석등이 자리하고 있으며 법당을 바라보면서 왼편으로 종각이 보인다. 종각에는 큰북과 범종, 운판과 목어가 있어 아침, 저녁 예불 때마다 울린다. 이는 비단 사람들뿐만 아니라 부처님의 깨달음의 말씀을 듣고자 하는 모든 중생들을 위한 것인데 큰북은 네발 달린 짐승을, 범종은 타락하거나 지옥의 고통을 받는 이들을, 운판은 하늘의 날짐승을, 목어는 물에 사는 짐승을 제도하기 위한 것이다.',
        37.5738701204, 126.9818371759,
        '', '서울특별시 종로구 우정국로 55', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (476, '조양방직', '1933년 국내 자본으로 설립된 강화 최초의 인견 공장이었던 조양 방직은 세월이 흘러 강화 직물이 쇠락하자 폐가로 전락했다. 흉물스럽게 쓰러져가던 건물은 거대한 카페로 재탄생하였다. 허물어져 가던 벽면은 근사한 미술관이며 영화관이 됐고, 기다란 작업대는 커피 테이블로 바뀌었으며, 기계도 사람도 떠난 공간은 중국과 유럽 등지에서 찾은 골동품으로 채워졌다. 깨진 유리창을 간직한 영국제 문짝, 체코의 옛 기차에 달렸던 둥근 거울, 겨울마다 몸을 뒤집느라 애쓴 붕어빵 기계가 원래부터 그 자리에 있었던 듯 자연스럽게 배치하여 990㎡(약 300평)가 넘는 공장 터와 건물 골조를 그대로 살려 시간의 이야기를 카페 곳곳에 남겨두고 있다. 현재 각종 음료와 케이크를 판매하고 있다.',
        37.7467938510, 126.4809003215,
        'http://tong.visitkorea.or.kr/cms/resource/09/3092709_image2_1.JPG', '인천광역시 강화군 강화읍 향나무길5번길 12', 2, '032-933-2192', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (477, '주전몽돌해변', '주전몽돌해변은 울산 12경으로 울산 시민들이 즐겨 찾는 여름철 대표 해변 관광지이다. 주전은 땅이 붉다는 뜻으로 땅 색깔이 붉은색을 띠고 있다. 몽돌은 모가 나지 않은 둥근돌을 말한다. 동해안을 따라 1.5km 해안에 직경 3~6cm의 동글동글한 까만 몽돌이 해안에 길게 늘어져 있어, 절경을 이루고 있다. 해변에는 노랑바위, 샛돌바위 등 많은 기암괴석이 있다. 또한, 주전몽돌해변의 파도 소리는 동구의 소리 9경 중의 하나이기도 하다. 산과 바다를 끼고 이어지는 국도는 드라이브 코스로 으뜸이며 주변에 볼만한 곳들도 많아 겨울 관광지로도 인기를 모으고 있다.',
        35.5656092760, 129.4552486547,
        'http://tong.visitkorea.or.kr/cms/resource/42/3014942_image2_1.jpg', '울산광역시 동구 주전동', 7, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (478, '죽변항', '죽변항은 후포항과 함께 울진 북단에 있는 동해안에서 손꼽는 어항이다. 오징어와 고등어, 꽁치, 도루묵, 가자미 등이 특히 많이 잡히고 우리나라 최대의 대게잡이 항으로 유명하다. 다양하고 많은 어획량만큼 죽변향 주변에는 크고 작은 수산물 가공 공장들이 줄지어 있다.
주변 관광지로는 덕천리 백사장으로부터 후정리와 온양리까지 이어지는 드넓은 백사장인 봉평해수욕장과 죽변 등대가 있으며, 이 외에도 드라마와 예능 프로그램 촬영지가 있다.',
        37.0581198928, 129.4229770481,
        'http://tong.visitkorea.or.kr/cms/resource/09/2393709_image2_1.jpg', '경상북도 울진군 죽변면', 35, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (479, '중명전', '중명전은 대한제국의 중요한 현장이다. 1904년 경운궁(현 덕수궁) 대화재 이후 중명전으로 거처를 옮긴 고종황제의 편전으로 사용되었다.
1905년 11월 을사늑약이 이곳에서 불법적으로 체결되었으며 그 후 을사늑약의 부당함을 국제사회에 알리고자 1907년 4월 20일 헤이그 특사로 이준 등을 파견한 곳도 바로 중명전이다. 한편 중명전은 1925년 화재로 외벽만 남기고 소실된 뒤 재건하여 외국인을 위한 사교클럽으로 주로 쓰이다가 자유당 정부가 들어서면서 국유재산으로 편입되었다. 그러나 1963년 박정희 대통령은 영구 귀국한 영친왕과 이방자 여사에게 중명전을 돌려주었다. 1977년 중명전은 다시 민간에 매각되었고, 2003년 정동극장에서 매입한 뒤 2006년 문화재청에 관리 전환하여, 2007년 2월 7일 사적으로 덕수궁에 편입되었다. 2009년 12월 복원을 거쳐 2010년 8월부터 전시관 [대한제국의 운명이 갈린 곳, 덕수궁 중명전]으로 일반에 공개하고 있다.
덕수궁 돌담길을 돌아 뒤쪽으로 가면 정동극장이 있고, 그 골목으로 들어가면 중명전이 있다. 중명전은 원래 정동지역 서양 선교사들의 거주지에 속해 있다가, 1897년 경운궁(현 덕수궁)이 확장되면서 궁궐로 편입되었다. 이때 당호를 [수옥헌]이라 짓고, 주로 황실 도서관 용도로 사용되었으나 1901년 화재로 전소된 후 재건되어 지금과 같은 2층 벽돌 건물의 외형을 갖추게 되었다. 건물의 설계는 독립문, 정관헌 등을 설계한 러시아 건축가 사바찐이 하였다.',
        37.5666207957, 126.9725215638,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=2b90e003-5e61-4d17-b796-a361f9be28bd', '서울특별시 중구 정동길 41-11 (정동)', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (480, '중문관광단지', '중문관광단지는 제주의 독특한 자연경관과 지리적 조건을 활용하여 한국관광공사가 1978년부터 제주특별자치도 서귀포시 중문, 대포, 색달동 일원(356만㎡)에 조성한 국제적인 관광 휴양지이다. 중문관광단지에는 국내 최고의 관광 휴양지답게 신라호텔, 롯데호텔, 그랜드조선호텔 등 9개의 최고급 호텔이 있으며, 퍼시픽리솜, 여미지식물원, 테디베어박물관, 그림포레스트, 박물관은 살아있다, 초콜릿랜드, 제주국제평화센터, 천제연폭포, 주상절리대, 중문골프클럽 등의 관광지가 있다. 특히 서핑, 요트, 패러세일링 등 해양 레포츠를 즐길 수 있는 중문색달해수욕장과 바다 전망 카페, 산책로 등이 최근 각광을 받고 있다.',
        33.2548917891, 126.4136493056,
        '', '제주특별자치도 서귀포시 중문관광로 38 (중문관광단지안내소)', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (481, '중문색달해수욕장', '중문색달해수욕장은 아름다운 해안 풍경과 야자수의 이국적인 모습을 가진 해변으로 중문관광단지에서 시작해 천제연폭포와 대포주상절리와 이어지는 곳에 있다. 해변의 길이가 약 560미터에 달하는 활처럼 굽은 백사장으로 해마다 100만 명이 넘는 사람들이 다양한 해양 스포츠를 즐기기 위해 몰려든다. 본래는 진모살이라고 불렸는데 긴 모래 해변이라는 뜻이다. 이곳의 모래는 흑색, 회색, 적색, 백색 4가지가 섞여 있어 해가 비추는 방향에 따라 해변의 색깔이 달라 보이며 제주도 특유의 검은 돌인 화산석과 아름다운 조화를 이루고 있다. 다른 해수욕장보다 파도가 잦고, 높은 편이라 서퍼들에게 인기가 많고, 국내의 가장 큰 규모의 국제 서핑 대회가 매년 6월에 개최된다.
이 밖에도 수상스키, 윈드서핑, 스쿠버다이빙, 제트보트 등 함께 즐길 수 있는 수상 레저 활동이 많아 수상 레저 마니아들이 좋아하는 해변으로 알려져 있다. 1999년 환경운동연합이 실시한 수질환경성 조사 결과 전국 44개 해수욕장 가운데 최고의 청정 해수욕장으로 꼽히기도 했다. 색달해변이 한눈에 보이는 쉬리의 언덕은 이곳의 또 다른 명소로 영화 쉬리의 마지막 장면의 촬영지이기도 하며 제주에서 가장 아름다운 일출과 일몰을 볼 수 있는 장소이다.',
        33.2454289066, 126.4112108397,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=5dae85de-7deb-4d39-9fc3-118af6a164d3', '제주특별자치도 서귀포시 색달동', 39, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (482, '중미산자연휴양림', '중미산자연휴양림은 양평군 옥천면에 위치하고 있으며, 드라이브 코스인 농다치 고갯길까지 올라가면 울창한 숲과 남한강이 조화롭게 배치되어 시야가 탁 트이는 느낌을 준다. 산 정상에 오르면 용문산의 전경과 서울, 남한강, 북한강의 아름다운 전경을 한눈에 굽어볼 수 있다. 중미산은 아름다움이 금강산 다음으로 아름답다고 해서 버금중, 아름다울 미를 붙여 부르게 되었다고 한다.
중미산자연휴양림에는 특색있는 트리하우스, 연립동, 숲속의집과 야영데크가 조화롭게 분산 배치되어 있으며, 휴양림 중심부엔 숲 산책로를 설치해 직접 산림을 체험할 수 있다. 특히 숲해설가들로부터 오감을 자극할 수 있는 유익한 숲 해설을 들을 수 있을 뿐만 아니라 숲 체험 프로그램, 오리엔티어링 프로그램도 운영해 더욱 특별한 경험을 할 수 있다.
서울 근교에 위치해 접근성이 뛰어나고, 주변에 가볼만한곳으로는 청평호반, 유명산자연휴양림, 용문산국민관광단지가 있다.',
        37.5818000399, 127.4571548101,
        'http://tong.visitkorea.or.kr/cms/resource/86/607986_image2_1.jpg', '경기도 양평군 옥천면 중미산로 1152', 31, '031-771-7166', NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (483, '중앙고등학교', '중앙고등학교는 1908년 기호지방의 우국지사들에 의해 설립된 기호학교가 1910년 9월 흥사단이 설립한 융희학교를 합병하여 설립된 학교이다. 1910년 11월 모두 통합하여 중앙학교로 학교 이름을 개칭하였다. 1915년 김성수가 인수하였고, 1917년에는 계산 언덕에 교사를 신축하고 이전하였다. 본관, 서관, 동관 등의 건물을 12월에 준공하였으며 당시의 교사는 2층 벽돌집이었다. 1934년 본관이 화재로 소실되었으나, 1935년 6월 우리나라 근대 건축가이자 고려대학교 본관과 도서관, 조선일보 사옥을 설계한 박동진(1899~1980)의 설계로 다시 착공하여 1937년 9월에 준공하였다.
중앙고등학교 본관, 서관, 동관은 각각 사적으로 지정되어 있다. 본관은 학교 정문에서 바라볼 때 정면에 세워졌으며, 석조 콘크리트 철근 2층의 근대식 건물이다. 좌우가 대칭되는 H자형 평면의 중앙에는 중세 시대 고딕 성관풍의 4층 탑을 두고, 그 좌우로 1층에는 교무실, 2층에는 교실을 두었다. 서관은 1921년 10월에 2층 붉은 벽돌집으로 준공되었으며, 설계자는 확실치 않다. 1934년 화재로 없어진 구 본관 건물(지금의 동상이 있는 곳)을 중심으로 왼쪽에 동관이 있고, 오른쪽에 바로 서관이 자리 잡고 있다. 회백색의 화강암 돌과 붉은 벽돌로 엇물려 지은 이층집이다. 설계자는 일본인 중촌여자평이다. 평면은 T자형이며, 박공면이 돌출해 있다. 이곳에는 모두 교실을 배치하였다. 또한, 3·1운동 이후 조선 소년군 창설, 6·10만세운동, 광주학생운동을 시작한 곳이기도 하다. 동관은 1923년 10월 준공된 2층의 붉은 벽돌집으로 1934년 화재로 소실된 구 본관 왼쪽에 지어져 건너편의 서관과 마주 보고 있다. 서관과 마찬가지로 설계자와 시공자는 기록이 없어 확실하지 않다. 동관의 건물구조와 특징은 서관과 비슷하다. 붉은 벽돌집 슬레이트 이름의 고딕식이다. 평면은 T자형이며, 박공면이 돌출되어 있다.',
        37.5841440162, 126.9868503170,
        '', '서울특별시 종로구 창덕궁길 164', 1, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (484, '증심사(광주)', '증심사는 광주지역의 대표적인 불교도량으로 무등산 서쪽 기슭에 자리 잡고 있다. 통일신라 때 고승 철감선사 도윤이 9세기 중엽에 세운 절로 고려 선종 때(1094년) 혜소국사가 고쳐 짓고 조선 세종 때 김방이 삼창하였는데 이때 오백나한의 불사가 이루어졌다고 한다. 이후 임진왜란 때 불타 없어진 것을 1609년(광해군 1)에 석경·수장·도광의 3대 선사가 4창했다고 한다. 그 후 신도들의 정성으로 몇 차례 보수가 이루어졌으나, 6 ·25 전쟁 때 많은 부분이 소실되었다가 1970년에야 대웅전을 비롯한 건물들이 복구되었다. 증심사의 유물로는 오백전과 비로전(사성전)에 봉안된 철조비로자나불 좌상(보물), 신라 말기의 석탑인 증심사 삼층석탑(지방유형문화재), 범종각, 각 층의 4면에 범자가 새겨진 범자칠층석탑 등 수많은 문화재가 있다. 특히 오백전은 무등산에 남아 있는 사찰 건물들 중 현재 가장 오래된 조선 초기(세종 25년)의 건물로 강진의 무위사 극락전과 계통을 같이 하는 정면 3간, 측면 3간의 단층 맞배지붕의 다심포 양식으로 그 희귀성이 돋보인다. 이에 1986년 11월 1일 광주문화재자료로 지정되어 있는 사찰이다. 다양한 템플스테이가 활발하게 운영되고 있으며 여러 가지 체험프로그램이 있다.',
        35.1287583574, 126.9697618157,
        'http://tong.visitkorea.or.kr/cms/resource/68/3368268_image2_1.jpg', '광주광역시 동구 증심사길 177', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (485, '지리산 허브밸리', '지리산 허브밸리는 지리산 운봉 아래 용산리에 조성한 허브관광농원으로 2005년 재정경제부로부터 지리산 웰빙 허브산업특구로 지정받았다. 남원은 깨끗한 자연환경에 총 1,300여 종의 허브가 자생하는 허브의 보고라 할 수 있다. 이곳에서는 허브를 이용한 식품, 대체의학 제품 등의 다양한 허브제품과 친환경 허브원료를 생산하고 있다. 남원 허브밸리에는 허브를 테마로 한 볼거리, 즐길 거리, 체험 거리가 가득한 허브테마파크와 허브꽃 따기, 허브차 만들기, 허브향초 만들기 등 허브를 이용한 다양한 체험을 할 수 있는 허브체험관광농원이 마련되어 있다. 또한, 허브제품가공단지, 허브농업지구, 자생식물생태공원 등이 조성되어 있어 다양한 방법으로 허브를 즐길 수 있다.',
        35.4351503763, 127.5486167347,
        '', '전북특별자치도 남원시 운봉읍 바래봉길 214', 37, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (486, '지산유원지', '1978년 4월 개장한 지산유원지는 약 20만 평 규모의 자연친화적 시설이다. 숲 속을 가로지르는 리프트와 해발 350m에 설치된 모노레일에 탑승하면 광주 시내 전경과 무등산 정상의 모습을 한눈에 구경할 수 있다. 모노레일은 리프트의 종착점에서 숲길로 50ｍ 떨어진 산 중턱에서 출발해 팔각정이 있는 지산유원지 산 정상을 왕복하는 코스로 운행된다. 재개장 후 젊은 세대 층을 중심으로 SNS와 입소문을 타고 광주 무등산에서 스릴을 즐길 수 있는 독특한 놀이시설로 인기몰이 중이다.',
        35.1494112311, 126.9491103443,
        'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=04f152f8-339c-41ad-a08a-f625ed491571', '광주광역시 동구 지호로164번길 35-1 (지산동)', 5, NULL, NOW(), NOW());
INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (487, '지평낚시터', '경기도 양평의 월산저수지에 있는 지평낚시터는 호수처럼 넓은 아름다운 곳이다. 자연 풍광이 좋기로 입소문이 나서 일상을 벗어나 여유로움을 즐기며 낚시를 하기 위해 찾는 관광객이 많다.1968년 준공된 지산저수지는 평균 수심이 4~5m로 깊은 편이며, 맑은 1급수 계곡물이 흘러내리는 천혜의 자연 낚시터이다. 토종붕어, 참붕어, 잉어, 향어, 메기 등이 자생하는 곳으로, 주기적으로 방류하기도 해 짜릿한 손맛을 느끼기 좋은 낚시터로도 유명하다. 주변 경관이 좋아 여름 밤이면 반딧불이를 볼 수 있고, 겨울이면 빙어축제를 즐기러 방문하는 사람이 하다. 빙어 축제는 비교적 입장료가 저렴하고, 빙어튀김, 빙어회, 어묵, 김밥 등 먹거리 장터가 있어 한겨울 가족들과 특별한 경험을 하기에 좋다.',
