INSERT INTO places (id, name, description, latitude, longitude, image_url, address, region_id, phone_number, created_at, updated_at)
VALUES (508, '청와대 앞길', '청와대 앞길은 종로구 팔판동 162번지(삼청동길)에서 청와대 앞을 거쳐 궁정동 98-6번지(효자로)에 이르는 폭 25m, 길이 810m의 4차선 도로이며 이 길이 청와대 앞을 지나는 데서 그 이름을 유래했다. 1988년 3월 31일 처음 청와대 앞길로 이름 붙여졌으며 경복궁 북쪽 담장길과 북악산 기슭 청와대 사이를 횡단하여 한때 시민의 통행이 금지되기도 했다.
청와대 앞길로 들어서면 좌우로 울창한 나무들이 줄지어 서 있어 나무 그늘과 아름다운 꽃밭을 감상하면서 산책을 할 수 있다. 경복궁의 북문인 신무문 맞은편에는 청와대가 있고, 청와대 앞길이 끝나는 곳에는 청와대의 기자회견장인 춘추문이 있다. 춘추문에서 경복궁 정문인 건춘문에 이르는 삼청동길에는 국제화랑, 그로리치 화랑, 갤러리 현대 등이 있으며, 효자로 쪽에는 진화랑이 있어 예술작품감상의 기회도 가질 수 있다.',
        37.5835410742, 126.9737681926,
        '', '서울특별시 종로구 궁정동', 1, NULL, NOW(), NOW());
