INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (77, '임진왜란의 영웅, 사명대사를 만나다', '밀양은 임진왜란 때 승병장으로 활약했던 사명대사가 태어난 고장이다. 밀양의 동서 축을 이으며 사명대사 생가지, 표충비, 표충사 등 사명대사의 유적이 이어진다. 사명대사가 태어난 생가가 남아 있는 생가지와 나라에 변고가 있을 때마다 땀을 흘리는 표충비, 사명당의 위패가 모셔진 표충사 등 승병장으로 국난 극복에 앞장선 사명당의 일대기를 따라가볼 수 있는 여정이다.', 36, 'http://tong.visitkorea.or.kr/cms/resource/85/2589485_image2_1.bmp', NOW(), NOW());
