INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (82, '뗏목 타고 피라미 잡으며 더위사냥', '경남 사천시에는 농어촌 체험마을이 많다. 이 가운데 비봉내마을과 바리안마을, 초량다슬기 마을의 농촌 체험 프로그램이 알차다. 체험 마을에서 민박도 겸하고, 체험을 끝낸 뒤 가까운 바다로 이동해 해수욕까지 즐길 수 있으니 일석이조다. 비봉내마을의 대표 체험거리는 뗏목 타기다. 구명조끼를 입고 냇가에서 통나무로 엮은 뗏목을 탈 수 있다. 폭이 넓지만 물이 깊지 않아 위험이 어린이들에게 인기 있는 체험이다.', 36, 'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=70f340a1-3d44-483a-978b-3cf9743e24b2', NOW(), NOW());
