INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (87, '평화의 바람 따라 강화 DMZ으로 떠나는 자전거여행', '강화군 교동도에 약30km 연장의 평화자전거길이 있다. 해안철책길과 농로를 지나 북녘 전경이 보이는 망향대를 거치고 비포장도로와 해안호수길을 지나 원점으로 돌아오는 일주코스다. 개인 자전거로 이용해도 좋고, 교동면의 관광안내소인 교동제비집에서 자전거를 대여할 수 있다. 교동제비집 인근에 있는 대룡시장도 둘러볼 만하다. 이곳은 한국전쟁 후 피란민이 정착하며 만들어진 시장이다. 오래된 전통시장의 풍경과 근래 자리 잡은 세련된 가게들이 좁은 시장 골목 안에 빼곡하다. 자전거를 타고 한 시간 남짓 이동하면 교동도 북쪽 밤머리 산에 위치한 망향대에 도착한다. 망향대에 설치된 망원경을 통해 보는 북녘 풍경이 손에 닿을 듯 가깝다. 교동도를 돌아보고 나오는 길, 강화읍에 자리한 카페 조양방직에 들러 커피 한 잔 마시며 여행을 마무리하면 좋다.', 2, 'http://tong.visitkorea.or.kr/cms/resource/09/3092709_image2_1.JPG', NOW(), NOW());
