INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (89, '화려하게 빛나는 인천의 낮과 밤', '낮 동안 인천의 역사와 문화예술을 만나고 밤에 더욱 화려해지는 야경과 함께 하는 코스다. 인천 개항장 거리에서 인천 개항기의 역사와 조화를 이루는 현대 문화예술을 만나고, 송월동 동화마을에서 동심의 세계에 빠져볼 수 있다. 외국의 어느 항구도시 같은 송도센트럴파크에는 바닷물로 만들어진 인공호수가 있다. 주변에 특급호텔과 쇼핑몰, 음식점들이 밀집해 있고, 야경이 무척 아름다운 명소다. 인천의 역사와 문화예술을 만난 후 바라보는 소래포구 상인들의 삶은 더욱 뭉클하고 깊게 기억될 것이다. 서해의 맛은 덤이다.', 2, 'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=2cd598bb-97b8-46aa-9e8c-2c5d24cd98d1', NOW(), NOW());
