INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (102, '서해랑길 (고창, 군산 구간)', '서해랑길 고창군산 구간은 세월이 켜켜이 쌓인 흔적을 다양한 각도와 시선에서 만날 수 있는 코스다. 흔히 동해모래사장에서도 상상할 수 있음직한 명사십리 해변이 드넓게 펼쳐져 있는가 하면, 지구의 허파라 불리는 갯벌이 바다와 육지 곳곳에 모세혈관처럼 퍼져있는 생물권보전지역이기도 하다. 서해에 대한 고정관념을 넘어 완전히 새로운 서해의 기억을 담아낼 준비가 된 서해랑길 고창~군산구간을 걸어본다.', 37, 'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=42d56b6c-da8b-4ab6-889f-dbec088fbe96', NOW(), NOW());
