INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (103, '시간을 건너 군산에 빠지다', '시간여행마을권역 일원에서 시작하는 이번 여행은 다양한 관광지와 볼거리, 먹을거리를 즐기면서 군산 특유의 매력에 뿍 빠지는 여행이다. 시간여행마을권역에서는 근대기 주택과 초원사진관, 경암동 철길마을을 건너면서 과거의 시간을 지나쳐본다. 옛 모습 그대로 남아있는 그곳에 시선을 두면 한번 군산에 빠지고, 맛있는 음식들을 즐기다보면 군산에 두번 빠지게 된다! 마지막으로 선유도 이곳 저곳의 신나는 레포츠를 즐기며 한번 더 군산에 빠져보자!', 37, 'http://tong.visitkorea.or.kr/cms/resource/70/3351370_image2_1.jpg', NOW(), NOW());
