INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (116, '운치 있는 산사에서의 하루', '깊은 자연 속 운치 가득한 산사에서 보내는 하루. 그저 사찰 안에 가만히 앉아만 있어도 저절로 힐링 되는 기분이다. 사찰이라고 해서 모두가 같은 분위기를 지니고 있는 건 아니다. 작고 아담한 정혜사부터 야생차로 유명한 선암사, 우리나라 삼보사찰인 송광사까지 서로 다른 매력에 흠뻑 빠져보자. 선암사까지 약 30분 정도 가벼운 산책을 즐기고 주암호 드라이브도 만끽해보자.', 38, 'http://tong.visitkorea.or.kr/cms/resource/59/193259_image2_1.jpg', NOW(), NOW());
