INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (136, '강바람 맞으며 죽림욕을 즐겨보자', '울산의 상징인 태화강을 따라 자연과 역사, 문화가 함께 어우러져 있다. 선사시대의 흔적이 고스란히 남아있는 천전리각석과 반구대암각화를 감상하고 일제강점기에 조성된 십리대숲을 걸으며 죽림욕을 즐길 수 있다. 우뚝 솟은 선바위는 태화강의 절경 중 하나다. 태화강의 물길이 한눈에 들어오는 전망대에도 올라보자.', 7, 'http://tong.visitkorea.or.kr/cms/resource/12/3341812_image2_1.jpg', NOW(), NOW());
