INSERT INTO courses (id, title, description, region_id, thumbnail_url, created_at, updated_at)
VALUES (139, '자! 떠나자! 울산에 고래 보러!', '울산 남구에 위치한 장생포고래문화특구는 우리나라에서 유일한 고래 특화 체험장이다. 고래잡이 마을로 유명했던 옛 장생포 어촌을 재현한 공간과 실물 크기 고래를 형상화한 이색 체험장이 마련되어 고래와 다양한 교감을 나눌 수 있다. 일본군의 군수물자 창고로 쓰였던 동굴에 다양한 볼거리를 조성한 태화강동굴피아, 상상력이 넘치는 업사이클링 예술공간 Fe01까지 모두 둘러보면 울산의 새로운 매력을 발견하게 된다.', 7, 'https://cdn.visitkorea.or.kr/img/call?cmd=VIEW&id=a0474e7c-d774-4928-bf39-df3f524a6fae', NOW(), NOW());
